'use client';

import { useState, useEffect } from 'react';
import Link from 'next/link';
import { useTranslations } from 'next-intl';
import { Calendar, ArrowUpRight, CreditCard, Download } from 'lucide-react';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { useSubscriptionStatus } from '@/hooks/useSubscriptionStatus';

export default function AccountBillingPage() {
  const t = useTranslations('billing');
  const subscriptionT = useTranslations('subscription');
  const { data, loading, error, openCustomerPortal } = useSubscriptionStatus();
  const [actionError, setActionError] = useState<string | null>(null);
  const [financeData, setFinanceData] = useState<any>(null);
  const [financeLoading, setFinanceLoading] = useState(false);

  useEffect(() => {
    if (data?.hasSubscription) {
      fetchFinanceData();
    }
  }, [data?.hasSubscription]);

  const fetchFinanceData = async () => {
    try {
      setFinanceLoading(true);
      const res = await fetch('/api/finance/me/summary', { method: 'GET' });
      if (res.ok) {
        const financeJson = await res.json();
        setFinanceData(financeJson);
      }
    } catch (err) {
      console.error('Error fetching finance data:', err);
    } finally {
      setFinanceLoading(false);
    }
  };

  const formatDate = (dateString: string | null) => {
    if (!dateString) return t('summary.no_date');
    return new Date(dateString).toLocaleDateString('ka-GE', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  };

  const formatCurrency = (cents: number) => {
    return `$${(cents / 100).toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;
  };

  const resolveStatusLabel = (status: string | null) => {
    if (!status) return t('status.none');
    return subscriptionT(`status.${status}` as any);
  };

  const planLabel = data?.subscription?.plan
    ? subscriptionT(`plans.${data.subscription.plan}.title` as any)
    : t('summary.no_plan');

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-[#05070A] via-[#0A0F1C] to-[#05070A] py-16 px-4">
        <div className="max-w-4xl mx-auto">
          <div className="animate-pulse space-y-6">
            <div className="h-8 w-48 bg-white/10 rounded" />
            <div className="h-4 w-64 bg-white/5 rounded" />
            <div className="h-48 bg-white/5 rounded-xl" />
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#05070A] via-[#0A0F1C] to-[#05070A] py-16 px-4">
      <div className="max-w-4xl mx-auto space-y-8">
        <div>
          <h1 className="text-4xl font-bold text-white mb-3">{t('title')}</h1>
          <p className="text-gray-400">{t('subtitle')}</p>
        </div>

        {(error || actionError) && (
          <Card className="bg-red-500/10 border-red-500/30 p-4 text-red-300">
            {actionError || t('errors.loadFailed')}
          </Card>
        )}

        {!data?.hasSubscription && (
          <Card className="bg-black/40 border-white/10 p-6 space-y-4">
            <div className="flex items-start gap-4">
              <div className="w-10 h-10 rounded-full bg-cyan-500/20 flex items-center justify-center">
                <CreditCard className="w-5 h-5 text-cyan-300" />
              </div>
              <div>
                <h2 className="text-xl font-semibold text-white">{t('empty.title')}</h2>
                <p className="text-gray-400 mt-2">{t('empty.description')}</p>
              </div>
            </div>
            <Link href="/pricing" className="inline-flex">
              <Button className="bg-gradient-to-r from-cyan-500 to-blue-600 text-white">
                {t('empty.cta')}
              </Button>
            </Link>
          </Card>
        )}

        {data?.hasSubscription && (
          <Card className="bg-black/40 border-white/10 p-6 space-y-6">
            <div className="flex items-start justify-between gap-6">
              <div>
                <p className="text-sm text-gray-400">{t('summary.plan')}</p>
                <h2 className="text-2xl font-semibold text-white">{planLabel}</h2>
              </div>
              <div className="text-right">
                <p className="text-sm text-gray-400">{t('summary.status')}</p>
                <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-semibold bg-emerald-500/20 text-emerald-300 border border-emerald-500/30">
                  {resolveStatusLabel(data.status)}
                </span>
              </div>
            </div>

            <div className="grid md:grid-cols-2 gap-6">
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-full bg-white/5 flex items-center justify-center">
                  <Calendar className="w-5 h-5 text-gray-300" />
                </div>
                <div>
                  <p className="text-sm text-gray-400">{t('summary.nextBilling')}</p>
                  <p className="text-white font-medium">
                    {formatDate(data.currentPeriodEnd)}
                  </p>
                </div>
              </div>
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-full bg-white/5 flex items-center justify-center">
                  <ArrowUpRight className="w-5 h-5 text-gray-300" />
                </div>
                <div>
                  <p className="text-sm text-gray-400">{t('summary.cancelAtPeriodEnd')}</p>
                  <p className="text-white font-medium">
                    {data.cancelAtPeriodEnd ? t('summary.cancelYes') : t('summary.cancelNo')}
                  </p>
                </div>
              </div>
            </div>

            <div className="flex flex-wrap gap-3">
              <Button
                onClick={async () => {
                  try {
                    setActionError(null);
                    await openCustomerPortal();
                  } catch (err) {
                    setActionError(err instanceof Error ? err.message : t('errors.loadFailed'));
                  }
                }}
                className="bg-gradient-to-r from-cyan-500 to-blue-600 text-white"
              >
                {t('actions.manageBilling')}
              </Button>
              <Link href="/pricing">
                <Button variant="outline" className="border-white/20 text-white hover:bg-white/10">
                  {t('actions.changePlan')}
                </Button>
              </Link>
            </div>
          </Card>
        )}

        {data?.hasSubscription && financeData && (
          <Card className="bg-black/40 border-white/10 p-6 space-y-6">
            <div>
              <h2 className="text-2xl font-semibold text-white">{t('history.title') || 'Payment History'}</h2>
              <p className="text-gray-400 mt-1">{t('history.subtitle') || 'Your recent invoices and transactions'}</p>
            </div>

            {/* Total Paid Summary */}
            <div className="grid md:grid-cols-3 gap-4">
              <div className="bg-white/5 rounded-lg p-4">
                <p className="text-sm text-gray-400">{t('history.totalPaid') || 'Total Paid'}</p>
                <p className="text-2xl font-bold text-white mt-2">{formatCurrency(financeData.totals?.totalPaid || 0)}</p>
              </div>
              <div className="bg-white/5 rounded-lg p-4">
                <p className="text-sm text-gray-400">{t('history.invoices') || 'Invoices'}</p>
                <p className="text-2xl font-bold text-white mt-2">{financeData.totals?.invoiceCount || 0}</p>
              </div>
              <div className="bg-white/5 rounded-lg p-4">
                <p className="text-sm text-gray-400">{t('history.payments') || 'Transactions'}</p>
                <p className="text-2xl font-bold text-white mt-2">{financeData.totals?.paymentCount || 0}</p>
              </div>
            </div>

            {/* Recent Invoices */}
            {financeData.recentInvoices && financeData.recentInvoices.length > 0 && (
              <div>
                <h3 className="text-lg font-semibold text-white mb-3">{t('history.invoices') || 'Recent Invoices'}</h3>
                <div className="overflow-x-auto">
                  <table className="w-full text-sm">
                    <thead className="border-b border-white/10">
                      <tr>
                        <th className="text-left py-2 px-3 text-gray-400 font-medium">{t('history.date') || 'Date'}</th>
                        <th className="text-left py-2 px-3 text-gray-400 font-medium">{t('history.invoice') || 'Invoice'}</th>
                        <th className="text-left py-2 px-3 text-gray-400 font-medium">{t('history.amount') || 'Amount'}</th>
                        <th className="text-left py-2 px-3 text-gray-400 font-medium">{t('history.status') || 'Status'}</th>
                      </tr>
                    </thead>
                    <tbody>
                      {financeData.recentInvoices.map((inv: any) => (
                        <tr key={inv.id} className="border-b border-white/5 hover:bg-white/5 transition">
                          <td className="py-3 px-3 text-white">{formatDate(inv.created_at)}</td>
                          <td className="py-3 px-3 text-gray-300 font-mono text-xs">{inv.stripe_invoice_id?.slice(0, 12)}</td>
                          <td className="py-3 px-3 text-white font-medium">{formatCurrency(inv.amount_cents || 0)}</td>
                          <td className="py-3 px-3">
                            <span className={`px-2 py-1 rounded text-xs font-semibold ${
                              inv.status === 'paid' ? 'bg-emerald-500/20 text-emerald-300' : 'bg-gray-500/20 text-gray-300'
                            }`}>
                              {inv.status}
                            </span>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}

            {/* Recent Payments */}
            {financeData.recentPayments && financeData.recentPayments.length > 0 && (
              <div>
                <h3 className="text-lg font-semibold text-white mb-3">{t('history.payments') || 'Recent Transactions'}</h3>
                <div className="overflow-x-auto">
                  <table className="w-full text-sm">
                    <thead className="border-b border-white/10">
                      <tr>
                        <th className="text-left py-2 px-3 text-gray-400 font-medium">{t('history.date') || 'Date'}</th>
                        <th className="text-left py-2 px-3 text-gray-400 font-medium">{t('history.description') || 'Description'}</th>
                        <th className="text-left py-2 px-3 text-gray-400 font-medium">{t('history.amount') || 'Amount'}</th>
                        <th className="text-left py-2 px-3 text-gray-400 font-medium">{t('history.status') || 'Status'}</th>
                      </tr>
                    </thead>
                    <tbody>
                      {financeData.recentPayments.map((payment: any) => (
                        <tr key={payment.id} className="border-b border-white/5 hover:bg-white/5 transition">
                          <td className="py-3 px-3 text-white">{formatDate(payment.created_at)}</td>
                          <td className="py-3 px-3 text-gray-300">{payment.description || 'Payment'}</td>
                          <td className="py-3 px-3 text-white font-medium">{formatCurrency(payment.amount_cents || 0)}</td>
                          <td className="py-3 px-3">
                            <span className={`px-2 py-1 rounded text-xs font-semibold ${
                              payment.status === 'succeeded' ? 'bg-emerald-500/20 text-emerald-300' : 'bg-yellow-500/20 text-yellow-300'
                            }`}>
                              {payment.status}
                            </span>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}
          </Card>
        )}

'use client';

import { useEffect, useState } from 'react';
import { useTranslations } from 'next-intl';
import { motion } from 'framer-motion';
import { Check, AlertCircle } from 'lucide-react';
import toast from 'react-hot-toast';

interface PaymentConfig {
  activeProvider: string;
  stripeEnabled: boolean;
  tbcEnabled: boolean;
  bogEnabled: boolean;
  payzeEnabled: boolean;
}

const PROVIDERS = [
  {
    id: 'stripe',
    name: 'Stripe',
    description: 'Payment processing and subscriptions',
    status: 'active',
    available: true,
  },
  {
    id: 'tbc',
    name: 'TBC Bank',
    description: 'Georgian bank payment gateway',
    status: 'coming_soon',
    available: false,
  },
  {
    id: 'bog',
    name: 'Bank of Georgia',
    description: 'Bank of Georgia payment gateway',
    status: 'coming_soon',
    available: false,
  },
  {
    id: 'payze',
    name: 'Payze',
    description: 'Georgian payment solutions',
    status: 'coming_soon',
    available: false,
  },
];

export default function PaymentProvidersPage() {
  const t = useTranslations();
  const [loading, setLoading] = useState(true);
  const [_saving, setSaving] = useState(false);
  const [_config, setConfig] = useState<PaymentConfig | null>(null);
  const [selectedProvider, setSelectedProvider] = useState('stripe');

  useEffect(() => {
    const fetchConfig = async () => {
      try {
        const response = await fetch('/api/payments/provider');
        if (response.ok) {
          const data = await response.json();
          setConfig(data);
          setSelectedProvider(data.active_provider);
        }
      } catch (error) {
        toast.error(t('common.error'));
        console.error('Error fetching payment config:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchConfig();
  }, [t]);

  const handleSelectProvider = async (providerId: string) => {
    if (!PROVIDERS.find((p) => p.id === providerId && p.available)) {
      return;
    }

    setSaving(true);
    try {
      const response = await fetch('/api/payments/provider', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ activeProvider: providerId }),
      });

      if (response.ok) {
        setSelectedProvider(providerId);
        toast.success(t('common.saved_successfully'));
      } else {
        toast.error(t('common.error'));
      }
    } catch (error) {
      toast.error(t('common.error'));
      console.error('Error updating provider:', error);
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-gray-600">{t('common.loading')}</div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto px-4 py-8">
      <motion.div initial={{ opacity: 0, y: 10 }} animate={{ opacity: 1, y: 0 }}>
        <h1 className="text-3xl font-bold mb-2">{t('payments.providers.title')}</h1>
        <p className="text-gray-600 mb-8">{t('payments.providers.description')}</p>

        {/* Info Box */}
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-8 flex gap-3">
          <AlertCircle size={20} className="text-blue-600 flex-shrink-0 mt-0.5" />
          <div>
            <p className="font-medium text-blue-900 mb-1">
              {t('payments.providers.info_title')}
            </p>
            <p className="text-sm text-blue-800">{t('payments.providers.info_desc')}</p>
          </div>
        </div>

        {/* Provider Options */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {PROVIDERS.map((provider) => (
            <motion.div
              key={provider.id}
              whileHover={provider.available ? { scale: 1.02 } : {}}
              onClick={() => provider.available && handleSelectProvider(provider.id)}
              className={`rounded-lg border-2 p-6 cursor-pointer transition ${
                selectedProvider === provider.id
                  ? 'border-blue-600 bg-blue-50'
                  : 'border-gray-200 bg-white hover:border-gray-300'
              } ${!provider.available ? 'opacity-60 cursor-not-allowed' : ''}`}
            >
              <div className="flex items-start justify-between mb-3">
                <div>
                  <h3 className="text-lg font-bold text-gray-900">{provider.name}</h3>
                  <p className="text-sm text-gray-600">{provider.description}</p>
                </div>
                {selectedProvider === provider.id && (
                  <div className="bg-blue-600 text-white p-2 rounded-full">
                    <Check size={20} />
                  </div>
                )}
              </div>

              {/* Status Badge */}
              <div className="flex items-center gap-2">
                <span
                  className={`px-3 py-1 rounded-full text-xs font-medium ${
                    provider.available
                      ? 'bg-green-100 text-green-800'
                      : 'bg-yellow-100 text-yellow-800'
                  }`}
                >
                  {provider.status === 'active'
                    ? t('payments.providers.active')
                    : t('payments.providers.coming_soon')}
                </span>
              </div>

              {/* Coming Soon Message */}
              {!provider.available && (
                <p className="text-xs text-gray-500 mt-3">
                  {t('payments.providers.setup_required')}
                </p>
              )}
            </motion.div>
          ))}
        </div>

        {/* Selected Provider Details */}
        {selectedProvider === 'stripe' && (
          <motion.div
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            className="mt-8 bg-white rounded-lg shadow p-6 border border-gray-200"
          >
            <h2 className="text-xl font-bold mb-4">{t('payments.providers.stripe_details')}</h2>
            <ul className="space-y-2 text-sm text-gray-700">
              <li className="flex items-center gap-2">
                <Check size={16} className="text-green-600" />
                {t('payments.providers.stripe_feature_1')}
              </li>
              <li className="flex items-center gap-2">
                <Check size={16} className="text-green-600" />
                {t('payments.providers.stripe_feature_2')}
              </li>
              <li className="flex items-center gap-2">
                <Check size={16} className="text-green-600" />
                {t('payments.providers.stripe_feature_3')}
              </li>
              <li className="flex items-center gap-2">
                <Check size={16} className="text-green-600" />
                {t('payments.providers.stripe_feature_4')}
              </li>
            </ul>
          </motion.div>
        )}

        {/* Future Providers Info */}
        <motion.div
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          className="mt-8 bg-yellow-50 border border-yellow-200 rounded-lg p-6"
        >
          <h3 className="font-bold text-yellow-900 mb-3">
            {t('payments.providers.future_title')}
          </h3>
          <p className="text-sm text-yellow-800 mb-3">
            {t('payments.providers.future_desc')}
          </p>
          <ul className="space-y-2 text-sm text-yellow-800">
            <li>• {t('payments.providers.future_note_1')}</li>
            <li>• {t('payments.providers.future_note_2')}</li>
            <li>• {t('payments.providers.future_note_3')}</li>
          </ul>
        </motion.div>
      </motion.div>
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { useTranslations } from 'next-intl';
import { motion } from 'framer-motion';
import { Package, AlertCircle, CheckCircle, Clock, TruckIcon } from 'lucide-react';

interface FulfillmentJob {
  id: string;
  order_id: string;
  fulfillment_type: string;
  status: string;
  tracking_number?: string;
  carrier?: string;
  error_message?: string;
  retry_count: number;
  created_at: string;
  orders?: {
    order_number: string;
    buyer_name: string;
    total_amount: number;
  };
  suppliers?: {
    name: string;
  };
}

export default function FulfillmentDashboard() {
  const _t = useTranslations();
  const [jobs, setJobs] = useState<FulfillmentJob[]>([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState<string>('all');

  useEffect(() => {
    fetchJobs();
  }, [filter]);

  const fetchJobs = async () => {
    try {
      const url = filter === 'all' ? '/api/fulfillment' : `/api/fulfillment?status=${filter}`;
      const response = await fetch(url);
      const data = await response.json();
      setJobs(data.jobs || []);
    } catch (error) {
      console.error('Error fetching jobs:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRetry = async (jobId: string) => {
    try {
      await fetch(`/api/fulfillment/${jobId}/retry`, { method: 'POST' });
      fetchJobs();
    } catch (error) {
      console.error('Error retrying job:', error);
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case'queued':
        return <Clock className="w-5 h-5 text-yellow-500" />;
      case 'processing':
        return <Package className="w-5 h-5 text-blue-500" />;
      case 'shipped':
        return <TruckIcon className="w-5 h-5 text-green-500" />;
      case 'delivered':
        return <CheckCircle className="w-5 h-5 text-green-600" />;
      case 'failed':
        return <AlertCircle className="w-5 h-5 text-red-500" />;
      default:
        return <Package className="w-5 h-5 text-gray-500" />;
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-[#05070A] via-[#0A0F1C] to-[#05070A] flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#05070A] via-[#0A0F1C] to-[#05070A] py-16 px-4">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-4xl font-bold text-white mb-8">Fulfillment Dashboard</h1>

        {/* Status Filter */}
        <div className="flex gap-2 mb-6 overflow-x-auto">
          {['all', 'queued', 'processing', 'shipped', 'delivered', 'failed'].map((status) => (
            <button
              key={status}
              onClick={() => setFilter(status)}
              className={`px-4 py-2 rounded-full text-sm font-medium whitepsace-nowrap ${
                filter === status
                  ? 'bg-blue-500 text-white'
                  : 'bg-white/5 text-gray-400 hover:bg-white/10'
              }`}
            >
              {status.charAt(0).toUpperCase() + status.slice(1)}
            </button>
          ))}
        </div>

        {/* Jobs List */}
        <div className="space-y-4">
          {jobs.length === 0 ? (
            <div className="bg-white/5 backdrop-blur-sm border border-white/10 rounded-2xl p-12 text-center">
              <Package className="w-16 h-16 text-gray-500 mx-auto mb-4" />
              <h3 className="text-xl font-semibold text-white mb-2">No fulfillment jobs</h3>
              <p className="text-gray-400">No jobs found with this filter</p>
            </div>
          ) : (
            jobs.map((job) => (
              <motion.div
                key={job.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="bg-white/5 backdrop-blur-sm border border-white/10 rounded-2xl p-6"
              >
                <div className="flex items-start justify-between mb-4">
                  <div className="flex items-center gap-3">
                    {getStatusIcon(job.status)}
                    <div>
                      <h3 className="text-lg font-semibold text-white">
                        Order #{job.orders?.order_number}
                      </h3>
                      <p className="text-sm text-gray-400">
                        {job.fulfillment_type} • {job.suppliers?.name || 'Manual'}
                      </p>
                    </div>
                  </div>
                  <span
                    className={`px-4 py-2 rounded-full text-sm font-medium ${
                      job.status === 'delivered'
                        ? 'bg-green-500/10 text-green-500'
                        : job.status === 'failed'
                        ? 'bg-red-500/10 text-red-500'
                        : job.status === 'shipped'
                        ? 'bg-blue-500/10 text-blue-500'
                        : 'bg-yellow-500/10 text-yellow-500'
                    }`}
                  >
                    {job.status}
                  </span>
                </div>

                {job.tracking_number && (
                  <div className="mb-4 p-3 bg-white/5 rounded-lg">
                    <p className="text-sm text-gray-400">Tracking</p>
                    <p className="text-white font-mono">{job.tracking_number}</p>
                    <p className="text-sm text-gray-400">{job.carrier}</p>
                  </div>
                )}

                {job.error_message && (
                  <div className="mb-4 p-3 bg-red-500/10 rounded-lg">
                    <p className="text-sm text-red-400">Error</p>
                    <p className="text-white text-sm">{job.error_message}</p>
                    <p className="text-sm text-gray-400 mt-1">
                      Retry attempts: {job.retry_count}
                    </p>
                  </div>
                )}

                {job.status === 'failed' && (
                  <button
                    onClick={() => handleRetry(job.id)}
                    className="w-full bg-blue-500 hover:bg-blue-600 text-white font-medium py-2 px-4 rounded-lg transition-colors"
                  >
                    Retry Fulfillment
                  </button>
                )}
              </motion.div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

'use client';

import { useMemo, useState } from 'react';
import { useTranslations } from 'next-intl';
import { Check, Sparkles } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card } from '@/components/ui/card';
import {
  SUBSCRIPTION_PLANS,
  SUBSCRIPTION_CONFIG,
  type BillingInterval,
  type SubscriptionPlan,
  getPriceId,
} from '@/lib/stripe/plans';
import { useSubscriptionStatus } from '@/hooks/useSubscriptionStatus';

const PLAN_ORDER: SubscriptionPlan[] = ['starter', 'pro', 'business'];

export default function PricingPage() {
  const t = useTranslations('subscription');
  const [interval, setInterval] = useState<BillingInterval>('monthly');
  const [actionError, setActionError] = useState<string | null>(null);
  const { data, loading, error, startCheckout, openCustomerPortal } = useSubscriptionStatus();

  const currentPlan = data?.subscription?.plan || null;
  const hasSubscription = data?.hasSubscription === true;

  const planFeatures = useMemo(() => {
    return PLAN_ORDER.reduce<Record<SubscriptionPlan, string[]>>((acc, planKey) => {
      const raw = t.raw(`plans.${planKey}.features`) as string[];
      acc[planKey] = Array.isArray(raw) ? raw : [];
      return acc;
    }, {} as Record<SubscriptionPlan, string[]>);
  }, [t]);

  const formatPrice = (amount: number) => {
    return amount.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 });
  };

  const formatGel = (amount: number) => {
    const gelAmount = amount * SUBSCRIPTION_CONFIG.gelRate;
    return gelAmount.toLocaleString('en-US', { maximumFractionDigits: 0 });
  };

  const resolveActionLabel = (planKey: SubscriptionPlan) => {
    if (!hasSubscription) {
      return SUBSCRIPTION_CONFIG.trialDays > 0
        ? t('actions.startTrial', { days: SUBSCRIPTION_CONFIG.trialDays })
        : t('actions.subscribe');
    }

    if (planKey === currentPlan) {
      return t('actions.manageBilling');
    }

    if (!currentPlan) {
      return t('actions.subscribe');
    }

    const currentPlanConfig = SUBSCRIPTION_PLANS[currentPlan as SubscriptionPlan];
    const targetPlanConfig = SUBSCRIPTION_PLANS[planKey];
    const currentAmount = interval === 'monthly' ? currentPlanConfig.amountMonthlyUSD : currentPlanConfig.amountYearlyUSD;
    const targetAmount = interval === 'monthly' ? targetPlanConfig.amountMonthlyUSD : targetPlanConfig.amountYearlyUSD;

    return targetAmount > currentAmount ? t('actions.upgrade') : t('actions.downgrade');
  };

  const handlePlanAction = async (planKey: SubscriptionPlan) => {
    try {
      setActionError(null);
      if (hasSubscription && planKey === currentPlan) {
        await openCustomerPortal();
        return;
      }

      const priceId = getPriceId(planKey, interval);
      await startCheckout(priceId);
    } catch (err) {
      setActionError(err instanceof Error ? err.message : t('errors.checkoutFailed'));
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#05070A] via-[#0A0F1C] to-[#05070A] py-16 px-4">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-12">
          <p className="text-cyan-400 uppercase tracking-[0.3em] text-xs mb-4">
            {t('pricing.kicker')}
          </p>
          <h1 className="text-4xl md:text-5xl font-bold text-white mb-4">
            {t('pricing.title')}
          </h1>
          <p className="text-gray-400 max-w-2xl mx-auto">
            {t('pricing.subtitle')}
          </p>

          <div className="mt-8 flex flex-wrap items-center justify-center gap-3">
            <button
              onClick={() => setInterval('monthly')}
              className={`px-6 py-2 rounded-full text-sm font-semibold transition ${
                interval === 'monthly'
                  ? 'bg-cyan-500/20 text-cyan-200 border border-cyan-500/40'
                  : 'bg-white/5 text-gray-400 border border-white/10 hover:bg-white/10'
              }`}
            >
              {t('interval.monthly')}
            </button>
            <button
              onClick={() => setInterval('yearly')}
              className={`px-6 py-2 rounded-full text-sm font-semibold transition ${
                interval === 'yearly'
                  ? 'bg-cyan-500/20 text-cyan-200 border border-cyan-500/40'
                  : 'bg-white/5 text-gray-400 border border-white/10 hover:bg-white/10'
              }`}
            >
              {t('interval.yearly')}
            </button>
            <span className="text-xs text-gray-500">
              {t('pricing.currency_note', { currency: SUBSCRIPTION_CONFIG.currency })}
            </span>
          </div>

          {(error || actionError) && (
            <div className="mt-6 inline-flex items-center gap-2 px-4 py-2 rounded-lg bg-red-500/10 text-red-300 border border-red-500/30">
              {actionError || t('errors.loadFailed')}
            </div>
          )}
        </div>

        <div className="grid lg:grid-cols-3 gap-8">
          {PLAN_ORDER.map((planKey, _index) => {
            const planConfig = SUBSCRIPTION_PLANS[planKey];
            const price = interval === 'monthly' ? planConfig.amountMonthlyUSD : planConfig.amountYearlyUSD;
            const isCurrent = planKey === currentPlan;
            const highlight = planKey === 'pro';

            return (
              <Card
                key={planKey}
                className={`relative bg-black/40 border p-8 transition ${
                  highlight
                    ? 'border-cyan-500/50 shadow-[0_0_40px_rgba(34,211,238,0.25)]'
                    : 'border-white/10'
                } ${isCurrent ? 'ring-2 ring-emerald-400/40' : ''}`}
              >
                {highlight && (
                  <span className="absolute -top-4 left-1/2 -translate-x-1/2 px-4 py-1 rounded-full bg-gradient-to-r from-cyan-500 to-blue-600 text-xs font-semibold text-white flex items-center gap-1">
                    <Sparkles className="w-3 h-3" />
                    {t('pricing.popular')}
                  </span>
                )}

                <div className="mb-6">
                  <h3 className="text-2xl font-bold text-white">
                    {t(`plans.${planKey}.title` as unknown)}
                  </h3>
                  <p className="text-gray-400 mt-2">
                    {t(`plans.${planKey}.description` as unknown)}
                  </p>
                </div>

                <div className="mb-6">
                  <div className="flex items-baseline gap-2">
                    <span className="text-4xl font-bold text-white">${formatPrice(price)}</span>
                    <span className="text-sm text-gray-400">/{t(`interval.${interval}` as unknown)}</span>
                  </div>
                  <div className="text-xs text-gray-500 mt-2">
                    {t('pricing.approx_gel', { amount: formatGel(price) })}
                  </div>
                </div>

                <div className="space-y-3 mb-8">
                  {planFeatures[planKey].map((feature) => (
                    <div key={feature} className="flex items-start gap-3">
                      <Check className="w-5 h-5 text-emerald-400 flex-shrink-0 mt-0.5" />
                      <span className="text-gray-300 text-sm">{feature}</span>
                    </div>
                  ))}
                </div>

                {isCurrent && hasSubscription && (
                  <div className="mb-4 text-xs text-emerald-300">
                    {t('pricing.subscribed_notice')}
                  </div>
                )}

                <Button
                  className={`w-full ${
                    isCurrent
                      ? 'bg-white/10 hover:bg-white/20 text-white'
                      : highlight
                      ? 'bg-gradient-to-r from-cyan-500 to-blue-600 text-white'
                      : 'bg-white/10 text-white hover:bg-white/20'
                  }`}
                  disabled={loading}
                  onClick={() => handlePlanAction(planKey)}
                >
                  {loading ? t('actions.loading') : resolveActionLabel(planKey)}
                </Button>
              </Card>
            );
          })}
        </div>
      </div>
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { useTranslations } from 'next-intl';
import { createClient } from '@supabase/supabase-js';
import { motion } from 'framer-motion';

interface Shipment {
  id: string;
  status: string;
  carrier: string;
  tracking_number: string | null;
  tracking_url: string | null;
  shipped_at: string | null;
  delivered_at: string | null;
  service_level: string;
  shipment_events: Array<{
    id: string;
    status: string;
    location: string | null;
    message: string | null;
    occurred_at: string;
    source: string;
  }>;
}

interface OrderDetail {
  id: string;
  buyer_name: string;
  total_amount: number;
  fulfillment_status: string;
  shipping_address_json: unknown;
  created_at: string;
  order_items: Array<{
    id: string;
    product_name: string;
    quantity: number;
    unit_price: number;
  }>;
}

export default function FulfillmentDetail({
  params,
}: {
  params: { id: string; locale: string };
}) {
  const t = useTranslations();
  const supabase = createClient(
    process.env.NEXT_PUBLIC_SUPABASE_URL || '',
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY || ''
  );

  const [order, setOrder] = useState<OrderDetail | null>(null);
  const [shipments, setShipments] = useState<Shipment[]>([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [_activeTab, _setActiveTab] = useState<'shipments' | 'timeline'>('shipments');
  const [trackingNumber, setTrackingNumber] = useState('');
  const [trackingUrl, setTrackingUrl] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('in_transit');
  const [updateMessage, setUpdateMessage] = useState('');

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const {
          data: { user },
        } = await supabase.auth.getUser();
        if (!user) return;

        const { data: orderData, error: orderError } = await supabase
          .from('orders')
          .select(
            `
            id,
            buyer_name,
            total_amount,
            fulfillment_status,
            shipping_address_json,
            created_at,
            order_items (
              id,
              product_name,
              quantity,
              unit_price
            )
          `
          )
          .eq('id', params.id)
          .single();

        if (orderError) throw orderError;
        setOrder(orderData);

        const { data: shipmentData, error: shipmentError } = await supabase
          .from('shipments')
          .select(
            `
            id,
            status,
            carrier,
            tracking_number,
            tracking_url,
            shipped_at,
            delivered_at,
            service_level,
            shipment_events (
              id,
              status,
              location,
              message,
              occurred_at,
              source
            )
          `
          )
          .eq('order_id', params.id)
          .eq('seller_user_id', user.id);

        if (shipmentError) throw shipmentError;
        setShipments(shipmentData || []);
      } catch (error) {
        console.error('Error fetching order:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchOrder();
  }, [params.id, supabase]);

  const handleUpdateShipment = async (shipmentId: string) => {
    if (!trackingNumber.trim()) {
      alert(t('shipping.fulfillment.error_tracking_number'));
      return;
    }

    setSaving(true);
    try {
      const response = await fetch(`/api/shipments/${shipmentId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          trackingNumber,
          trackingUrl,
          status: selectedStatus,
          message: updateMessage || undefined,
        }),
      });

      if (!response.ok) throw new Error('Failed to update shipment');

      // Refresh shipments
      const {
        data: { user },
      } = await supabase.auth.getUser();
      if (user) {
        const { data } = await supabase
          .from('shipments')
          .select(
            `
            id,
            status,
            carrier,
            tracking_number,
            tracking_url,
            shipped_at,
            delivered_at,
            service_level,
            shipment_events (
              id,
              status,
              location,
              message,
              occurred_at,
              source
            )
          `
          )
          .eq('order_id', params.id)
          .eq('seller_user_id', user.id);

        setShipments(data || []);
      }

      setTrackingNumber('');
      setTrackingUrl('');
      setSelectedStatus('in_transit');
      setUpdateMessage('');
    } catch (error) {
      console.error('Error updating shipment:', error);
      alert(t('shipping.fulfillment.error_updating'));
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 flex items-center justify-center">
        <div className="text-center">
          <div className="inline-block animate-spin">
            <div className="w-12 h-12 border-4 border-blue-200 border-t-blue-600 rounded-full" />
          </div>
          <p className="mt-4 text-gray-600">{t('common.loading')}</p>
        </div>
      </div>
    );
  }

  if (!order) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 flex items-center justify-center">
        <p className="text-gray-600">{t('shipping.fulfillment.order_not_found')}</p>
      </div>
    );
  }

  const address = order.shipping_address_json || {};

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 p-6">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <motion.div initial={{ opacity: 0, y: -10 }} animate={{ opacity: 1, y: 0 }} className="mb-8">
          <h1 className="text-4xl font-bold text-gray-900 mb-2">
            {t('shipping.fulfillment.order_details')}
          </h1>
          <p className="text-gray-600">{order.id.substring(0, 8)}</p>
        </motion.div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Order Summary */}
          <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="lg:col-span-2">
            <div className="bg-white rounded-xl shadow-sm p-6 mb-6">
              <h2 className="text-lg font-semibold text-gray-900 mb-4">
                {t('shipping.fulfillment.order_summary')}
              </h2>

              <div className="grid grid-cols-2 gap-4 mb-6">
                <div>
                  <p className="text-sm text-gray-500">{t('shipping.fulfillment.buyer')}</p>
                  <p className="text-lg font-medium text-gray-900">{order.buyer_name}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-500">{t('shipping.fulfillment.total')}</p>
                  <p className="text-lg font-medium text-gray-900">
                    ${order.total_amount.toFixed(2)}
                  </p>
                </div>
              </div>

              <div className="mb-6">
                <p className="text-sm text-gray-500 mb-2">
                  {t('shipping.fulfillment.shipping_address')}
                </p>
                <div className="bg-gray-50 rounded-lg p-4">
                  <p className="text-sm text-gray-900 font-medium">{address.name}</p>
                  <p className="text-sm text-gray-700">{address.street}</p>
                  <p className="text-sm text-gray-700">
                    {address.city}, {address.zip} {address.country}
                  </p>
                  <p className="text-sm text-gray-700">{address.phone}</p>
                </div>
              </div>

              {/* Items */}
              <div>
                <p className="text-sm font-semibold text-gray-900 mb-3">
                  {t('shipping.fulfillment.items')}
                </p>
                <div className="space-y-2">
                  {order.order_items?.map((item) => (
                    <div key={item.id} className="flex justify-between text-sm">
                      <span className="text-gray-700">
                        {item.product_name} x{item.quantity}
                      </span>
                      <span className="text-gray-900 font-medium">
                        ${(item.unit_price * item.quantity).toFixed(2)}
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            {/* Shipment Management */}
            <div className="bg-white rounded-xl shadow-sm p-6">
              <h2 className="text-lg font-semibold text-gray-900 mb-4">
                {t('shipping.fulfillment.shipment_management')}
              </h2>

              {shipments.length === 0 ? (
                <p className="text-gray-600 mb-4">
                  {t('shipping.fulfillment.no_shipments')}
                </p>
              ) : (
                <div className="mb-6">
                  {shipments.map((shipment, idx) => (
                    <motion.div
                      key={shipment.id}
                      initial={{ opacity: 0 }}
                      animate={{ opacity: 1 }}
                      transition={{ delay: idx * 0.1 }}
                      className="bg-gray-50 rounded-lg p-4 mb-4"
                    >
                      <div className="flex justify-between items-start mb-3">
                        <div>
                          <p className="text-sm font-medium text-gray-900">
                            {shipment.carrier} - {shipment.service_level}
                          </p>
                          <p className="text-xs text-gray-500 mt-1">
                            {shipment.tracking_number || 'Tracking pending'}
                          </p>
                        </div>
                        <span className="px-3 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                          {shipment.status}
                        </span>
                      </div>
                    </motion.div>
                  ))}
                </div>
              )}

              {/* Update Form */}
              <div className="border-t pt-6">
                <h3 className="font-semibold text-gray-900 mb-4">
                  {t('shipping.fulfillment.update_tracking')}
                </h3>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      {t('shipping.fulfillment.tracking_number')}
                    </label>
                    <input
                      type="text"
                      value={trackingNumber}
                      onChange={(e) => setTrackingNumber(e.target.value)}
                      placeholder={t('shipping.fulfillment.enter_tracking_number')}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      {t('shipping.fulfillment.tracking_url')}
                    </label>
                    <input
                      type="url"
                      value={trackingUrl}
                      onChange={(e) => setTrackingUrl(e.target.value)}
                      placeholder={t('shipping.fulfillment.enter_tracking_url')}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      {t('shipping.fulfillment.status')}
                    </label>
                    <select
                      value={selectedStatus}
                      onChange={(e) => setSelectedStatus(e.target.value)}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    >
                      <option value="label_created">
                        {t('shipping.fulfillment.status_label_created')}
                      </option>
                      <option value="in_transit">
                        {t('shipping.fulfillment.status_in_transit')}
                      </option>
                      <option value="out_for_delivery">
                        {t('shipping.fulfillment.status_out_for_delivery')}
                      </option>
                      <option value="delivered">
                        {t('shipping.fulfillment.status_delivered')}
                      </option>
                    </select>
                  </div>

                  <button
                    onClick={() =>
                      shipments[0] && handleUpdateShipment(shipments[0].id)
                    }
                    disabled={saving}
                    className="w-full bg-blue-600 hover:bg-blue-700 disabled:bg-gray-400 text-white font-medium py-2 px-4 rounded-lg transition-colors"
                  >
                    {saving ? t('common.saving') : t('shipping.fulfillment.update_shipment')}
                  </button>
                </div>
              </div>
            </div>
          </motion.div>

          {/* Sidebar - Status & Timeline */}
          <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="lg:col-span-1">
            <div className="bg-white rounded-xl shadow-sm p-6 sticky top-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">
                {t('shipping.fulfillment.status')}
              </h3>
              <div
                className="px-4 py-3 rounded-lg text-center font-medium mb-4"
                style={{
                  backgroundColor:
                    order.fulfillment_status === 'delivered' ? '#dcfce7' : '#dbeafe',
                  color:
                    order.fulfillment_status === 'delivered' ? '#166534' : '#0c2d6b',
                }}
              >
                {t(`shipping.fulfillment.status_${order.fulfillment_status}`)}
              </div>

              {/* Timeline */}
              {shipments.length > 0 && shipments[0].shipment_events && (
                <div>
                  <h4 className="font-semibold text-gray-900 mb-3 text-sm">
                    {t('shipping.fulfillment.timeline')}
                  </h4>
                  <div className="space-y-3">
                    {[...shipments[0].shipment_events]
                      .sort(
                        (a, b) =>
                          new Date(b.occurred_at).getTime() -
                          new Date(a.occurred_at).getTime()
                      )
                      .slice(0, 5)
                      .map((event) => (
                        <motion.div
                          key={event.id}
                          initial={{ opacity: 0, x: -10 }}
                          animate={{ opacity: 1, x: 0 }}
                          className="text-xs"
                        >
                          <p className="font-medium text-gray-900">{event.status}</p>
                          <p className="text-gray-500">
                            {event.location && `📍 ${event.location}`}
                          </p>
                          <p className="text-gray-400 text-xs mt-1">
                            {new Date(event.occurred_at).toLocaleDateString()}
                          </p>
                        </motion.div>
                      ))}
                  </div>
                </div>
              )}
            </div>
          </motion.div>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { useTranslations } from 'next-intl';
import { motion } from 'framer-motion';
import { ArrowLeft, Package, CheckCircle, XCircle, Clock, Truck } from 'lucide-react';
import Link from 'next/link';
import { useRouter } from 'next/navigation';

interface ReturnRequest {
  id: string;
  order_id: string;
  status: string;
  reason: string;
  comments?: string;
  refund_amount_cents?: number;
  created_at: string;
  orders?: {
    order_number: string;
    total_amount: number;
  };
}

const STATUS_FILTERS = ['all', 'requested', 'approved', 'in_transit', 'received', 'rejected'];

export default function SellerReturnsPage() {
  const t = useTranslations();
  const _router = useRouter();
  const [returns, setReturns] = useState<ReturnRequest[]>([]);
  const [filteredReturns, setFilteredReturns] = useState<ReturnRequest[]>([]);
  const [loading, setLoading] = useState(true);
  const [statusFilter, setStatusFilter] = useState('all');

  useEffect(() => {
    fetchReturns();
  }, []);

  useEffect(() => {
    if (statusFilter === 'all') {
      setFilteredReturns(returns);
    } else {
      setFilteredReturns(returns.filter((r) => r.status === statusFilter));
    }
  }, [statusFilter, returns]);

  const fetchReturns = async () => {
    try {
      const response = await fetch('/api/returns?view=seller');
      const data = await response.json();
      setReturns(data.returns || []);
      setFilteredReturns(data.returns || []);
    } catch (error) {
      console.error('Error fetching returns:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAction = async (returnId: string, action: string, refundAmount?: number) => {
    try {
      const response = await fetch(`/api/returns/${returnId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ action, refundAmount }),
      });

      if (response.ok) {
        fetchReturns();
      }
    } catch (error) {
      console.error('Error updating return:', error);
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'requested':
        return <Clock className="w-5 h-5 text-yellow-500" />;
      case 'approved':
        return <CheckCircle className="w-5 h-5 text-green-500" />;
      case 'rejected':
        return <XCircle className="w-5 h-5 text-red-500" />;
      case 'in_transit':
        return <Truck className="w-5 h-5 text-blue-500" />;
      case 'received':
      case 'refunded':
        return <CheckCircle className="w-5 h-5 text-green-600" />;
      default:
        return <Package className="w-5 h-5 text-gray-500" />;
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-[#05070A] via-[#0A0F1C] to-[#05070A] flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#05070A] via-[#0A0F1C] to-[#05070A] py-16 px-4">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <Link
            href="/sell"
            className="inline-flex items-center gap-2 text-gray-400 hover:text-white transition-colors mb-4"
          >
            <ArrowLeft className="w-4 h-4" />
            {t('common.back')}
          </Link>
          <h1 className="text-4xl font-bold text-white mb-2">
            {t('returns.returnRequests')}
          </h1>
          <p className="text-gray-400">{t('returns.manageCustomerReturns')}</p>
        </div>

        {/* Status Filters */}
        <div className="flex gap-2 mb-6 overflow-x-auto pb-2">
          {STATUS_FILTERS.map((status) => (
            <button
              key={status}
              onClick={() => setStatusFilter(status)}
              className={`px-4 py-2 rounded-full text-sm font-medium transition-all whitespace-nowrap ${
                statusFilter === status
                  ? 'bg-blue-500 text-white'
                  : 'bg-white/5 text-gray-400 hover:bg-white/10'
              }`}
            >
              {t(`returns.filter.${status}`)}
              {status === 'all' && ` (${returns.length})`}
            </button>
          ))}
        </div>

        {/* Returns List */}
        {filteredReturns.length === 0 ? (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="bg-white/5 backdrop-blur-sm border border-white/10 rounded-2xl p-12 text-center"
          >
            <Package className="w-16 h-16 text-gray-500 mx-auto mb-4" />
            <h3 className="text-xl font-semibold text-white mb-2">
              {t('returns.noReturnsFound')}
            </h3>
            <p className="text-gray-400">{t('returns.noReturnsFoundDescription')}</p>
          </motion.div>
        ) : (
          <div className="space-y-4">
            {filteredReturns.map((returnRequest, index) => (
              <motion.div
                key={returnRequest.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.05 }}
                className="bg-white/5 backdrop-blur-sm border border-white/10 rounded-2xl p-6"
              >
                <div className="flex items-start justify-between mb-4">
                  <div className="flex items-center gap-3">
                    {getStatusIcon(returnRequest.status)}
                    <div>
                      <h3 className="text-lg font-semibold text-white">
                        {t('returns.orderNumber')}: {returnRequest.orders?.order_number}
                      </h3>
                      <p className="text-sm text-gray-400">
                        {new Date(returnRequest.created_at).toLocaleDateString()}
                      </p>
                    </div>
                  </div>
                  <span
                    className={`px-4 py-2 rounded-full text-sm font-medium ${
                      returnRequest.status === 'requested'
                        ? 'bg-yellow-500/10 text-yellow-500'
                        : returnRequest.status === 'approved'
                        ? 'bg-green-500/10 text-green-500'
                        : returnRequest.status === 'rejected'
                        ? 'bg-red-500/10 text-red-500'
                        : 'bg-gray-500/10 text-gray-500'
                    }`}
                  >
                    {t(`returns.status.${returnRequest.status}`)}
                  </span>
                </div>

                <div className="grid grid-cols-2 gap-4 mb-4">
                  <div>
                    <p className="text-sm text-gray-400 mb-1">{t('returns.reason')}</p>
                    <p className="text-white">{returnRequest.reason}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-400 mb-1">{t('returns.orderTotal')}</p>
                    <p className="text-white font-semibold">
                      ${((returnRequest.orders?.total_amount || 0) / 100).toFixed(2)}
                    </p>
                  </div>
                </div>

                {returnRequest.comments && (
                  <div className="mb-4 p-3 bg-white/5 rounded-lg">
                    <p className="text-sm text-gray-400 mb-1">{t('returns.buyerComments')}</p>
                    <p className="text-white text-sm">{returnRequest.comments}</p>
                  </div>
                )}

                {/* Action Buttons */}
                {returnRequest.status === 'requested' && (
                  <div className="flex gap-3">
                    <button
                      onClick={() =>
                        handleAction(
                          returnRequest.id,
                          'approve',
                          returnRequest.orders?.total_amount
                        )
                      }
                      className="flex-1 bg-green-500 hover:bg-green-600 text-white font-medium py-2 px-4 rounded-lg transition-colors"
                    >
                      {t('returns.approve')}
                    </button>
                    <button
                      onClick={() => handleAction(returnRequest.id, 'reject')}
                      className="flex-1 bg-red-500 hover:bg-red-600 text-white font-medium py-2 px-4 rounded-lg transition-colors"
                    >
                      {t('returns.reject')}
                    </button>
                  </div>
                )}

                {returnRequest.status === 'approved' && (
                  <button
                    onClick={() => handleAction(returnRequest.id, 'mark_in_transit')}
                    className="w-full bg-blue-500 hover:bg-blue-600 text-white font-medium py-2 px-4 rounded-lg transition-colors"
                  >
                    {t('returns.markInTransit')}
                  </button>
                )}

                {returnRequest.status === 'in_transit' && (
                  <button
                    onClick={() => handleAction(returnRequest.id, 'mark_received')}
                    className="w-full bg-green-500 hover:bg-green-600 text-white font-medium py-2 px-4 rounded-lg transition-colors"
                  >
                    {t('returns.markReceived')}
                  </button>
                )}
              </motion.div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { useParams } from 'next/navigation';
import { motion } from 'framer-motion';
import { Package, TruckIcon, CheckCircle, MapPin } from 'lucide-react';

interface ShipmentEvent {
  timestamp: string;
  status: string;
  location?: string;
  description: string;
}

interface Shipment {
  id: string;
  tracking_number: string;
  carrier: string;
  status: string;
  shipped_at?: string;
  estimated_delivery_at?: string;
  delivered_at?: string;
  tracking_events: ShipmentEvent[];
}

export default function OrderTrackingPage() {
  const params = useParams();
  const orderId = params.orderId as string;
  const [order, setOrder] = useState<unknown>(null);
  const [shipments, setShipments] = useState<Shipment[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (orderId) {
      fetchTracking();
    }
  }, [orderId]);

  const fetchTracking = async () => {
    try {
      const response = await fetch(`/api/tracking/${orderId}`);
      const data = await response.json();
      setOrder(data.order);
      setShipments(data.shipments || []);
    } catch (error) {
      console.error('Error fetching tracking:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-[#05070A] via-[#0A0F1C] to-[#05070A] flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (!order) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-[#05070A] via-[#0A0F1C] to-[#05070A] flex items-center justify-center">
        <div className="text-center">
          <Package className="w-16 h-16 text-gray-500 mx-auto mb-4" />
          <h2 className="text-2xl font-bold text-white mb-2">Order Not Found</h2>
          <p className="text-gray-400">Unable to find tracking information for this order</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#05070A] via-[#0A0F1C] to-[#05070A] py-16 px-4">
      <div className="max-w-4xl mx-auto">
        {/* Order Header */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-white/5 backdrop-blur-sm border border-white/10 rounded-2xl p-8 mb-8"
        >
          <h1 className="text-3xl font-bold text-white mb-2">
            Order #{order.orderNumber}
          </h1>
          <p className="text-gray-400">
            Placed on {new Date(order.createdAt).toLocaleDateString()}
          </p>
          <div className="mt-4 inline-block px-4 py-2 bg-blue-500/10 text-blue-500 rounded-full text-sm font-medium">
            {order.status}
          </div>
        </motion.div>

        {/* Shipments */}
        {shipments.length === 0 ? (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="bg-white/5 backdrop-blur-sm border border-white/10 rounded-2xl p-12 text-center"
          >
            <TruckIcon className="w-16 h-16 text-gray-500 mx-auto mb-4" />
            <h3 className="text-xl font-semibold text-white mb-2">Preparing Shipment</h3>
            <p className="text-gray-400">
              Your order is being prepared. Tracking information will appear here once shipped.
            </p>
          </motion.div>
        ) : (
          shipments.map((shipment, index) => (
            <motion.div
              key={shipment.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
              className="bg-white/5 backdrop-blur-sm border border-white/10 rounded-2xl p-8 mb-6"
            >
              <div className="mb-6">
                <h2 className="text-2xl font-bold text-white mb-2">Shipment {index + 1}</h2>
                <div className="grid grid-cols-2 gap-4 mt-4">
                  <div>
                    <p className="text-sm text-gray-400 mb-1">Tracking Number</p>
                    <p className="text-white font-mono">{shipment.tracking_number}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-400 mb-1">Carrier</p>
                    <p className="text-white">{shipment.carrier}</p>
                  </div>
                  {shipment.estimated_delivery_at && (
                    <div>
                      <p className="text-sm text-gray-400 mb-1">Estimated Delivery</p>
                      <p className="text-white">
                        {new Date(shipment.estimated_delivery_at).toLocaleDateString()}
                      </p>
                    </div>
                  )}
                </div>
              </div>

              {/* Timeline */}
              {shipment.tracking_events && shipment.tracking_events.length > 0 && (
                <div className="space-y-4">
                  <h3 className="text-lg font-semibold text-white mb-4">Tracking History</h3>
                  {shipment.tracking_events.map((event, eventIndex) => (
                    <div key={eventIndex} className="flex gap-4">
                      <div className="flex-shrink-0">
                        {eventIndex === 0 ? (
                          <CheckCircle className="w-6 h-6 text-green-500" />
                        ) : (
                          <div className="w-6 h-6 rounded-full border-2 border-gray-500" />
                        )}
                      </div>
                      <div className="flex-1 pb-4 border-l-2 border-gray-700 pl-4 ml-3">
                        <p className="text-white font-medium">{event.description}</p>
                        <p className="text-sm text-gray-400">
                          {new Date(event.timestamp).toLocaleString()}
                        </p>
                        {event.location && (
                          <p className="text-sm text-gray-400 flex items-center gap-1 mt-1">
                            <MapPin className="w-4 h-4" />
                            {event.location}
                          </p>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </motion.div>
          ))
        )}
      </div>
    </div>
  );
}

/**
 * Admin Affiliates Management Page
 * 
 * List and manage all affiliates
 * Route: /admin/affiliates
 */

'use client';

import { useEffect, useState } from 'react';
import { useTranslations } from 'next-intl';
import { Search, RefreshCw, Check, X } from 'lucide-react';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { DataTable } from '@/components/affiliate/DataTable';
import { StatusBadge } from '@/components/affiliate/StatusBadge';

interface Affiliate {
  user_id: string;
  affiliate_code: string;
  status: 'active' | 'disabled';
  total_clicks: number;
  total_signups: number;
  totals: {
    pending: number;
    eligible: number;
    paid: number;
    total: number;
  };
  created_at: string;
}

export default function AdminAffiliatesPage() {
  const t = useTranslations();
  const [affiliates, setAffiliates] = useState<Affiliate[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [search, setSearch] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');

  useEffect(() => {
    fetchAffiliates();
  }, [search, statusFilter]);

  const fetchAffiliates = async () => {
    try {
      setIsLoading(true);
      
      const params = new URLSearchParams();
      if (search) params.append('search', search);
      if (statusFilter !== 'all') params.append('status', statusFilter);

      const response = await fetch(`/api/admin/affiliates?${params}`);
      
      if (response.status === 403) {
        setError('Unauthorized: Admin access required');
        return;
      }

      if (!response.ok) {
        throw new Error('Failed to fetch affiliates');
      }

const data = await response.json();
      setAffiliates(data.affiliates || []);
      setError(null);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
      console.error('Error fetching affiliates:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const toggleStatus = async (userId: string, currentStatus: string) => {
    try {
      const newStatus = currentStatus === 'active' ? 'disabled' : 'active';
      
      const response = await fetch('/api/admin/affiliates', {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId, status: newStatus }),
      });

      if (!response.ok) {
        throw new Error('Failed to update status');
      }

      // Refresh list
      fetchAffiliates();
    } catch (err) {
      console.error('Error toggling status:', err);
      alert('Failed to update affiliate status');
    }
  };

  const formatCurrency = (cents: number) => {
    return `$${(cents / 100).toFixed(2)}`;
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('ka-GE', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  if (error && error.includes('Unauthorized')) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-[#05070A] via-[#0A0F1C] to-[#05070A] flex items-center justify-center p-4">
        <Card className="bg-red-500/10 border-red-500/30 p-6 max-w-md">
          <h2 className="text-xl font-bold text-red-400 mb-2">
            {t('common.error')}
          </h2>
          <p className="text-red-300">{error}</p>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#05070A] via-[#0A0F1C] to-[#05070A] py-12 px-4">
      <div className="max-w-7xl mx-auto space-y-8">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-4xl font-bold text-white mb-2">
              {t('admin.affiliates.title')}
            </h1>
            <p className="text-gray-400">
              {affiliates.length} {t('admin.affiliates.list_title').toLowerCase()}
            </p>
          </div>
          
          <Button
            onClick={fetchAffiliates}
            variant="outline"
            className="bg-white/5 border-white/10 text-white hover:bg-white/10"
          >
            <RefreshCw className="w-4 h-4 mr-2" />
            {t('common.retry')}
          </Button>
        </div>

        {/* Filters */}
        <Card className="bg-black/40 border-white/10 p-6">
          <div className="grid md:grid-cols-3 gap-4">
            <div className="md:col-span-2">
              <label className="block text-sm text-gray-400 mb-2">
                {t('admin.affiliates.search')}
              </label>
              <div className="relative">
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-gray-400" />
                <input
                  type="text"
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                  placeholder={t('admin.affiliates.search')}
                  className="w-full bg-white/5 border border-white/10 rounded-lg px-10 py-2 text-white placeholder-gray-500 focus:outline-none focus:border-cyan-500/50"
                />
              </div>
            </div>

            <div>
              <label className="block text-sm text-gray-400 mb-2">
                {t('admin.affiliates.status')}
              </label>
              <select
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                className="w-full bg-white/5 border border-white/10 rounded-lg px-4 py-2 text-white focus:outline-none focus:border-cyan-500/50"
              >
                <option value="all">{t('affiliate.commissions.all_statuses')}</option>
                <option value="active">{t('affiliate.status.active')}</option>
                <option value="disabled">{t('affiliate.status.disabled')}</option>
              </select>
            </div>
          </div>
        </Card>

        {/* Affiliates Table */}
        <Card className="bg-black/40 border-white/10 p-6">
          <DataTable
            columns={[
              {
                key: 'code',
                header: t('admin.affiliates.code'),
                render: (item: Affiliate) => (
                  <code className="text-cyan-400 font-mono">
                    {item.affiliate_code}
                  </code>
                ),
                width: '15%',
              },
              {
                key: 'user_id',
                header: t('admin.affiliates.user_id'),
                render: (item: Affiliate) => (
                  <code className="text-xs text-gray-400">
                    {item.user_id.slice(0, 8)}...
                  </code>
                ),
                width: '15%',
              },
              {
                key: 'status',
                header: t('admin.affiliates.status'),
                render: (item: Affiliate) => (
                  <StatusBadge
                    status={item.status}
                    label={t(`affiliate.status.${item.status}`)}
                  />
                ),
                width: '12%',
              },
              {
                key: 'clicks',
                header: t('admin.affiliates.clicks'),
                render: (item: Affiliate) => item.total_clicks,
                width: '10%',
              },
              {
                key: 'signups',
                header: t('admin.affiliates.signups'),
                render: (item: Affiliate) => item.total_signups,
                width: '10%',
              },
              {
                key: 'commissions',
                header: t('admin.affiliates.commissions'),
                render: (item: Affiliate) => (
                  <div className="space-y-1 text-xs">
                    <div className="text-green-400">
                      {formatCurrency(item.totals.eligible)} eligible
                    </div>
                    <div className="text-gray-400">
                      {formatCurrency(item.totals.paid)} paid
                    </div>
                  </div>
                ),
                width: '15%',
              },
              {
                key: 'created',
                header: 'Created',
                render: (item: Affiliate) => (
                  <span className="text-xs text-gray-400">
                    {formatDate(item.created_at)}
                  </span>
                ),
                width: '13%',
              },
              {
                key: 'actions',
                header: t('admin.affiliates.actions'),
                render: (item: Affiliate) => (
                  <Button
                    onClick={() => toggleStatus(item.user_id, item.status)}
                    size="sm"
                    variant="outline"
                    className={`text-xs ${
                      item.status === 'active'
                        ? 'bg-red-500/10 border-red-500/30 text-red-400 hover:bg-red-500/20'
                        : 'bg-green-500/10 border-green-500/30 text-green-400 hover:bg-green-500/20'
                    }`}
                  >
                    {item.status === 'active' ? (
                      <>
                        <X className="w-3 h-3 mr-1" />
                        {t('admin.affiliates.disable')}
                      </>
                    ) : (
                      <>
                        <Check className="w-3 h-3 mr-1" />
                        {t('admin.affiliates.activate')}
                      </>
                    )}
                  </Button>
                ),
                width: '10%',
              },
            ]}
            data={affiliates}
            isLoading={isLoading}
            emptyMessage={t('admin.affiliates.no_affiliates')}
            keyExtractor={(item) => item.user_id}
          />
        </Card>
      </div>
    </div>
  );
}

'use client';

import React, { useEffect, useState } from 'react';
import { useTranslations } from 'next-intl';
import { TrendingUp, DollarSign, Users, ShoppingCart, AlertCircle } from 'lucide-react';
import { motion } from 'framer-motion';

interface FinanceSummary {
  mrr: number;
  arr: number;
  activeSubs: number;
  revenue30d: number;
  gmv30d: number;
  fees30d: number;
  refunds30d: number;
  churn: number;
}

interface TimeseriesData {
  date: string;
  mrr: number;
  revenue: number;
  gmv: number;
  fees: number;
  affiliateCommissions: number;
  payouts: number;
  subscriptionsActive: number;
}

const _COLORS = ['#3b82f6', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6', '#ec4899'];

export default function AdminFinanceDashboard() {
  const _t = useTranslations();
  const [summary, setSummary] = useState<FinanceSummary | null>(null);
  const [timeseries, setTimeseries] = useState<TimeseriesData[]>([]);
  const [range, setRange] = useState('30d');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchFinanceData();
  }, [range]);

  const fetchFinanceData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Fetch summary
      const summaryRes = await fetch(`/api/finance/admin/summary?range=${range}`, {
        method: 'GET',
      });

      if (!summaryRes.ok) {
        throw new Error('Failed to fetch summary');
      }

      const summaryData = await summaryRes.json();
      setSummary(summaryData.summary);

      // Fetch timeseries
      const timeseriesRes = await fetch(`/api/finance/admin/timeseries?range=${range}`, {
        method: 'GET',
      });

      if (!timeseriesRes.ok) {
        throw new Error('Failed to fetch timeseries');
      }

      const timeseriesData = await timeseriesRes.json();
      setTimeseries(timeseriesData.data);
    } catch (err) {
      console.error('Error fetching finance data:', err);
      setError(err instanceof Error ? err.message : 'Failed to load finance data');
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (cents: number) => {
    return `$${(cents / 100).toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;
  };

  const KPICard = ({ label, value, icon: Icon, color = 'blue' }: unknown) => (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className={`bg-white rounded-lg shadow-md p-6 border-l-4 border-${color}-500`}
    >
      <div className="flex items-center justify-between">
        <div>
          <p className="text-gray-600 text-sm mb-1">{label}</p>
          <p className="text-2xl font-bold text-gray-900">{value}</p>
        </div>
        <Icon className={`w-8 h-8 text-${color}-500`} />
      </div>
    </motion.div>
  );

  if (loading) {
    return (
      <div className="p-8 max-w-7xl mx-auto">
        <div className="text-center">
          <div className="animate-spin inline-block w-8 h-8 border-4 border-gray-300 border-t-blue-500 rounded-full"></div>
          <p className="mt-4 text-gray-600">Loading finance dashboard...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8 max-w-7xl mx-auto">
        <div className="bg-red-50 border border-red-200 rounded-lg p-4 flex items-start">
          <AlertCircle className="w-5 h-5 text-red-600 mt-0.5 mr-3 flex-shrink-0" />
          <div>
            <h3 className="font-semibold text-red-900">Error</h3>
            <p className="text-red-700">{error}</p>
          </div>
        </div>
      </div>
    );
  }

  if (!summary) {
    return (
      <div className="p-8 max-w-7xl mx-auto">
        <p className="text-gray-600">No data available</p>
      </div>
    );
  }

  const _revenueBreakdown = [
    { name: 'Subscription Revenue', value: summary.revenue30d },
    { name: 'GMV (Marketplace)', value: summary.gmv30d },
  ];

  const _feesBreakdown = [
    { name: 'Platform Fees', value: summary.fees30d },
    { name: 'Other', value: Math.max(0, summary.revenue30d - summary.fees30d) },
  ];

  return (
    <div className="bg-gray-50 min-h-screen p-8">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Financial Analytics</h1>
            <p className="text-gray-600 mt-1">Platform-wide financial overview</p>
          </div>
          <div className="flex gap-2">
            {['30d', '90d', '180d'].map((r) => (
              <button
                key={r}
                onClick={() => setRange(r)}
                className={`px-4 py-2 rounded-lg font-medium transition ${
                  range === r
                    ? 'bg-blue-600 text-white'
                    : 'bg-white text-gray-700 border border-gray-300 hover:bg-gray-50'
                }`}
              >
                {r === '30d' ? 'Last 30 days' : r === '90d' ? 'Last 90 days' : 'Last 180 days'}
              </button>
            ))}
          </div>
        </div>

        {/* KPI Cards Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <KPICard
            label="Monthly Recurring Revenue (MRR)"
            value={formatCurrency(summary.mrr)}
            icon={TrendingUp}
            color="blue"
          />
          <KPICard
            label="Annual Recurring Revenue (ARR)"
            value={formatCurrency(summary.arr)}
            icon={DollarSign}
            color="green"
          />
          <KPICard
            label="Active Subscriptions"
            value={summary.activeSubs.toLocaleString()}
            icon={Users}
            color="purple"
          />
          <KPICard
            label="Gross Margin Value (GMV)"
            value={formatCurrency(summary.gmv30d)}
            icon={ShoppingCart}
            color="amber"
          />
        </div>

        {/* Revenue & Fees Summary Row */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Revenue Summary (30d)</h3>
            <div className="space-y-2">
              <div className="flex justify-between">
                <span className="text-gray-600">Subscription Revenue</span>
                <span className="font-medium">{formatCurrency(summary.revenue30d)}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Refunds</span>
                <span className="font-medium text-red-600">{formatCurrency(summary.refunds30d)}</span>
              </div>
              <div className="border-t pt-2 mt-2">
                <div className="flex justify-between font-bold">
                  <span>Net Revenue</span>
                  <span>{formatCurrency(summary.revenue30d - summary.refunds30d)}</span>
                </div>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Fees & Payouts (30d)</h3>
            <div className="space-y-2">
              <div className="flex justify-between">
                <span className="text-gray-600">Platform Fees</span>
                <span className="font-medium text-green-600">{formatCurrency(summary.fees30d)}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Churn Rate</span>
                <span className="font-medium">{summary.churn.toFixed(2)}%</span>
              </div>
            </div>
          </div>
        </div>

        {/* Charts Section - Tables for now (recharts requires installation) */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          {/* Revenue Trend */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Revenue Trend (Last 7 Days)</h3>
            <div className="overflow-x-auto">
              <table className="min-w-full text-sm">
                <thead className="border-b">
                  <tr>
                    <th className="text-left py-2 px-3 font-medium">Date</th>
                    <th className="text-left py-2 px-3 font-medium">Revenue</th>
                    <th className="text-left py-2 px-3 font-medium">MRR</th>
                  </tr>
                </thead>
                <tbody>
                  {timeseries.slice(0, 7).map((row) => (
                    <tr key={row.date} className="border-b hover:bg-gray-50">
                      <td className="py-2 px-3">{row.date}</td>
                      <td className="py-2 px-3">{formatCurrency(row.revenue)}</td>
                      <td className="py-2 px-3">{formatCurrency(row.mrr)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

          {/* GMV & Fees Trend */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">GMV & Fees Trend (Last 7 Days)</h3>
            <div className="overflow-x-auto">
              <table className="min-w-full text-sm">
                <thead className="border-b">
                  <tr>
                    <th className="text-left py-2 px-3 font-medium">Date</th>
                    <th className="text-left py-2 px-3 font-medium">GMV</th>
                    <th className="text-left py-2 px-3 font-medium">Platform Fees</th>
                  </tr>
                </thead>
                <tbody>
                  {timeseries.slice(0, 7).map((row) => (
                    <tr key={row.date} className="border-b hover:bg-gray-50">
                      <td className="py-2 px-3">{row.date}</td>
                      <td className="py-2 px-3">{formatCurrency(row.gmv)}</td>
                      <td className="py-2 px-3">{formatCurrency(row.fees)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>

        {/* Affiliate & Payouts */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Affiliate Commissions (Last 7 Days)</h3>
            <div className="overflow-x-auto">
              <table className="min-w-full text-sm">
                <thead className="border-b">
                  <tr>
                    <th className="text-left py-2 px-3 font-medium">Date</th>
                    <th className="text-left py-2 px-3 font-medium">Commissions</th>
                  </tr>
                </thead>
                <tbody>
                  {timeseries.slice(0, 7).map((row) => (
                    <tr key={row.date} className="border-b hover:bg-gray-50">
                      <td className="py-2 px-3">{row.date}</td>
                      <td className="py-2 px-3">{formatCurrency(row.affiliateCommissions)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Seller Payouts (Last 7 Days)</h3>
            <div className="overflow-x-auto">
              <table className="min-w-full text-sm">
                <thead className="border-b">
                  <tr>
                    <th className="text-left py-2 px-3 font-medium">Date</th>
                    <th className="text-left py-2 px-3 font-medium">Payouts</th>
                  </tr>
                </thead>
                <tbody>
                  {timeseries.slice(0, 7).map((row) => (
                    <tr key={row.date} className="border-b hover:bg-gray-50">
                      <td className="py-2 px-3">{row.date}</td>
                      <td className="py-2 px-3">{formatCurrency(row.payouts)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import React, { useEffect, useState } from 'react';
import { useTranslations } from 'next-intl';
import { TrendingUp, DollarSign, Copy, AlertCircle } from 'lucide-react';
import { motion } from 'framer-motion';

interface AffiliateFinanceSummary {
  affiliate: {
    id: string;
    status: string;
    referralCode: string;
  };
  commissions: unknown[];
  payouts: unknown[];
  balances: {
    pending: number;
    available: number;
    paid: number;
    reversed: number;
    total: number;
  };
  summary: {
    totalEarned: number;
    totalPaidOut: number;
    commissionCount: number;
    payoutCount: number;
  };
}

const _COLORS = ['#3b82f6', '#10b981', '#f59e0b', '#ef4444'];

export default function AffiliateFinanceDashboard() {
  const _t = useTranslations();
  const [data, setData] = useState<AffiliateFinanceSummary | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [copied, setCopied] = useState(false);

  useEffect(() => {
    fetchAffiliateFinance();
  }, []);

  const fetchAffiliateFinance = async () => {
    try {
      setLoading(true);
      setError(null);

      const res = await fetch('/api/finance/affiliate/summary', {
        method: 'GET',
      });

      if (!res.ok) {
        if (res.status === 404) {
          setError('You are not registered as an affiliate');
        } else {
          throw new Error('Failed to fetch affiliate finance data');
        }
        setLoading(false);
        return;
      }

      const data = await res.json();
      setData(data);
    } catch (err) {
      console.error('Error fetching affiliate finance:', err);
      setError(err instanceof Error ? err.message : 'Failed to load affiliate finance data');
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (cents: number) => {
    return `$${(cents / 100).toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  const BalanceCard = ({ label, value, icon: Icon, color = 'blue' }: unknown) => (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className={`bg-white rounded-lg shadow-md p-6 border-l-4 border-${color}-500`}
    >
      <div className="flex items-center justify-between">
        <div>
          <p className="text-gray-600 text-sm mb-1">{label}</p>
          <p className="text-2xl font-bold text-gray-900">{value}</p>
        </div>
        <Icon className={`w-8 h-8 text-${color}-500`} />
      </div>
    </motion.div>
  );

  if (loading) {
    return (
      <div className="p-8 max-w-7xl mx-auto">
        <div className="text-center">
          <div className="animate-spin inline-block w-8 h-8 border-4 border-gray-300 border-t-blue-500 rounded-full"></div>
          <p className="mt-4 text-gray-600">Loading affiliate dashboard...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8 max-w-7xl mx-auto">
        <div className="bg-red-50 border border-red-200 rounded-lg p-4 flex items-start">
          <AlertCircle className="w-5 h-5 text-red-600 mt-0.5 mr-3 flex-shrink-0" />
          <div>
            <h3 className="font-semibold text-red-900">Error</h3>
            <p className="text-red-700">{error}</p>
          </div>
        </div>
      </div>
    );
  }

  if (!data) {
    return (
      <div className="p-8 max-w-7xl mx-auto">
        <p className="text-gray-600">No data available</p>
      </div>
    );
  }

  // Prepare commission data by date
  const commissionsByDate = data.commissions.reduce((acc: unknown, comm: unknown) => {
    const date = new Date(comm.created_at).toISOString().slice(0, 10);
    if (!acc[date]) {
      acc[date] = { date, pending: 0, available: 0, paid: 0 };
    }
    if (comm.status === 'pending') acc[date].pending += comm.commission_cents || 0;
    if (comm.status === 'available') acc[date].available += comm.commission_cents || 0;
    if (comm.status === 'paid') acc[date].paid += comm.commission_cents || 0;
    return acc;
  }, {});

  const commissionChartData = Object.values(commissionsByDate).slice(-30);

  // Prepare balance breakdown for pie chart
  const _balanceData = [
    { name: 'Pending', value: data.balances.pending },
    { name: 'Available', value: data.balances.available },
    { name: 'Paid', value: data.balances.paid },
  ].filter((item) => item.value > 0);

  return (
    <div className="bg-gray-50 min-h-screen p-8">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Affiliate Earnings</h1>
          <p className="text-gray-600 mt-1">Referral code: {data.affiliate.referralCode}</p>
        </div>

        {/* Balance Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <BalanceCard
            label="Pending Earnings"
            value={formatCurrency(data.balances.pending)}
            icon={AlertCircle}
            color="amber"
          />
          <BalanceCard
            label="Available to Withdraw"
            value={formatCurrency(data.balances.available)}
            icon={TrendingUp}
            color="green"
          />
          <BalanceCard
            label="Already Paid"
            value={formatCurrency(data.balances.paid)}
            icon={DollarSign}
            color="blue"
          />
          <BalanceCard
            label="Total Earned"
            value={formatCurrency(data.summary.totalEarned)}
            icon={TrendingUp}
            color="purple"
          />
        </div>

        {/* Referral Code Section */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-8 border-l-4 border-blue-500">
          <h3 className="text-lg font-semibold text-gray-900 mb-2">Your Referral Link</h3>
          <div className="flex items-center gap-2 bg-gray-100 rounded p-3 mb-2">
            <code className="flex-1 font-mono text-sm">{data.affiliate.referralCode}</code>
            <button
              onClick={() => copyToClipboard(data.affiliate.referralCode)}
              className="inline-flex items-center gap-2 px-3 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
            >
              <Copy className="w-4 h-4" />
              {copied ? 'Copied!' : 'Copy'}
            </button>
          </div>
          <p className="text-sm text-gray-600">Share this code with friends to earn commissions on their purchases</p>
        </div>

        {/* Summary Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          {/* Commission Balance */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Commission Balance</h3>
            <div className="space-y-3">
              <div className="flex justify-between">
                <span className="text-gray-600">Pending (7-day hold)</span>
                <span className="font-medium text-amber-600">{formatCurrency(data.balances.pending)}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Ready to Withdraw</span>
                <span className="font-medium text-green-600">{formatCurrency(data.balances.available)}</span>
              </div>
              <div className="border-t pt-3 mt-3">
                <div className="flex justify-between font-bold">
                  <span>Total Available</span>
                  <span>{formatCurrency(data.balances.pending + data.balances.available)}</span>
                </div>
              </div>
            </div>
          </div>

          {/* Statistics */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Statistics</h3>
            <div className="space-y-3">
              <div className="flex justify-between">
                <span className="text-gray-600">Commission Events</span>
                <span className="font-medium">{data.summary.commissionCount}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Payouts Received</span>
                <span className="font-medium">{data.summary.payoutCount}</span>
              </div>
              <div className="border-t pt-3 mt-3">
                <div className="flex justify-between">
                  <span>Affiliate Status</span>
                  <span className={`px-2 py-1 rounded text-sm font-medium ${
                    data.affiliate.status === 'active' ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-800'
                  }`}>
                    {data.affiliate.status}
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Charts - Tables for now (recharts requires installation) */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          {/* Commission Trend */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Commissions by Status (Last 7 Days)</h3>
            {commissionChartData.length > 0 ? (
              <div className="overflow-x-auto">
                <table className="min-w-full text-sm">
                  <thead className="border-b">
                    <tr>
                      <th className="text-left py-2 px-3 font-medium">Date</th>
                      <th className="text-left py-2 px-3 font-medium">Pending</th>
                      <th className="text-left py-2 px-3 font-medium">Available</th>
                      <th className="text-left py-2 px-3 font-medium">Paid</th>
                    </tr>
                  </thead>
                  <tbody>
                    {(commissionChartData as unknown).slice(0, 7).map((row: unknown) => (
                      <tr key={row.date} className="border-b hover:bg-gray-50">
                        <td className="py-2 px-3">{row.date}</td>
                        <td className="py-2 px-3 text-amber-600">{formatCurrency(row.pending)}</td>
                        <td className="py-2 px-3 text-green-600">{formatCurrency(row.available)}</td>
                        <td className="py-2 px-3 text-blue-600">{formatCurrency(row.paid)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <p className="text-gray-600 text-center py-8">No commission data yet</p>
            )}
          </div>

          {/* Balance Summary */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Balance Summary</h3>
            <div className="space-y-3">
              <div className="flex justify-between items-center p-3 bg-amber-50 rounded">
                <span className="text-gray-700">Pending (7-day hold)</span>
                <span className="font-semibold text-amber-700">{formatCurrency(data.balances.pending)}</span>
              </div>
              <div className="flex justify-between items-center p-3 bg-green-50 rounded">
                <span className="text-gray-700">Ready to Withdraw</span>
                <span className="font-semibold text-green-700">{formatCurrency(data.balances.available)}</span>
              </div>
              <div className="flex justify-between items-center p-3 bg-blue-50 rounded">
                <span className="text-gray-700">Already Paid</span>
                <span className="font-semibold text-blue-700">{formatCurrency(data.balances.paid)}</span>
              </div>
              <div className="border-t pt-3 mt-3">
                <div className="flex justify-between items-center font-bold">
                  <span>Total Available</span>
                  <span>{formatCurrency(data.balances.pending + data.balances.available)}</span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Recent Commissions */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Recent Commission Events</h3>
          {data.commissions.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="min-w-full">
                <thead>
                  <tr className="border-b">
                    <th className="text-left py-3 px-4 font-semibold text-gray-600">Date</th>
                    <th className="text-left py-3 px-4 font-semibold text-gray-600">Gross Amount</th>
                    <th className="text-left py-3 px-4 font-semibold text-gray-600">Commission (10%)</th>
                    <th className="text-left py-3 px-4 font-semibold text-gray-600">Status</th>
                    <th className="text-left py-3 px-4 font-semibold text-gray-600">Available On</th>
                  </tr>
                </thead>
                <tbody>
                  {data.commissions.slice(0, 10).map((comm) => (
                    <tr key={comm.id} className="border-b hover:bg-gray-50">
                      <td className="py-3 px-4">{new Date(comm.created_at).toLocaleDateString()}</td>
                      <td className="py-3 px-4">{formatCurrency(comm.gross_amount_cents || 0)}</td>
                      <td className="py-3 px-4 font-semibold text-green-600">{formatCurrency(comm.commission_amount_cents || 0)}</td>
                      <td className="py-3 px-4">
                        <span className={`px-2 py-1 rounded text-sm font-medium ${
                          comm.status === 'pending' ? 'bg-amber-100 text-amber-800' : 
                          comm.status === 'available' ? 'bg-green-100 text-green-800' :
                          comm.status === 'paid' ? 'bg-blue-100 text-blue-800' :
                          'bg-gray-100 text-gray-800'
                        }`}>
                          {comm.status}
                        </span>
                      </td>
                      <td className="py-3 px-4 text-sm">
                        {comm.available_at ? new Date(comm.available_at).toLocaleDateString() : '-'}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-gray-600 text-center py-8">No commissions yet. Start referring friends!</p>
          )}
        </div>
      </div>
    </div>
  );
}

/**
 * Admin Payouts API
 * 
 * GET /api/admin/payouts/eligible
 * Get affiliates eligible for payout
 * 
 * POST /api/admin/payouts
 * Create a payout record
 */

import { NextRequest, NextResponse } from 'next/server';
import { requireAdmin } from '@/lib/auth/adminGuard';
import { createRouteHandlerClient } from '@/lib/supabase/server';

const MINIMUM_PAYOUT_CENTS = 5000; // $50.00

export async function GET(_request: NextRequest) {
  try {
    // 1. Verify admin access
    await requireAdmin();

    // 2. Get affiliates with eligible commissions
    const supabase = createRouteHandlerClient();
    
    // Get all active affiliates
    const { data: affiliates, error: affiliatesError } = await supabase
      .from('affiliates')
      .select('*')
      .eq('is_active', true);

    if (affiliatesError) {
      console.error('[Admin] Error fetching affiliates:', affiliatesError);
      return NextResponse.json(
        { error: 'Failed to fetch affiliates' },
        { status: 500 }
      );
    }

    // Calculate eligible amounts for each
    const eligible = await Promise.all(
      (affiliates || []).map(async (affiliate) => {
        const { data: commissions } = await supabase
          .from('affiliate_commission_events')
          .select('commission_amount_cents')
          .eq('affiliate_id', affiliate.id)
          .eq('status', 'available');

        const eligibleAmount = commissions?.reduce((sum, c) => sum + c.commission_amount_cents, 0) || 0;

        // Get last payout
        const { data: lastPayout } = await supabase
          .from('affiliate_payouts')
          .select('requested_at')
          .eq('affiliate_id', affiliate.id)
          .order('requested_at', { ascending: false })
          .limit(1)
          .single();

        return {
          affiliate_id: affiliate.id,
          affiliate_code: affiliate.affiliate_code,
          eligible_amount_cents: eligibleAmount,
          last_payout_at: lastPayout?.requested_at || null,
        };
      })
    );

    // Filter out those below minimum threshold
    const eligibleFiltered = eligible.filter(
      (a) => a.eligible_amount_cents >= MINIMUM_PAYOUT_CENTS
    );

    return NextResponse.json({
      eligible: eligibleFiltered,
      total: eligibleFiltered.length,
      minimum_threshold_cents: MINIMUM_PAYOUT_CENTS,
    });
  } catch (error) {
    console.error('[Admin] Error:', error);
    
    if (error instanceof Error && error.message.includes('Unauthorized')) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 403 });
    }

    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

export async function POST(request: NextRequest) {
  try {
    // 1. Verify admin access
    await requireAdmin();

    // 2. Parse request body
    const body = await request.json();
    const { affiliateId, amountCents, notes } = body;

    if (!affiliateId || !amountCents) {
      return NextResponse.json(
        { error: 'Missing required fields: affiliateId, amountCents' },
        { status: 400 }
      );
    }

    if (amountCents < MINIMUM_PAYOUT_CENTS) {
      return NextResponse.json(
        { error: `Amount must be at least $${MINIMUM_PAYOUT_CENTS / 100}` },
        { status: 400 }
      );
    }

    const supabase = createRouteHandlerClient();

    // 3. Get all eligible commissions
    const { data: eligibleCommissions, error: commissionsError } = await supabase
      .from('affiliate_commission_events')
      .select('*')
      .eq('affiliate_id', affiliateId)
      .eq('status', 'available');

    if (commissionsError || !eligibleCommissions || eligibleCommissions.length === 0) {
      return NextResponse.json(
        { error: 'No eligible commissions found for this affiliate' },
        { status: 400 }
      );
    }

    const totalEligible = eligibleCommissions.reduce((sum, c) => sum + c.commission_amount_cents, 0);

    if (amountCents > totalEligible) {
      return NextResponse.json(
        { error: 'Payout amount exceeds eligible balance' },
        { status: 400 }
      );
    }

    // 4. Create payout record
    const _periodStart = new Date(
      Math.min(...eligibleCommissions.map((c) => new Date(c.created_at).getTime()))
    ).toISOString();
    
    const _periodEnd = new Date().toISOString();

    const { data: payout, error: payoutError } = await supabase
      .from('affiliate_payouts')
      .insert({
        affiliate_id: affiliateId,
        amount_cents: amountCents,
        status: 'paid',
        notes,
        requested_at: new Date().toISOString(),
        processed_at: new Date().toISOString(),
      })
      .select()
      .single();

    if (payoutError) {
      console.error('[Admin] Error creating payout:', payoutError);
      return NextResponse.json(
        { error: 'Failed to create payout' },
        { status: 500 }
      );
    }

    // 5. Update commissions to 'paid' status
    const commissionsToPay = eligibleCommissions.slice(
      0,
      Math.ceil((amountCents / totalEligible) * eligibleCommissions.length)
    );

    const { error: updateError } = await supabase
      .from('affiliate_commission_events')
      .update({
        status: 'paid',
        stripe_object_id: payout.id,
      })
      .in(
        'id',
        commissionsToPay.map((c) => c.id)
      );

    if (updateError) {
      console.error('[Admin] Error updating commissions:', updateError);
      // Rollback payout
      await supabase.from('affiliate_payouts').delete().eq('id', payout.id);
      
      return NextResponse.json(
        { error: 'Failed to update commissions' },
        { status: 500 }
      );
    }

    return NextResponse.json({
      success: true,
      payout,
      commissions_updated: commissionsToPay.length,
    });
  } catch (error) {
    console.error('[Admin] Error:', error);
    
    if (error instanceof Error && error.message.includes('Unauthorized')) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 403 });
    }

    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

/**
 * GET /api/affiliate/analytics
 * Returns affiliate time-series metrics
 */

import { NextRequest, NextResponse } from 'next/server';
import { createRouteHandlerClient } from '@/lib/supabase/server';

export async function GET(_request: NextRequest) {
  try {
    const supabase = createRouteHandlerClient();
    const { data: { user }, error: authError } = await supabase.auth.getUser();

    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const { data: affiliate } = await supabase
      .from('affiliates')
      .select('id')
      .eq('user_id', user.id)
      .single();

    if (!affiliate) {
      return NextResponse.json({ series: [] });
    }

    const startDate = new Date();
    startDate.setDate(startDate.getDate() - 30);

    const { data: commissions } = await supabase
      .from('affiliate_commission_events')
      .select('commission_amount_cents, created_at, status')
      .eq('affiliate_id', affiliate.id)
      .gte('created_at', startDate.toISOString());

    const daily: Record<string, number> = {};
    (commissions || []).forEach((item) => {
      if (item.status === 'reversed') return;
      const dateKey = new Date(item.created_at).toISOString().slice(0, 10);
      daily[dateKey] = (daily[dateKey] || 0) + item.commission_amount_cents;
    });

    const series = Object.entries(daily)
      .sort(([a], [b]) => (a > b ? 1 : -1))
      .map(([date, amount_cents]) => ({ date, amount_cents }));

    return NextResponse.json({ series });
  } catch (error) {
    console.error('[Affiliate] Error fetching analytics:', error);
    return NextResponse.json(
      { error: error instanceof Error ? error.message : 'Failed to fetch analytics' },
      { status: 500 }
    );
  }
}

