// ============================================
// POST /api/commerce/wallet/deposit
// ============================================

export async function POST(request: NextRequest) {
  try {
    // Get authenticated user
    const cookieStore = cookies();
    const supabase = createServerClient(
      getServerEnv().NEXT_PUBLIC_SUPABASE_URL || '',
      getServerEnv().NEXT_PUBLIC_SUPABASE_ANON_KEY || '',
      {
        cookies: {
          getAll() {
            return cookieStore.getAll();
          },
          setAll(cookiesToSet: Array<{ name: string; value: string; options?: unknown }>) {
            try {
              cookiesToSet.forEach(({ name, value, options }) => {
                cookieStore.set(name, value, options);
              });
            } catch {}
          },
        },
      }
    );

    const {
      data: { user },
      error: authError,
    } = await supabase.auth.getUser();

    if (authError || !user) {
      return NextResponse.json({
        success: false,
        error: {
          code: 'UNAUTHORIZED',
          message: 'Not authenticated',
        },
      }, { status: 401 });
    }

    // Parse and validate request body
    const body = await request.json();

    // Note: We don't validate walletId in the schema - we'll use user's wallet
    const validated = {
      amount: body.amount,
      description: body.description,
      metadata: body.metadata,
    };

    // Validate
    if (!validated.amount || validated.amount <= 0) {
      return NextResponse.json({
        success: false,
        error: {
          code: 'INVALID_INPUT',
          message: 'Amount must be a positive number',
        },
      }, { status: 400 });
    }

    if (validated.amount > 1000000) {
      return NextResponse.json({
        success: false,
        error: {
          code: 'INVALID_INPUT',
          message: 'Amount exceeds maximum limit ($1,000,000)',
        },
      }, { status: 400 });
    }

    // Get wallet (auto-create if doesn't exist)
    let wallet = await getWalletByUserId(user.id);

    if (!wallet) {
      // Create wallet
      const { data, error } = await supabase
        .from('shop_wallets')
        .insert({
          user_id: user.id,
          currency: 'USD',
          balance_amount: 0,
        })
        .select()
        .single();

      if (error) {
        console.error('[POST /api/commerce/wallet/deposit] Create wallet error:', error);
        return NextResponse.json({
          success: false,
          error: {
            code: 'WALLET_CREATE_FAILED',
            message: 'Failed to create wallet',
          },
        }, { status: 500 });
      }

      wallet = data;
    }

    // Deposit to wallet
    const depositResult = await depositToWallet(
      user.id,
      validated.amount,
      validated.description || 'Wallet deposit',
      {
        ...validated.metadata,
        source: 'web_api',
        timestamp: new Date().toISOString(),
      }
    );

    // In production: would integrate with Stripe to actually charge card
    // For now, we allow the deposit (Stripe integration in Phase 2)

    return NextResponse.json({
      success: true,
      data: {
        transaction: {
          id: depositResult.transaction.id,
          amount: depositResult.transaction.amount,
          balanceAfter: depositResult.transaction.balance_after,
          type: depositResult.transaction.type,
          createdAt: depositResult.transaction.created_at,
        },
        wallet: {
          id: depositResult.wallet.id,
          balance: depositResult.wallet.balance_amount,
          currency: depositResult.wallet.currency,
          amlFlagged: depositResult.wallet.aml_risk_score > 50,
        },
      },
    });
  } catch (error) {
    console.error('[POST /api/commerce/wallet/deposit] Error:', error);
    return NextResponse.json({
      success: false,
      error: {
        code: 'INTERNAL_ERROR',
        message: 'Failed to process deposit',
        details: error instanceof Error ? error.message : undefined,
      },
    }, { status: 500 });
  }
}

/**
 * GET /api/finance/affiliate/summary
 * Authenticated affiliate: Commission earnings, pending, available, and paid totals
 */

import { NextRequest, NextResponse } from 'next/server';
import { createRouteHandlerClient } from '@/lib/supabase/server';

export const dynamic = 'force-dynamic';

export async function GET(_request: NextRequest) {
  try {
    const supabase = createRouteHandlerClient();
    const { data: { user }, error: authError } = await supabase.auth.getUser();

    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Get affiliate profile
    const { data: affiliate, error: affError } = await supabase
      .from('affiliates')
      .select('*')
      .eq('user_id', user.id)
      .single();

    if (affError && affError.code !== 'PGRST116') {
      throw affError;
    }

    if (!affiliate) {
      return NextResponse.json({ error: 'Not an affiliate' }, { status: 404 });
    }

    // Get commission events
    const { data: commissions, error: commError } = await supabase
      .from('affiliate_commission_events')
      .select('*')
      .eq('affiliate_id', affiliate.id)
      .order('created_at', { ascending: false });

    if (commError) {
      throw commError;
    }

    // Get payouts
    const { data: payouts, error: payoutsError } = await supabase
      .from('affiliate_payouts')
      .select('*')
      .eq('affiliate_id', affiliate.id)
      .order('created_at', { ascending: false });

    if (payoutsError) {
      throw payoutsError;
    }

    // Calculate balances
    const pending = (commissions || [])
      .filter((c) => c.status === 'pending')
      .reduce((sum, c) => sum + (c.commission_cents || 0), 0);

    const available = (commissions || [])
      .filter((c) => c.status === 'available')
      .reduce((sum, c) => sum + (c.commission_cents || 0), 0);

    const paid = (commissions || [])
      .filter((c) => c.status === 'paid')
      .reduce((sum, c) => sum + (c.commission_cents || 0), 0);

    const reversed = (commissions || [])
      .filter((c) => c.status === 'reversed')
      .reduce((sum, c) => sum + (c.commission_cents || 0), 0);

    const totalPaidOut = (payouts || [])
      .filter((p) => p.status === 'paid')
      .reduce((sum, p) => sum + (p.amount_cents || 0), 0);

    return NextResponse.json({
      affiliate: {
        id: affiliate.id,
        status: affiliate.status,
        referralCode: affiliate.referral_code,
      },
      commissions: commissions || [],
      payouts: payouts || [],
      balances: {
        pending,
        available,
        paid,
        reversed,
        total: pending + available,
      },
      summary: {
        totalEarned: pending + available + paid - reversed,
        totalPaidOut,
        commissionCount: commissions?.length || 0,
        payoutCount: payouts?.length || 0,
      },
    });
  } catch (error) {
    console.error('[GET /api/finance/affiliate/summary]', error);
    return NextResponse.json({ error: 'Failed to fetch affiliate summary' }, { status: 500 });
  }
}

