export async function GET(_request: NextRequest) {
  try {
    const supabase = createRouteHandlerClient();
    const { data: { user }, error: authError } = await supabase.auth.getUser();

    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Get seller profile
    const { data: sellerProfile, error: profileError } = await supabase
      .from('seller_profiles')
      .select('*')
      .eq('user_id', user.id)
      .single();

    if (profileError && profileError.code !== 'PGRST116') {
      throw profileError;
    }

    if (!sellerProfile) {
      return NextResponse.json({ error: 'Not a seller' }, { status: 404 });
    }

    // Get marketplace orders for this seller
    const { data: orders, error: ordersError } = await supabase
      .from('marketplace_orders')
      .select('*')
      .eq('seller_user_id', user.id)
      .order('created_at', { ascending: false });

    if (ordersError) {
      throw ordersError;
    }

    // Get seller payouts
    const { data: payouts, error: payoutsError } = await supabase
      .from('seller_payouts')
      .select('*')
      .eq('seller_user_id', user.id)
      .order('created_at', { ascending: false });

    if (payoutsError) {
      throw payoutsError;
    }

    // Calculate summary
    const totalGmv = (orders || []).reduce((sum, o) => sum + (o.gross_amount_cents || 0), 0);
    const totalFees = (orders || []).reduce((sum, o) => sum + (o.platform_fee_cents || 0), 0);
    const totalNet = totalGmv - totalFees;
    const totalPaidOut = (payouts || [])
      .filter((p) => p.status === 'paid')
      .reduce((sum, p) => sum + (p.amount_cents || 0), 0);

    return NextResponse.json({
      seller: {
        id: sellerProfile.id,
        displayName: sellerProfile.display_name,
        stripeAccountId: sellerProfile.stripe_account_id,
      },
      orders: orders || [],
      payouts: payouts || [],
      summary: {
        totalGmv,
        totalFees,
        totalNet,
        totalPaidOut,
        pendingPayout: totalNet - totalPaidOut,
        orderCount: orders?.length || 0,
        payoutCount: payouts?.length || 0,
      },
    });
  } catch (error) {
    console.error('[GET /api/finance/seller/summary]', error);
    return NextResponse.json({ error: 'Failed to fetch seller summary' }, { status: 500 });
  }
}

/**
 * POST /api/invoices/create
 * Create a new invoice with PDF generation
 */

import { createRouteHandlerClient } from '@supabase/auth-helpers-nextjs';
import { cookies } from 'next/headers';
import { NextRequest, NextResponse } from 'next/server';
import { v4 as uuidv4 } from 'uuid';
import {
  calculateTax,
  getBusinessProfile,
  recordTaxAccounting,
} from '@/lib/tax/georgia';
import { generateAndSaveInvoicePdf } from '@/lib/invoices/pdfGenerator';

interface CreateInvoiceRequest {
  buyerName: string;
  buyerTaxId?: string;
  buyerAddress?: string;
  buyerEmail?: string;
  currency: 'GEL' | 'USD';
  items: Array<{
    title: string;
    quantity: number;
    unitPriceCents: number;
    description?: string;
  }>;
  notes?: string;
  stripeObjectId?: string;
}

export async function POST(req: NextRequest) {
  try {
    const supabase = createRouteHandlerClient({ cookies });

    // Get current user
    const {
      data: { user },
      error: authError,
    } = await supabase.auth.getUser();

    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Parse request body
    const body: CreateInvoiceRequest = await req.json();

    const { buyerName, buyerTaxId, buyerAddress, buyerEmail, currency, items, notes, stripeObjectId } =
      body;

    // Validate required fields
    if (!buyerName || !currency || !items || items.length === 0) {
      return NextResponse.json(
        { error: 'Missing required fields: buyerName, currency, items' },
        { status: 400 },
      );
    }

    // Get user business profile
    const profile = await getBusinessProfile(supabase, user.id);

    if (!profile) {
      return NextResponse.json(
        {
          error: 'Business profile not found. Please complete your business profile first.',
        },
        { status: 400 },
      );
    }

    // Calculate subtotal
    let subtotalCents = 0;
    const invoiceItems: Array<{
      title: string;
      quantity: number;
      unitPriceCents: number;
      lineTotalCents: number;
      description?: string;
    }> = [];

    for (const item of items) {
      const lineTotalCents = Math.round(item.unitPriceCents * item.quantity);
      _subtotalCents += lineTotalCents;
      invoiceItems.push({
        title: item.title,
        quantity: item.quantity,
        unitPriceCents: item.unitPriceCents,
        lineTotalCents,
        description: item.description,
      });
    }

    // Calculate tax
    const taxCalc = calculateTax({
      isVatPayer: profile.isVatPayer,
      vatRate: profile.vatRate,
      lineItems: items,
      currency,
    });

    // Generate invoice number (atomically increment from profile)
    const { data: updatedProfile, error: updateError } = await supabase
      .from('business_profiles')
      .update({ next_invoice_number: profile.nextInvoiceNumber + 1 })
      .eq('user_id', user.id)
      .select('next_invoice_number')
      .single();

    if (updateError || !updatedProfile) {
      return NextResponse.json(
        { error: 'Failed to generate invoice number' },
        { status: 500 },
      );
    }

    const invoiceNumber = `${profile.invoicePrefix}-${String(profile.nextInvoiceNumber).padStart(6, '0')}`;

    // Create invoice record
    const invoiceId = uuidv4();
    const now = new Date();
    const dueDate = new Date(now);
    dueDate.setDate(dueDate.getDate() + 30); // 30 days payment terms

    const { error: invoiceError, data: invoiceData } = await supabase
      .from('invoices')
      .insert([
        {
          id: invoiceId,
          user_id: user.id,
          buyer_name: buyerName,
          buyer_tax_id: buyerTaxId || null,
          buyer_address: buyerAddress || null,
          buyer_email: buyerEmail || null,
          invoice_number: invoiceNumber,
          currency,
          fx_rate_gel_per_usd: profile.fxRateGelPerUsd,
          subtotal_cents: taxCalc.subtotalCents,
          vat_rate: taxCalc.vatRatePct,
          vat_cents: taxCalc.vatCents,
          total_cents: taxCalc.totalCents,
          status: 'issued',
          stripe_object_id: stripeObjectId || null,
          notes: notes || null,
          created_at: now.toISOString(),
        },
      ])
      .select()
      .single();

    if (invoiceError || !invoiceData) {
      return NextResponse.json(
        { error: 'Failed to create invoice' },
        { status: 500 },
      );
    }

    // Insert invoice items
    const itemsForDb = invoiceItems.map((item) => ({
      invoice_id: invoiceId,
      title: item.title,
      quantity: item.quantity,
      unit_price_cents: item.unitPriceCents,
      line_total_cents: item.lineTotalCents,
      description: item.description || null,
    }));

    const { error: itemsError } = await supabase.from('invoice_items').insert(itemsForDb);

    if (itemsError) {
      console.error('Failed to insert invoice items:', itemsError);
    }

    // Record tax accounting entry
    try {
      await recordTaxAccounting(supabase, user.id, {
        invoiceId,
        recordType: 'invoice',
        incomeGrossCents: taxCalc.incomeGrosseCents,
        vatCollectedCents: taxCalc.vatCollectedCents,
        vatRate: taxCalc.vatRatePct,
        netIncomeCents: taxCalc.netIncomeCents,
        currency,
        notes: `Invoice ${invoiceNumber}`,
      });
    } catch (taxError) {
      console.error('Failed to record tax accounting:', taxError);
    }

    // Generate and upload PDF
    let pdfUrl: string | null = null;
    try {
      pdfUrl = await generateAndSaveInvoicePdf(
        supabase,
        user.id,
        invoiceId,
        {
          invoiceNumber,
          issueDate: now,
          dueDate,
          currency,
          sellerName: profile.legalName || 'Avatar G',
          sellerTaxId: profile.taxId || '',
          sellerAddress: profile.address || '',
          sellerEmail: profile.email || '',
          sellerPhone: profile.phone,
          buyerName,
          buyerTaxId,
          buyerAddress,
          buyerEmail,
          items: invoiceItems,
          subtotalCents: taxCalc.subtotalCents,
          vatRate: taxCalc.vatRatePct,
          vatCents: taxCalc.vatCents,
          totalCents: taxCalc.totalCents,
          stripeObjectId,
          notes,
          fxRateGelPerUsd: profile.fxRateGelPerUsd,
        },
      );
    } catch (pdfError) {
      console.error('Failed to generate PDF:', pdfError);
    }

    // Return invoice details
    return NextResponse.json(
      {
        id: invoiceId,
        invoiceNumber,
        status: 'issued',
        totalCents: taxCalc.totalCents,
        currency,
        pdfUrl,
        createdAt: now.toISOString(),
      },
      { status: 201 },
    );
  } catch (error) {
    console.error('Error creating invoice:', error);
    return NextResponse.json(
      {
        error:
          error instanceof Error ? error.message : 'Failed to create invoice',
      },
      { status: 500 },
    );
  }
}

import { NextRequest, _NextResponse } from 'next/server';
import { createSupabaseServerClient } from '@/lib/supabase/server';
import { apiError, apiSuccess } from '@/lib/api/response';

export const dynamic = 'force-dynamic';
export const runtime = 'nodejs'; // Error logging requires nodejs for Supabase

interface ErrorLogBody {
  message: string;
  digest?: string;
  stack?: string;
  timestamp: string;
  url: string;
  userAgent: string;
}

/**
 * Error Logging Endpoint
 * POST /api/log-error
 * 
 * Logs client-side errors to Supabase for monitoring
 * Rate limited to prevent abuse
 */
export async function POST(request: NextRequest) {
  try {
    // Rate limit: max 10 error logs per minute per IP
    const ip = request.headers.get('x-forwarded-for') || 'unknown';
    const now = new Date();
    
    // Parse request body
    const body: ErrorLogBody = await request.json();

    // Validate required fields
    if (!body.message || !body.url) {
      return apiError('Missing required fields: message, url', 400);
    }

    // Only log in production
    if (process.env.NODE_ENV !== 'production') {
      console.log('[Client Error Logged]', body.message, 'at', body.url);
      return apiSuccess({ logged: false, reason: 'development' });
    }

    try {
      const supabase = createSupabaseServerClient();

      // Insert error log
      const { error: insertError } = await supabase
        .from('error_logs')
        .insert({
          error_type: 'client_error',
          error_message: body.message.substring(0, 500),
          error_digest: body.digest,
          error_stack: body.stack?.substring(0, 2000),
          page_url: body.url,
          user_agent: body.userAgent?.substring(0, 500),
          client_ip: ip,
          logged_at: now.toISOString(),
          environment: process.env.VERCEL_ENV || 'unknown',
        });

      if (insertError && insertError.code !== 'PGRST116') {
        // PGRST116 = table doesn't exist (OK during setup)
        console.error('[Error Log Insert Failed]', insertError);
        return apiSuccess({ logged: false, reason: 'database_unavailable' });
      }

      return apiSuccess({ logged: true });
    } catch (dbError) {
      // Silently fail - don't break the client
      console.error('[Error Logging Failed]', dbError);
      return apiSuccess({ logged: false, reason: 'server_error' });
    }
  } catch (error) {
    console.error('[Error Log Route Error]', error);
    // Always return 200 - error logging should never block the app
    return apiSuccess({ logged: false, reason: 'parse_error' });
  }
}

/**
 * Market Scan API Route
 * POST /api/market/scan
 *
 * Scans product niches and returns ranked list of opportunities
 * Each product must pass decision engine before returning
 */

import { NextRequest, NextResponse } from 'next/server';
import { z } from 'zod';
import { createSupabaseServerClient } from '@/lib/supabase/server';
import { evaluateProductCandidate } from '@/lib/decision-engine/decisionEngine';
import { _MarketScanRequest, ScannedProduct } from '@/lib/pricing/types';

const MarketScanRequestSchema = z.object({
  niche: z.string().min(2).max(100),
  country: z.string().length(2).default('GE'),
  priceRangeCents: z.tuple([z.number().int().positive(), z.number().int().positive()]),
  competitorUrl: z.string().url().optional(),
});

export const dynamic = 'force-dynamic';

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const parsed = MarketScanRequestSchema.safeParse(body);

    if (!parsed.success) {
      return NextResponse.json(
        { error: 'Invalid input', details: parsed.error.flatten() },
        { status: 400 }
      );
    }

    const { niche, country, priceRangeCents, competitorUrl } = parsed.data;

    // Authenticate user
    const supabase = await createSupabaseServerClient();
    const { data: { user }, error: _authError } = await supabase.auth.getUser();

    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Mock market scan (in production, would call ML model or API)
    const scannedProducts = await scanMarketNiche(
      niche,
      country,
      priceRangeCents,
      competitorUrl
    );

    // Filter through decision engine: only return approved products
    const evaluatedProducts: ScannedProduct[] = [];

    for (const product of scannedProducts) {
      const decision = evaluateProductCandidate(
        {
          productType: 'standard',
          retailPriceCents: product.currentPriceCents,
          supplierCostCents: product.estimatedCostCents,
          shippingCostCents: 300, // Assume ₾3 average
          vatEnabled: true,
          platformFeeBps: 500,
        },
        { standard: 1500, dropshipping: 2500, digital: 7000 }
      );

      const evaluated: ScannedProduct = {
        ...product,
        profitMarginBps: decision.computed.marginBps,
        decisionEngineApproval: decision.decision === 'publish' ? 'approved' : 'rejected',
        rejectionReason: decision.decision === 'reject' ? decision.reasons[0] : undefined,
        recommendedPriceCents:
          decision.decision === 'reject' ? decision.recommendedPriceCents : undefined,
      };

      evaluatedProducts.push(evaluated);
    }

    // Rank by profitability and demand
    evaluatedProducts.sort((a, b) => {
      // Approved products first
      if (a.decisionEngineApproval !== b.decisionEngineApproval) {
        return a.decisionEngineApproval === 'approved' ? -1 : 1;
      }

      // Then by profit margin
      return b.profitMarginBps - a.profitMarginBps;
    });

    return NextResponse.json({
      data: {
        niche,
        country,
        scanDate: new Date().toISOString(),
        productsScanned: scannedProducts.length,
        productsApproved: evaluatedProducts.filter(
          (p) => p.decisionEngineApproval === 'approved'
        ).length,
        products: evaluatedProducts.slice(0, 20), // Top 20
      },
    });
  } catch (error) {
    console.error('Market scan error:', error);
    return NextResponse.json(
      { error: 'Market scan failed' },
      { status: 500 }
    );
  }
}

