/**
 * POST /api/tools/margin-calculator
 * Calculate product margin with various scenarios
 */

import { NextRequest, NextResponse } from 'next/server';
import { calculateMargin, analyzePrice, _convertCurrency } from '@/lib/finance/margins';

interface MarginCalculatorRequest {
  costCents: number;
  shippingCents?: number;
  paymentFeePct?: number;
  platformFeePct?: number;
  affiliateFeePct?: number;
  isVatPayer: boolean;
  vatRate?: number;
  desiredProfitPct?: number;
  desiredProfitCents?: number;
  analysisMode?: 'calculate' | 'analyze';
  sellingPriceCents?: number; // For analyze mode
  currency?: 'GEL' | 'USD';
  fxRateGelPerUsd?: number;
}

export async function POST(req: NextRequest) {
  try {
    const body: MarginCalculatorRequest = await req.json();

    const {
      costCents,
      shippingCents = 0,
      paymentFeePct = 2.9,
      platformFeePct = 30,
      affiliateFeePct = 0,
      isVatPayer,
      vatRate = 18.0,
      desiredProfitPct,
      desiredProfitCents,
      analysisMode = 'calculate',
      sellingPriceCents,
      currency = 'GEL',
      fxRateGelPerUsd = 2.7,
    } = body;

    // Validate required fields
    if (typeof costCents !== 'number' || costCents < 0) {
      return NextResponse.json(
        { error: 'Invalid costCents' },
        { status: 400 },
      );
    }

    if (typeof isVatPayer !== 'boolean') {
      return NextResponse.json(
        { error: 'Invalid isVatPayer' },
        { status: 400 },
      );
    }

    let result;

    if (analysisMode === 'analyze' && sellingPriceCents !== undefined) {
      // Analyze existing price
      result = analyzePrice({
        sellingPriceCents,
        costCents,
        shippingCents,
        paymentFeePct,
        platformFeePct,
        affiliateFeePct,
        isVatPayer,
        vatRate,
      });
    } else {
      // Calculate recommended price
      result = calculateMargin({
        costCents,
        shippingCents,
        paymentFeePct,
        platformFeePct,
        affiliateFeePct,
        isVatPayer,
        vatRate,
        desiredProfitPct,
        desiredProfitCents,
      });
    }

    // Add currency conversions if applicable
    const response: unknown = {
      ...result,
      currency,
    };

    // If GEL, also show USD equivalent
    if (currency === 'GEL' && fxRateGelPerUsd > 0) {
      response.recommendedPriceUsd = Math.ceil(result.recommendedPriceCents / fxRateGelPerUsd);
      response.fxRate = fxRateGelPerUsd;

      // Convert breakdown to USD
      response.breakdownUsd = {
        revenue: Math.ceil(result.breakdown.revenue / fxRateGelPerUsd),
        vat: Math.ceil(result.breakdown.vat / fxRateGelPerUsd),
        cost: Math.ceil(result.breakdown.cost / fxRateGelPerUsd),
        shipping: Math.ceil(result.breakdown.shipping / fxRateGelPerUsd),
        paymentFee: Math.ceil(result.breakdown.paymentFee / fxRateGelPerUsd),
        platformFee: Math.ceil(result.breakdown.platformFee / fxRateGelPerUsd),
        affiliateFee: Math.ceil(result.breakdown.affiliateFee / fxRateGelPerUsd),
        netProfit: Math.ceil(result.breakdown.netProfit / fxRateGelPerUsd),
      };
    }

    return NextResponse.json(response, { status: 200 });
  } catch (error) {
    console.error('Error calculating margin:', error);
    return NextResponse.json(
      {
        error:
          error instanceof Error ? error.message : 'Failed to calculate margin',
      },
      { status: 500 },
    );
  }
}

// GET /api/tracking/[token]
// Public endpoint for tracking shipment status (no auth required)
// Token is random and unguessable, valid for 7 days

import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';
import { ShippingService } from '@/lib/shipping/ShippingService';

export async function GET(
  request: NextRequest,
  { params }: { params: { token: string } }
) {
  try {
    const supabase = createClient(
      process.env.NEXT_PUBLIC_SUPABASE_URL!,
      process.env.SUPABASE_SERVICE_ROLE_KEY!
    );

    const shippingService = new ShippingService(supabase);
    const shipment = await shippingService.getShipmentForTracking(params.token);

    if (!shipment) {
      return NextResponse.json(
        { error: 'Tracking information not found or link expired' },
        { status: 404 }
      );
    }

    // Calculate ETA from initial shipment date
    const _createdAt = new Date(shipment.created_at);
    const eta = {
      minDays: 2, // Default for manual
      maxDays: 7,
    };

    // Safe response: no personal information
    return NextResponse.json(
      {
        status: shipment.status,
        carrier: shipment.carrier,
        events: (shipment.shipment_events || []).map((event: unknown) => ({
          status: event.status,
          location: event.location,
          message: event.message,
          occurredAt: event.occurred_at,
        })),
        currentLocation: shipment.shipment_events?.[0]?.location || null,
        trackingNumberMasked: shipment.tracking_number
          ? `...${shipment.tracking_number.slice(-4)}`
          : null,
        eta,
        shippedAt: shipment.shipped_at,
        deliveredAt: shipment.delivered_at,
        trackingUrl: shipment.tracking_url || null,
      },
      {
        status: 200,
        headers: {
          'Cache-Control': 'public, max-age=60', // Cache for 1 minute
        },
      }
    );
  } catch (error) {
    console.error('Error fetching tracking info:', error);
    return NextResponse.json(
      { error: 'Failed to retrieve tracking information' },
      { status: 500 }
    );
  }
}

