          </Button>
        </div>

      </div>
    </div>
  );
}

/**
 * Marketplace Growth KPIs Dashboard - Client Component
 */
'use client';

import { useEffect, useState } from 'react';

interface KPI {
  id: string;
  date: string;
  impressions: number;
  clicks: number;
  conversions: number;
  revenue_cents: number;
}

interface Aggregates {
  totalImpressions: number;
  totalClicks: number;
  totalConversions: number;
  totalRevenueCents: number;
}

export default function GrowthKPIsClient({ storeId }: { storeId: string }) {
  const [kpis, setKpis] = useState<KPI[]>([]);
  const [aggregates, setAggregates] = useState<Aggregates | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchKpis();
  }, [storeId]);

  const fetchKpis = async () => {
    try {
      const response = await fetch(`/api/marketplace/kpis?storeId=${storeId}`);
      if (!response.ok) throw new Error('Failed to fetch KPIs');
      const data = await response.json();
      setKpis(data.data.kpis);
      setAggregates(data.data.aggregates);
    } catch (err: unknown) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const formatCents = (cents: number) => {
    return `₾${(cents / 100).toFixed(2)}`;
  };

  const ctr = aggregates && aggregates.totalImpressions > 0
    ? ((aggregates.totalClicks / aggregates.totalImpressions) * 100).toFixed(2)
    : 0;

  const conversionRate = aggregates && aggregates.totalClicks > 0
    ? ((aggregates.totalConversions / aggregates.totalClicks) * 100).toFixed(2)
    : 0;

  return (
    <div className="space-y-8">
      {loading ? (
        <p className="text-gray-400">Loading KPIs...</p>
      ) : error ? (
        <div className="bg-red-900 border border-red-700 rounded p-3 text-red-100">{error}</div>
      ) : !aggregates ? (
        <p className="text-gray-400">No KPI data yet</p>
      ) : (
        <>
          {/* KPI Cards */}
          <div className="grid grid-cols-2 gap-4">
            <div className="bg-gray-800 rounded-lg p-6">
              <div className="text-sm text-gray-400 mb-1">Total Impressions</div>
              <div className="text-3xl font-bold">{aggregates.totalImpressions.toLocaleString()}</div>
            </div>
            <div className="bg-gray-800 rounded-lg p-6">
              <div className="text-sm text-gray-400 mb-1">Total Clicks</div>
              <div className="text-3xl font-bold">{aggregates.totalClicks.toLocaleString()}</div>
            </div>
            <div className="bg-gray-800 rounded-lg p-6">
              <div className="text-sm text-gray-400 mb-1">Total Conversions</div>
              <div className="text-3xl font-bold">{aggregates.totalConversions.toLocaleString()}</div>
            </div>
            <div className="bg-gray-800 rounded-lg p-6">
              <div className="text-sm text-gray-400 mb-1">Total Revenue</div>
              <div className="text-3xl font-bold">{formatCents(aggregates.totalRevenueCents)}</div>
            </div>
            <div className="bg-gray-800 rounded-lg p-6">
              <div className="text-sm text-gray-400 mb-1">CTR (Click-Through Rate)</div>
              <div className="text-3xl font-bold">{ctr}%</div>
            </div>
            <div className="bg-gray-800 rounded-lg p-6">
              <div className="text-sm text-gray-400 mb-1">Conversion Rate</div>
              <div className="text-3xl font-bold">{conversionRate}%</div>
            </div>
          </div>

          {/* Daily KPI History */}
          <div className="bg-gray-800 rounded-lg p-6">
            <h3 className="text-xl font-bold mb-4">Daily Performance (Last 30 Days)</h3>
            {kpis.length === 0 ? (
              <p className="text-gray-400">No daily data yet</p>
            ) : (
              <div className="overflow-x-auto">
                <table className="w-full text-sm">
                  <thead className="border-b border-gray-700">
                    <tr>
                      <th className="text-left py-2 px-2">Date</th>
                      <th className="text-right py-2 px-2">Impressions</th>
                      <th className="text-right py-2 px-2">Clicks</th>
                      <th className="text-right py-2 px-2">Conversions</th>
                      <th className="text-right py-2 px-2">Revenue</th>
                    </tr>
                  </thead>
                  <tbody>
                    {kpis.map((kpi) => (
                      <tr key={kpi.id} className="border-b border-gray-700 hover:bg-gray-700">
                        <td className="py-2 px-2">{new Date(kpi.date).toLocaleDateString()}</td>
                        <td className="text-right py-2 px-2">{kpi.impressions.toLocaleString()}</td>
                        <td className="text-right py-2 px-2">{kpi.clicks.toLocaleString()}</td>
                        <td className="text-right py-2 px-2">{kpi.conversions.toLocaleString()}</td>
                        <td className="text-right py-2 px-2">{formatCents(kpi.revenue_cents)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { toCents, fromCents } from '@/lib/finance/money';

export default function PayoutsClient({ _storeId }: { storeId: string }) {
  const [accounts, setAccounts] = useState<unknown[]>([]);
  const [requests, setRequests] = useState<unknown[]>([]);
  const [amount, setAmount] = useState(100);
  const [currency, setCurrency] = useState<'GEL' | 'USD'>('GEL');
  const [accountType, setAccountType] = useState<'stripe' | 'tbc' | 'bog' | 'payze'>('stripe');
  const [details, setDetails] = useState('{}');
  const [error, setError] = useState<string | null>(null);

  async function loadData() {
    const accountsRes = await fetch('/api/payouts/accounts');
    const accountsData = await accountsRes.json();
    setAccounts(accountsData.data || []);

    const requestsRes = await fetch('/api/payouts/requests');
    const requestsData = await requestsRes.json();
    setRequests(requestsData.data || []);
  }

  useEffect(() => {
    loadData();
  }, []);

  async function createAccount() {
    setError(null);
    try {
      const response = await fetch('/api/payouts/accounts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          type: accountType,
          details: JSON.parse(details || '{}'),
        }),
      });
      const data = await response.json();
      if (!response.ok) throw new Error(data.error || 'Failed to create account');
      setAccounts([data.data, ...accounts]);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to create account');
    }
  }

  async function requestPayout() {
    setError(null);
    try {
      const response = await fetch('/api/payouts/requests', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          amount_cents: toCents(amount),
          currency,
        }),
      });
      const data = await response.json();
      if (!response.ok) throw new Error(data.error || 'Failed to request payout');
      setRequests([data.data, ...requests]);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to request payout');
    }
  }

  return (
    <div className="space-y-8">
      <div>
        <h1 className="text-3xl font-bold">Payouts</h1>
        <p className="text-gray-400">Request withdrawals and manage payout accounts.</p>
      </div>

      {error && (
        <div className="bg-red-900/40 border border-red-500 text-red-200 px-4 py-3 rounded">
          {error}
        </div>
      )}

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="bg-white/5 border border-white/10 rounded-xl p-6">
          <h2 className="text-xl font-semibold mb-4">Request Payout</h2>
          <div className="grid grid-cols-1 gap-4">
            <div>
              <label className="text-sm text-gray-300">Amount</label>
              <input
                type="number"
                value={amount}
                onChange={(e) => setAmount(Number(e.target.value))}
                className="w-full mt-1 bg-black/40 border border-white/10 rounded px-3 py-2"
              />
            </div>
            <div>
              <label className="text-sm text-gray-300">Currency</label>
              <select
                value={currency}
                onChange={(e) => setCurrency(e.target.value as 'GEL' | 'USD')}
                className="w-full mt-1 bg-black/40 border border-white/10 rounded px-3 py-2"
              >
                <option value="GEL">GEL</option>
                <option value="USD">USD</option>
              </select>
            </div>
            <button
              onClick={requestPayout}
              className="px-4 py-2 bg-cyan-500 hover:bg-cyan-400 text-black font-semibold rounded"
            >
              Request Payout
            </button>
          </div>
        </div>

        <div className="bg-white/5 border border-white/10 rounded-xl p-6">
          <h2 className="text-xl font-semibold mb-4">Add Payout Account</h2>
          <div className="grid grid-cols-1 gap-4">
            <div>
              <label className="text-sm text-gray-300">Type</label>
              <select
                value={accountType}
                onChange={(e) => setAccountType(e.target.value as unknown)}
                className="w-full mt-1 bg-black/40 border border-white/10 rounded px-3 py-2"
              >
                <option value="stripe">Stripe</option>
                <option value="tbc">TBC</option>
                <option value="bog">BoG</option>
                <option value="payze">Payze</option>
              </select>
            </div>
            <div>
              <label className="text-sm text-gray-300">Details JSON</label>
              <textarea
                value={details}
                onChange={(e) => setDetails(e.target.value)}
                className="w-full h-24 mt-1 bg-black/40 border border-white/10 rounded px-3 py-2 text-sm"
              />
            </div>
            <button
              onClick={createAccount}
              className="px-4 py-2 bg-white/10 hover:bg-white/20 rounded"
            >
              Add Account
            </button>
          </div>
        </div>
      </div>

      <div className="bg-white/5 border border-white/10 rounded-xl p-6">
        <h2 className="text-xl font-semibold mb-4">Payout History</h2>
        {requests.length === 0 ? (
          <p className="text-gray-400">No payout requests yet.</p>
        ) : (
          <div className="space-y-3">
            {requests.map((request) => (
              <div key={request.id} className="bg-black/30 rounded p-3">
                <div className="flex items-center justify-between">
                  <p className="font-semibold">{fromCents(request.amount_cents).toFixed(2)} {request.currency}</p>
                  <span className="text-xs bg-white/10 px-2 py-1 rounded">{request.status}</span>
                </div>
                <p className="text-sm text-gray-400">Requested: {new Date(request.requested_at).toLocaleDateString()}</p>
                {request.review_required && (
                  <p className="text-xs text-yellow-300">Review required</p>
                )}
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="bg-white/5 border border-white/10 rounded-xl p-6">
        <h2 className="text-xl font-semibold mb-4">Linked Accounts</h2>
        {accounts.length === 0 ? (
          <p className="text-gray-400">No payout accounts yet.</p>
        ) : (
          <div className="space-y-3">
            {accounts.map((account) => (
              <div key={account.id} className="bg-black/30 rounded p-3">
                <div className="flex items-center justify-between">
                  <p className="font-semibold">{account.type.toUpperCase()}</p>
                  <span className="text-xs bg-white/10 px-2 py-1 rounded">{account.status}</span>
                </div>
                <p className="text-xs text-gray-400">Created: {new Date(account.created_at).toLocaleDateString()}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { useRouter } from 'next/navigation';
import Link from 'next/link';

export default function PayPage() {
  const _router = useRouter();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string>('');
  const [customAmount, setCustomAmount] = useState<string>('100');

  const handlePay = async (amountUsd: number = 100) => {
    setLoading(true);
    setError('');

    try {
      const amountCents = Math.round(amountUsd * 100);

      const response = await fetch('/api/stripe/create-checkout-session', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          amount: amountCents,
          currency: 'usd',
          description: `Avatar G - Test Payment $${amountUsd}`,
        }),
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.error || 'Failed to create checkout session');
      }

      const { url } = await response.json();
      if (url) {
        // Redirect to Stripe Checkout
        window.location.href = url;
      } else {
        throw new Error('No checkout URL returned');
      }
    } catch (err) {
      const message = err instanceof Error ? err.message : 'Unknown error';
      setError(message);
      console.error('[Pay] Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleCustomPay = () => {
    const amount = parseFloat(customAmount);
    if (isNaN(amount) || amount <= 0) {
      setError('Please enter a valid amount greater than 0');
      return;
    }
    handlePay(amount);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 to-slate-800 flex items-center justify-center p-4">
      <div className="max-w-md w-full bg-white rounded-lg shadow-2xl p-8">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-slate-900 mb-2">💳 Test Payment</h1>
          <p className="text-sm text-slate-500">
            Use this page to test Stripe payments in sandbox mode
          </p>
        </div>

        {/* Error Display */}
        {error && (
          <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg">
            <p className="text-red-700 text-sm font-medium">❌ Error: {error}</p>
          </div>
        )}

        {/* Quick Payment Buttons */}
        <div className="space-y-3 mb-8">
          <p className="text-sm font-semibold text-slate-700 mb-3">Quick Payment Options:</p>
          
          <button
            onClick={() => handlePay(10)}
            disabled={loading}
            className="w-full bg-blue-600 hover:bg-blue-700 disabled:bg-slate-400 text-white font-semibold py-3 px-4 rounded-lg transition disabled:cursor-not-allowed"
          >
            {loading ? '⏳ Processing...' : '💰 Pay $10'}
          </button>

          <button
            onClick={() => handlePay(50)}
            disabled={loading}
            className="w-full bg-blue-600 hover:bg-blue-700 disabled:bg-slate-400 text-white font-semibold py-3 px-4 rounded-lg transition disabled:cursor-not-allowed"
          >
            {loading ? '⏳ Processing...' : '💰 Pay $50'}
          </button>

          <button
            onClick={() => handlePay(100)}
            disabled={loading}
            className="w-full bg-emerald-600 hover:bg-emerald-700 disabled:bg-slate-400 text-white font-semibold py-3 px-4 rounded-lg transition disabled:cursor-not-allowed border-2 border-emerald-600"
          >
            {loading ? '⏳ Processing...' : '✨ Pay $100 (Test)'}
          </button>
        </div>

        {/* Custom Amount */}
        <div className="mb-6 p-4 bg-slate-50 rounded-lg">
          <label className="block text-sm font-semibold text-slate-700 mb-2">
            Custom Amount (USD):
          </label>
          <div className="flex gap-2">
            <input
              type="number"
              min="0.01"
              step="0.01"
              value={customAmount}
              onChange={(e) => setCustomAmount(e.target.value)}
              disabled={loading}
              placeholder="100.00"
              className="flex-1 px-3 py-2 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:bg-slate-200"
            />
            <button
              onClick={handleCustomPay}
              disabled={loading}
              className="bg-purple-600 hover:bg-purple-700 disabled:bg-slate-400 text-white font-semibold px-4 py-2 rounded-lg transition disabled:cursor-not-allowed"
            >
              {loading ? '⏳' : 'Pay'}
            </button>
          </div>
        </div>

        {/* Test Card Info */}
        <div className="p-4 bg-amber-50 border border-amber-200 rounded-lg mb-6">
          <p className="text-xs font-semibold text-amber-900 mb-2">🧪 Use Stripe Test Card:</p>
          <p className="text-xs text-amber-800 font-mono">Card: 4242 4242 4242 4242</p>
          <p className="text-xs text-amber-800 font-mono">Expires: Any future date</p>
          <p className="text-xs text-amber-800 font-mono">CVC: Any 3-digit number</p>
        </div>

        {/* Info Box */}
        <div className="p-4 bg-blue-50 border border-blue-200 rounded-lg mb-6">
          <p className="text-xs text-blue-900">
            <strong>ℹ️ Sandbox Mode:</strong> This is a test payment in Stripe&apos;s sandbox environment. 
            No real charges will be made.
          </p>
        </div>

        {/* Navigation */}
        <div className="flex gap-2">
          <Link
            href="/"
            className="flex-1 text-center bg-slate-200 hover:bg-slate-300 text-slate-900 font-semibold py-2 px-4 rounded-lg transition"
          >
            ← Back Home
          </Link>
          <Link
            href="/pay/success"
            className="flex-1 text-center bg-slate-200 hover:bg-slate-300 text-slate-900 font-semibold py-2 px-4 rounded-lg transition text-xs"
          >
            View Success ✓
          </Link>
        </div>
      </div>
    </div>
  );
}

'use client';

import React, { useEffect, useState } from 'react';
import { useTranslations } from 'next-intl';
import { DollarSign, ShoppingCart, TrendingUp, AlertCircle } from 'lucide-react';
import { motion } from 'framer-motion';

interface SellerFinanceSummary {
  seller: {
    id: string;
    displayName: string;
    stripeAccountId: string;
  };
  orders: unknown[];
  payouts: unknown[];
  summary: {
    totalGmv: number;
    totalFees: number;
    totalNet: number;
    totalPaidOut: number;
    pendingPayout: number;
    orderCount: number;
    payoutCount: number;
  };
}

export default function SellerFinanceDashboard() {
  const _t = useTranslations();
  const [data, setData] = useState<SellerFinanceSummary | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchSellerFinance();
  }, []);

  const fetchSellerFinance = async () => {
    try {
      setLoading(true);
      setError(null);

      const res = await fetch('/api/finance/seller/summary', {
        method: 'GET',
      });

      if (!res.ok) {
        if (res.status === 404) {
          setError('You are not registered as a seller');
        } else {
          throw new Error('Failed to fetch seller finance data');
        }
        setLoading(false);
        return;
      }

      const data = await res.json();
      setData(data);
    } catch (err) {
      console.error('Error fetching seller finance:', err);
      setError(err instanceof Error ? err.message : 'Failed to load seller finance data');
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (cents: number) => {
    return `$${(cents / 100).toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;
  };

  const SummaryCard = ({ label, value, icon: Icon, color = 'blue' }: unknown) => (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className={`bg-white rounded-lg shadow-md p-6 border-l-4 border-${color}-500`}
    >
      <div className="flex items-center justify-between">
        <div>
          <p className="text-gray-600 text-sm mb-1">{label}</p>
          <p className="text-2xl font-bold text-gray-900">{value}</p>
        </div>
        <Icon className={`w-8 h-8 text-${color}-500`} />
      </div>
    </motion.div>
  );

  if (loading) {
    return (
      <div className="p-8 max-w-7xl mx-auto">
        <div className="text-center">
          <div className="animate-spin inline-block w-8 h-8 border-4 border-gray-300 border-t-blue-500 rounded-full"></div>
          <p className="mt-4 text-gray-600">Loading seller dashboard...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8 max-w-7xl mx-auto">
        <div className="bg-red-50 border border-red-200 rounded-lg p-4 flex items-start">
          <AlertCircle className="w-5 h-5 text-red-600 mt-0.5 mr-3 flex-shrink-0" />
          <div>
            <h3 className="font-semibold text-red-900">Error</h3>
            <p className="text-red-700">{error}</p>
          </div>
        </div>
      </div>
    );
  }

  if (!data) {
    return (
      <div className="p-8 max-w-7xl mx-auto">
        <p className="text-gray-600">No data available</p>
      </div>
    );
  }

  // Group orders by date for chart
  const ordersByDate = data.orders.reduce((acc: unknown, order: unknown) => {
    const date = new Date(order.created_at).toISOString().slice(0, 10);
    if (!acc[date]) {
      acc[date] = { date, revenue: 0, fees: 0, net: 0 };
    }
    acc[date].revenue += order.gross_amount_cents || 0;
    acc[date].fees += order.platform_fee_cents || 0;
    acc[date].net += (order.gross_amount_cents || 0) - (order.platform_fee_cents || 0);
    return acc;
  }, {});

  const orderChartData = Object.values(ordersByDate).slice(-30);

  // Group payouts by date
  const payoutsByDate = data.payouts.reduce((acc: unknown, payout: unknown) => {
    const date = new Date(payout.created_at).toISOString().slice(0, 10);
    if (!acc[date]) {
      acc[date] = { date, payouts: 0 };
    }
    if (payout.status === 'paid') {
      acc[date].payouts += payout.amount_cents || 0;
    }
    return acc;
  }, {});

  const _payoutChartData = Object.values(payoutsByDate).slice(-30);

  return (
    <div className="bg-gray-50 min-h-screen p-8">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Seller Dashboard</h1>
          <p className="text-gray-600 mt-1">
            {data.seller.displayName || 'Your Store'} • {data.seller.stripeAccountId}
          </p>
        </div>

        {/* KPI Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <SummaryCard
            label="Total GMV"
            value={formatCurrency(data.summary.totalGmv)}
            icon={ShoppingCart}
            color="blue"
          />
          <SummaryCard
            label="Total Fees"
            value={formatCurrency(data.summary.totalFees)}
            icon={DollarSign}
            color="red"
          />
          <SummaryCard
            label="Net Amount"
            value={formatCurrency(data.summary.totalNet)}
            icon={TrendingUp}
            color="green"
          />
          <SummaryCard
            label="Pending Payout"
            value={formatCurrency(data.summary.pendingPayout)}
            icon={AlertCircle}
            color="amber"
          />
        </div>

        {/* Summary Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Financial Summary</h3>
