/**
 * Avatar G Commerce - Server Utilities & Business Logic
 * All computations performed server-side only
 */

'use server';

import { createClient } from '@supabase/ssr';
import { cookies } from 'next/headers';
import { getServerEnv } from '@/lib/env/server';
import {
  ComputeOrderSplit,
  ComputeProductMargin,
  _Order,
  CreateOrder,
} from './validation';

/**
 * Initialize Supabase client for server operations
 */
function getSupabaseClient() {
  const cookieStore = cookies();
  const supabase = createClient(
    getServerEnv().NEXT_PUBLIC_SUPABASE_URL,
    getServerEnv().NEXT_PUBLIC_SUPABASE_ANON_KEY,
    {
      cookies: {
        getAll() {
          return cookieStore.getAll();
        },
        setAll(cookiesToSet) {
          try {
            cookieStore.set(cookiesToSet);
          } catch {}
        },
      },
    }
  );
  
  return supabase;
}

