/**
 * Decision Engine - Product profitability evaluation and pricing recommendations
 * Server-side only
 */

import { computeMargin } from '@/lib/finance/margin';
import { computeVatInclusive, GEORGIA_VAT_BPS } from '@/lib/finance/vat';
import { percentageOf, safeRound } from '@/lib/finance/money';
import type { ProductCandidate, DecisionResult, Decision, MarginThresholds, _ProductType } from './types';

const DEFAULT_THRESHOLDS: MarginThresholds = {
  standard: 1500, // 15%
  dropshipping: 2500, // 25%
  digital: 7000, // 70%
};

/**
 * Evaluate product candidate for publishability
 * Returns decision + reasons + recommended price if margin too low
 */
export function evaluateProductCandidate(
  input: ProductCandidate,
  thresholds: Partial<MarginThresholds> = {}
): DecisionResult {
  const marginThresholds = { ...DEFAULT_THRESHOLDS, ...thresholds };
  const reasons: string[] = [];
  const warnings: string[] = [];

  // Compute margin using finance lib
  const marginResult = computeMargin({
    retail_price_cents: input.retailPriceCents,
    supplier_cost_cents: input.supplierCostCents,
    shipping_cost_cents: input.shippingCostCents,
    vat_enabled: input.vatEnabled,
    vat_rate_bps: input.vatRateBps || GEORGIA_VAT_BPS,
    platform_fee_bps: input.platformFeeBps || 500,
    affiliate_bps: input.affiliateBps || 0,
    refund_reserve_bps: input.refundReserveBps || 200,
  });

  // Rule 1: Reject if net <= 0
  if (marginResult.net_profit_cents <= 0) {
    reasons.push('Net profit per order is zero or negative');
  }

  // Compute margin in basis points for comparison
  const marginBps = input.retailPriceCents > 0
    ? safeRound((marginResult.net_profit_cents / input.retailPriceCents) * 10000)
    : 0;

  // Rule 2: Check against threshold per product type
  const threshold = marginThresholds[input.productType];
  if (marginBps < threshold) {
    reasons.push(
      `Margin ${(marginBps / 100).toFixed(2)}% below ${input.productType} minimum ${(threshold / 100).toFixed(2)}%`
    );
  }

  // Warnings: Shipping time
  if (input.shippingDaysMax && input.shippingDaysMax > 21) {
    warnings.push(
      `Shipping takes ${input.shippingDaysMax} days (typical buyers expect <= 21 days)`
    );
  }

  // Warnings: Low refund reserve
  if ((input.refundReserveBps || 200) < 200) {
    warnings.push('Refund reserve below 200 bps (2%) - insufficient safety margin');
  }

  // Decide
  const decision: Decision = reasons.length > 0 ? 'reject' : 'publish';

  // Compute recommended price if rejected due to margin
  const marginReasonExists = reasons.some((r) => r.includes('Margin'));
  let recommendedPriceCents: number | undefined;
  if (marginReasonExists && decision === 'reject') {
    recommendedPriceCents = computeRecommendedPrice(
      input,
      threshold,
      marginThresholds
    );
  }

  return {
    decision,
    reasons,
    warnings,
    computed: {
      netPerOrderCents: marginResult.net_profit_cents,
      marginBps,
      marginPercent: marginResult.margin_percent,
      vatAmountCents: marginResult.vat_amount_cents,
      totalCostsCents:
        input.supplierCostCents +
        input.shippingCostCents +
        marginResult.platform_fee_cents +
        marginResult.affiliate_fee_cents +
        marginResult.refund_reserve_cents,
    },
    recommendedPriceCents,
  };
}

/**
 * Compute recommended retail price to achieve target margin
 * Working backwards from: netProfit = price - vat - costs - fees
 * And: marginBps = netProfit / price * 10000
 * So: price = netProfit / (marginBps / 10000)
 */
function computeRecommendedPrice(
  input: ProductCandidate,
  targetMarginBps: number,
  _thresholds: MarginThresholds
): number {
  // Estimate total non-VAT costs
  const totalCosts =
    input.supplierCostCents +
    input.shippingCostCents +
    percentageOf(input.retailPriceCents, input.platformFeeBps || 500) +
    percentageOf(input.retailPriceCents, input.affiliateBps || 0) +
    percentageOf(input.retailPriceCents, input.refundReserveBps || 200);

  // Estimate VAT if enabled
  const estimatedVat = input.vatEnabled
    ? computeVatInclusive(input.retailPriceCents, input.vatRateBps || GEORGIA_VAT_BPS)
        .vat_amount_cents
    : 0;

  // Target net per order: price * (targetMarginBps / 10000)
  // Rearranged: price = (costs + vat + netProfit) where netProfit = price * (targetMarginBps / 10000)
  // This becomes: price * (1 - targetMarginBps/10000) = costs + vat
  // So: price = (costs + vat) / (1 - targetMarginBps / 10000)

  const marginRatio = 1 - targetMarginBps / 10000;
  if (marginRatio <= 0) {
    // Target margin is 100% or higher, which is impossible
    return input.retailPriceCents * 1.5; // Fallback: 50% increase
  }

  const recommendedPrice = safeRound((totalCosts + estimatedVat) / marginRatio);
  return Math.max(recommendedPrice, input.retailPriceCents);
}

// Avatar G Dispute Service
// Handles chargebacks and disputes from Stripe

import { SupabaseClient } from '@supabase/supabase-js';
import _Stripe from 'stripe';

export interface DisputeData {
  stripeDisputeId: string;
  stripeChargeId: string;
  amountCents: number;
  currency: string;
  status: string; // warning_needs_response|warning_under_review|needs_response|under_review|won|lost
  reason: string;
  reasonDescription?: string;
}

export class DisputeService {
  private supabase: SupabaseClient;

  constructor(supabase: SupabaseClient) {
    this.supabase = supabase;
  }

  /**
   * Handle Stripe dispute webhook (charge.dispute.created/updated/closed)
   * Step 1: Check if dispute already exists (idempotent via stripe_dispute_id)
   * Step 2: Link dispute to order via stripe_charge_id
   * Step 3: Apply payout hold
   * Step 4: Mark affiliate commissions as pending
   * Step 5: Notify seller
   */
  async handleDisputeWebhook(dispute: DisputeData): Promise<{ success: boolean; orderId?: string; error?: string }> {
    try {
      // Check if dispute already exists (idempotency)
      const { data: existingDispute, error: _fetchError } = await this.supabase
        .from('disputes')
        .select('id, order_id')
        .eq('stripe_dispute_id', dispute.stripeDisputeId)
        .single();

      if (existingDispute) {
        // Update existing dispute status
        await this.supabase
          .from('disputes')
          .update({ status: dispute.status })
          .eq('stripe_dispute_id', dispute.stripeDisputeId);

        return {
          success: true,
          orderId: existingDispute.order_id,
          details: 'Dispute already exists, status updated',
        } as unknown;
      }

      // Find order by stripe charge
      // (Need to join payment_intent -> charge -> orders)
      // For now, use metadata or search by amount + date
      const { data: order, error: _orderError } = await this.supabase
        .from('orders')
        .select('id, user_id, total_amount, stripe_payment_intent_id')
        .eq('stripe_payment_intent_id', dispute.stripeChargeId) // Rough match
        .limit(1);

      let orderId: string | null = null;
      if (order && order.length > 0) {
        orderId = order[0].id;
      }

      // Create dispute record
      const { data: disputeRecord, error: createError } = await this.supabase
        .from('disputes')
        .insert({
          order_id: orderId,
          stripe_dispute_id: dispute.stripeDisputeId,
          stripe_charge_id: dispute.stripeChargeId,
          amount_cents: dispute.amountCents,
          currency: dispute.currency,
          status: dispute.status,
          reason: dispute.reason,
          reason_description: dispute.reasonDescription,
        })
        .select()
        .single();

      if (createError) {
        return { success: false, error: 'Failed to create dispute record' };
      }

      // Apply payout hold (if dispute created/opened)
      if (
        dispute.status.includes('needs_response') ||
        dispute.status.includes('under_review')
      ) {
        const holdSuccess = await this.applyPayoutHold(
          disputeRecord.id,
          orderId,
          dispute.amountCents
        );

        if (!holdSuccess) {
          console.warn('Failed to apply payout hold for dispute:', disputeRecord.id);
        }
      }

      // Mark affiliate commissions as pending (don't pay)
      if (orderId) {
        await this.holdAffiliateCommissions(orderId);
      }

      // Update order status to disputed
      if (orderId) {
        await this.supabase
          .from('orders')
          .update({ status: 'disputed' })
          .eq('id', orderId);
      }

      return {
        success: true,
        orderId,
      } as unknown;
    } catch (error) {
      console.error('Error handling dispute webhook:', error);
      return { success: false, error: 'Internal server error' };
    }
  }

  /**
   * Apply payout hold when dispute is opened
   * Prevents seller from receiving payout until resolved
   */
  private async applyPayoutHold(
    disputeId: string,
    orderId: string | null,
    holdAmountCents: number
  ): Promise<boolean> {
    try {
      if (!orderId) return false;

      // Get seller for this order
      const { data: shipments } = await this.supabase
        .from('shipments')
        .select('seller_user_id')
        .eq('order_id', orderId)
        .limit(1);

      if (!shipments || shipments.length === 0) return false;

      // Mark payout as held (in a real system, this would use a payout management table)
      // For now, just mark in dispute record
      const { error } = await this.supabase
        .from('disputes')
        .update({
          payout_hold_applied: true,
          payout_hold_amount_cents: holdAmountCents,
        })
        .eq('id', disputeId);

      return !error;
    } catch (error) {
      console.error('Error applying payout hold:', error);
      return false;
    }
  }

  /**
   * Hold affiliate commissions (don't pay until dispute resolved)
   */
  private async holdAffiliateCommissions(orderId: string): Promise<boolean> {
    try {
      // Mark related affiliate conversions as pending (don't include in payout)
      // Stub: In full system, create separate "dispute_hold" records
      const { error } = await this.supabase
        .from('affiliate_conversions')
        .update({ metadata_json: { dispute_hold: true } })
        .eq('order_id', orderId);

      return !error;
    } catch (error) {
      console.error('Error holding affiliate commissions:', error);
      return false;
    }
  }

  /**
   * Handle dispute resolution (won/lost)
   * Step 1: Release payout hold if dispute won
   * Step 2: Release affiliate commissions
   * Step 3: Update order status
   */
  async resolveDispute(
    stripeDisputeId: string,
    outcome: 'won' | 'lost'
  ): Promise<{ success: boolean; error?: string }> {
    try {
      // Get dispute
      const { data: dispute, error: fetchError } = await this.supabase
        .from('disputes')
        .select('*')
        .eq('stripe_dispute_id', stripeDisputeId)
        .single();

      if (fetchError || !dispute) {
        return { success: false, error: 'Dispute not found' };
      }

      // Update dispute status
      const { error: updateError } = await this.supabase
        .from('disputes')
        .update({
          status: outcome,
          payout_hold_applied: false,
        })
        .eq('id', dispute.id);

      if (updateError) {
        return { success: false, error: 'Failed to update dispute' };
      }

      // Update order status
      if (dispute.order_id) {
        const orderStatus = outcome === 'won' ? 'completed' : 'disputed_lost';
        await this.supabase
          .from('orders')
          .update({ status: orderStatus })
          .eq('id', dispute.order_id);

        // Release affiliate hold
        await this.supabase
          .from('affiliate_conversions')
          .update({ metadata_json: { dispute_hold: false } })
          .eq('order_id', dispute.order_id);
      }

      return { success: true };
    } catch (error) {
      console.error('Error resolving dispute:', error);
      return { success: false, error: 'Internal server error' };
    }
  }

  /**
   * Get dispute details
   */
  async getDispute(disputeId: string) {
    try {
      const { data, error } = await this.supabase
        .from('disputes')
        .select(
          `
          *,
          orders (
            id,
            user_id,
            total_amount,
            status,
            shipments (seller_user_id)
          )
        `
        )
        .eq('id', disputeId)
        .single();

      if (error) throw error;
      return data;
    } catch (error) {
      console.error('Error fetching dispute:', error);
      return null;
    }
  }

  /**
   * List disputes (admin view)
   */
  async listDisputes(status?: string) {
    try {
      let query = this.supabase
        .from('disputes')
        .select(
          `
          id,
          order_id,
          stripe_dispute_id,
          amount_cents,
          status,
          reason,
          created_at
        `
        );

      if (status) {
        query = query.eq('status', status);
      }

      const { data, error } = await query.order('created_at', { ascending: false });

      if (error) throw error;
      return data || [];
    } catch (error) {
      console.error('Error listing disputes:', error);
      return [];
    }
  }

  /**
   * List disputes for seller
   */
  async listSellerDisputes(sellerUserId: string) {
    try {
      // This requires joining through orders -> shipments
      const { data, error } = await this.supabase
        .from('disputes')
        .select(
          `
          id,
          order_id,
          stripe_dispute_id,
          amount_cents,
          status,
          reason,
          created_at,
          orders (
            id,
            shipments (
              id,
              seller_user_id
            )
          )
        `
        )
        .order('created_at', { ascending: false });

      if (error) throw error;

      // Filter by seller user ID
      const sellerDisputes = (data || []).filter((d: unknown) =>
        d.orders?.shipments?.some((s: unknown) => s.seller_user_id === sellerUserId)
      );

      return sellerDisputes;
    } catch (error) {
      console.error('Error listing seller disputes:', error);
      return [];
    }
  }
}

// Fulfillment Service Orchestrator
// Main service for automated order fulfillment

import { SupabaseClient } from '@supabase/supabase-js';
import { SupplierScoringEngine } from './SupplierScoringEngine';
import { SupplierAdapter } from '../suppliers/SupplierAdapter';
import { ManualSupplierAdapter } from '../suppliers/ManualSupplierAdapter';
import { WarehouseAdapter } from '../suppliers/WarehouseAdapter';
import { MockApiSupplierAdapter } from '../suppliers/MockApiSupplierAdapter';
import { DigitalDeliveryAdapter } from '../suppliers/DigitalDeliveryAdapter';

export interface FulfillmentJobInput {
  orderId: string;
  storeId: string;
  items: Array<{
    productId: string;
    quantity: number;
  }>;
}

export interface FulfillmentJobResult {
  success: boolean;
  jobId?: string;
  error?: string;
}

export class FulfillmentService {
  private supabase: SupabaseClient;
  private scoringEngine: SupplierScoringEngine;
  private adapters: Map<string, SupplierAdapter>;

  constructor(supabase: SupabaseClient) {
    this.supabase = supabase;
    this.scoringEngine = new SupplierScoringEngine(supabase);
    this.adapters = new Map();
  }

  /**
   * Create fulfillment job(s) for an order
   * This is called after payment confirmation
   */
  async createFulfillmentJob(input: FulfillmentJobInput): Promise<FulfillmentJobResult> {
    try {
      // Get order details
      const { data: order, error: orderError } = await this.supabase
        .from('orders')
        .select('*, order_items(*)')
        .eq('id', input.orderId)
        .single();

      if (orderError || !order) {
        return {
          success: false,
          error: 'Order not found',
        };
      }

      // Check fraud before processing
      const fraudCheck = await this.performFraudCheck(order);
      if (fraudCheck.status === 'blocked') {
        return {
          success: false,
          error: 'Order flagged for fraud review',
        };
      }

      // Get product details to determine fulfillment type
      const productIds = input.items.map((item) => item.productId);
      const { data: products } = await this.supabase
        .from('products')
        .select('*')
        .in('id', productIds);

      if (!products || products.length === 0) {
        return {
          success: false,
          error: 'Products not found',
        };
      }

      // Group items by fulfillment type
      const groupedItems = this.groupItemsByFulfillmentType(input.items, products);

      // Create fulfillment job for each group
      const jobIds: string[] = [];
      for (const [fulfillmentType, items] of Object.entries(groupedItems)) {
        const jobId = await this.createJobForType(
          input.orderId,
          input.storeId,
          fulfillmentType as unknown,
          items
        );
        if (jobId) {
          jobIds.push(jobId);
        }
      }

      if (jobIds.length === 0) {
        return {
          success: false,
          error: 'Failed to create fulfillment jobs',
        };
      }

      return {
        success: true,
        jobId: jobIds[0], // Return first job ID
      };
    } catch (error: unknown) {
      console.error('Error creating fulfillment job:', error);
      return {
        success: false,
        error: error.message || 'Internal error',
      };
    }
  }

  /**
   * Process a fulfillment job (async worker)
   */
  async processFulfillmentJob(jobId: string): Promise<void> {
    try {
      // Get job details
      const { data: job, error } = await this.supabase
        .from('fulfillment_jobs')
        .select('*')
        .eq('id', jobId)
        .single();

      if (error || !job) {
        console.error('Job not found:', jobId);
        return;
      }

      // Update status to processing
      await this.supabase
        .from('fulfillment_jobs')
        .update({ status: 'processing', processed_at: new Date().toISOString() })
        .eq('id', jobId);

      // Route to appropriate handler
      switch (job.fulfillment_type) {
        case 'digital':
          await this.handleDigitalFulfillment(job);
          break;
        case 'manual':
          await this.handleManualFulfillment(job);
          break;
        case 'warehouse':
          await this.handleWarehouseFulfillment(job);
          break;
        case 'dropship':
          await this.handleDropshipFulfillment(job);
          break;
        default:
          throw new Error(`Unknown fulfillment type: ${job.fulfillment_type}`);
      }
    } catch (error: unknown) {
      console.error('Error processing fulfillment job:', error);
      await this.handleJobError(jobId, error.message);
    }
  }

  /**
   * Handle digital product fulfillment (instant delivery)
   */
  private async handleDigitalFulfillment(job: unknown): Promise<void> {
    const _adapter = new DigitalDeliveryAdapter('digital-001');
    
    // Digital fulfillment is instant
    await this.supabase
      .from('fulfillment_jobs')
      .update({
        status: 'delivered',
        supplier_order_id: `DIGITAL-${Date.now()}`,
      })
      .eq('id', job.id);

    // Update order status
    await this.supabase
      .from('orders')
      .update({ status: 'delivered', delivered_at: new Date().toISOString() })
      .eq('id', job.order_id);

    // TODO: Send download link to customer via email
  }

  /**
   * Handle manual fulfillment (seller ships)
   */
  private async handleManualFulfillment(job: unknown): Promise<void> {
    // Manual fulfillment stays in queued/processing
    // Seller will add tracking manually
    
    await this.supabase
      .from('fulfillment_jobs')
      .update({
        status: 'processing',
        supplier_order_id: `MANUAL-${Date.now()}`,
      })
      .eq('id', job.id);

    // TODO: Notify seller to ship order
  }

  /**
   * Handle warehouse fulfillment (internal pick & pack)
   */
  private async handleWarehouseFulfillment(job: unknown): Promise<void> {
    const adapter = new WarehouseAdapter('warehouse-001');
    
    // Get order details for shipping address
    const { data: order } = await this.supabase
      .from('orders')
      .select('*')
      .eq('id', job.order_id)
      .single();

    if (!order) throw new Error('Order not found');

    // Create warehouse task
    const result = await adapter.createOrder({
      orderReference: job.order_id,
      customerName: order.buyer_name || 'Customer',
      shippingAddress: order.shipping_address || {},
      items: [], // TODO: Map order items
    });

    if (result.success) {
      await this.supabase
        .from('fulfillment_jobs')
        .update({
          status: 'processing',
          supplier_order_id: result.supplierOrderId,
        })
        .eq('id', job.id);

      // TODO: Notify warehouse staff
    } else {
      throw new Error(result.error || 'Warehouse order creation failed');
    }
  }

  /**
   * Handle dropship fulfillment (supplier ships)
   */
  private async handleDropshipFulfillment(job: unknown): Promise<void> {
    // Get order details
    const { data: order } = await this.supabase
      .from('orders')
      .select('*, order_items(*)')
      .eq('id', job.order_id)
      .single();

    if (!order || !order.order_items) throw new Error('Order not found');

    // Select best supplier using AI
    const productId = order.order_items[0]?.product_id;
    if (!productId) throw new Error('No products in order');

    const bestSupplier = await this.scoringEngine.selectBestSupplier(productId);
    if (!bestSupplier) throw new Error('No supplier available');

    // Get supplier adapter
    const adapter = await this.getSupplierAdapter(bestSupplier.supplierId);
    if (!adapter) throw new Error('Supplier adapter not found');

    // Create order with supplier
    const result = await adapter.createOrder({
      orderReference: job.order_id,
      customerName: order.buyer_name || 'Customer',
      shippingAddress: order.shipping_address || {},
      items: order.order_items.map((item: unknown) => ({
        supplierSku: item.supplier_sku || item.product_id,
        quantity: item.quantity,
        unitPriceCents: item.unit_price_cents,
      })),
    });

    if (result.success) {
      // Update job with tracking info
      await this.supabase
        .from('fulfillment_jobs')
        .update({
          status: 'shipped',
          supplier_id: bestSupplier.supplierId,
          supplier_order_id: result.supplierOrderId,
          tracking_number: result.trackingNumber,
          carrier: result.carrier,
          estimated_delivery_date: result.estimatedDeliveryDate,
        })
        .eq('id', job.id);

      // Create shipment record
      if (result.trackingNumber) {
        await this.supabase.from('order_shipments').insert({
          order_id: job.order_id,
          fulfillment_job_id: job.id,
          tracking_number: result.trackingNumber,
          carrier: result.carrier || 'Unknown',
          status: 'in_transit',
          shipped_at: new Date().toISOString(),
          estimated_delivery_at: result.estimatedDeliveryDate,
        });
      }

      // Update order status
      await this.supabase
        .from('orders')
        .update({ status: 'shipped' })
        .eq('id', job.order_id);
    } else {
      throw new Error(result.error || 'Supplier order creation failed');
    }
  }

  /**
   * Handle job error with retry logic
   */
  private async handleJobError(jobId: string, errorMessage: string): Promise<void> {
    const { data: job } = await this.supabase
      .from('fulfillment_jobs')
      .select('*')
      .eq('id', jobId)
      .single();

    if (!job) return;

    const retryCount = job.retry_count + 1;
    const maxRetries = job.max_retries || 3;

    // Log error
    await this.supabase.from('fulfillment_errors').insert({
      fulfillment_job_id: jobId,
      error_type: 'processing_error',
      error_message: errorMessage,
      retry_attempt: retryCount,
    });

    if (retryCount >= maxRetries) {
      // Max retries reached, mark as failed
      await this.supabase
        .from('fulfillment_jobs')
        .update({
          status: 'failed',
          error_message: errorMessage,
          retry_count: retryCount,
        })
        .eq('id', jobId);

      // TODO: Notify admin + auto-refund option
    } else {
      // Schedule retry with exponential backoff
      const nextRetryMinutes = Math.pow(2, retryCount) * 5; // 5, 10, 20 minutes
      const nextRetryAt = new Date(Date.now() + nextRetryMinutes * 60 * 1000);

      await this.supabase
        .from('fulfillment_jobs')
        .update({
          status: 'failed',
          error_message: errorMessage,
          retry_count: retryCount,
          next_retry_at: nextRetryAt.toISOString(),
        })
        .eq('id', jobId);
    }
  }

  /**
   * Perform fraud check before fulfillment
   */
  private async performFraudCheck(order: unknown): Promise<unknown> {
    // Check if fraud check already exists
    const { data: existing } = await this.supabase
      .from('fraud_checks')
      .select('*')
      .eq('order_id', order.id)
      .single();

    if (existing) return existing;

    // Simple fraud checks (can be extended)
    const riskFactors: string[] = [];
    let riskScore = 0;

    // Check Stripe risk level
    if (order.stripe_risk_level === 'highest') {
      riskFactors.push('High Stripe risk score');
      riskScore += 50;
    } else if (order.stripe_risk_level === 'elevated') {
      riskFactors.push('Elevated Stripe risk');
      riskScore += 25;
    }

    // Check order value (high value = higher risk)
    if (order.total_amount > 50000) {
      // $500+
      riskFactors.push('High order value');
      riskScore += 20;
    }

    // Determine status
    let status = 'approved';
    if (riskScore >= 50) {
      status = 'flagged';
    }
    if (riskScore >= 75) {
      status = 'blocked';
    }

    // Create fraud check record
    const { data: fraudCheck } = await this.supabase
      .from('fraud_checks')
      .insert({
        order_id: order.id,
        stripe_risk_level: order.stripe_risk_level,
        stripe_risk_score: riskScore,
        risk_factors: riskFactors,
        status,
        checks_performed: {
          stripe_check: true,
          value_check: true,
        },
      })
      .select()
      .single();

    return fraudCheck || { status: 'approved' };
  }

  /**
   * Group items by fulfillment type
   */
  private groupItemsByFulfillmentType(
    items: Array<{ productId: string; quantity: number }>,
    products: unknown[]
  ): Record<string, unknown[]> {
    const grouped: Record<string, unknown[]> = {};

    for (const item of items) {
      const product = products.find((p) => p.id === item.productId);
      if (!product) continue;

      const type = product.fulfillment_type || 'manual';
      if (!grouped[type]) {
        grouped[type] = [];
      }
      grouped[type].push({ ...item, product });
    }

    return grouped;
  }

  /**
   * Create job for specific fulfillment type
   */
  private async createJobForType(
    orderId: string,
    storeId: string,
    fulfillmentType: string,
    items: unknown[]
  ): Promise<string | null> {
    const { data: job, error } = await this.supabase
      .from('fulfillment_jobs')
      .insert({
        order_id: orderId,
        store_id: storeId,
        fulfillment_type: fulfillmentType,
        status: 'queued',
        metadata_json: { items },
      })
      .select()
      .single();

    if (error || !job) {
      console.error('Error creating job:', error);
      return null;
    }

    // Process job asynchronously
    setTimeout(() => this.processFulfillmentJob(job.id), 1000);

    return job.id;
  }

  /**
   * Get supplier adapter instance
   */
  private async getSupplierAdapter(supplierId: string): Promise<SupplierAdapter | null> {
    // Check cache
    if (this.adapters.has(supplierId)) {
      return this.adapters.get(supplierId)!;
    }

    // Fetch supplier details
    const { data: supplier } = await this.supabase
      .from('suppliers')
      .select('*')
      .eq('id', supplierId)
      .single();

    if (!supplier) return null;

    let adapter: SupplierAdapter;

    switch (supplier.type) {
      case 'manual':
        adapter = new ManualSupplierAdapter(supplier.id, supplier.name);
        break;
      case 'warehouse':
        adapter = new WarehouseAdapter(supplier.id, supplier.name);
        break;
      case 'api':
        adapter = new MockApiSupplierAdapter({
          supplierId: supplier.id,
          supplierName: supplier.name,
          apiEndpoint: supplier.api_endpoint,
          apiKey: supplier.api_key_encrypted, // TODO: Decrypt
        });
        break;
      default:
        return null;
    }

    this.adapters.set(supplierId, adapter);
    return adapter;
  }
}

// Tracking Sync Service
// Periodically syncs tracking info from suppliers

import { SupabaseClient } from '@supabase/supabase-js';
import { SupplierAdapter } from '../suppliers/SupplierAdapter';
import { MockApiSupplierAdapter } from '../suppliers/MockApiSupplierAdapter';
import { ManualSupplierAdapter } from '../suppliers/ManualSupplierAdapter';
import { WarehouseAdapter } from '../suppliers/WarehouseAdapter';

export class TrackingSync Service {
  private supabase: SupabaseClient;
  private adapters: Map<string, SupplierAdapter>;

  constructor(supabase: SupabaseClient) {
    this.supabase = supabase;
    this.adapters = new Map();
  }

  /**
   * Sync tracking for all shipped orders
   * Run this as a cron job (e.g., every 6 hours)
   */
  async syncAllTracking(): Promise<{ synced: number; errors: number }> {
    let synced = 0;
    let errors = 0;

    try {
      // Get all jobs with status 'shipped' that have supplier_order_id
      const { data: jobs } = await this.supabase
        .from('fulfillment_jobs')
        .select('*, suppliers(*)')
        .eq('status', 'shipped')
        .not('supplier_order_id', 'is', null);

      if (!jobs || jobs.length === 0) {
        console.log('No jobs to sync');
        return { synced, errors };
      }

      console.log(`Syncing tracking for ${jobs.length} jobs`);

      for (const job of jobs) {
        try {
          const updated = await this.syncJobTracking(job);
          if (updated) synced++;
        } catch (error) {
          console.error(`Error syncing job ${job.id}:`, error);
          errors++;
        }
      }

      return { synced, errors };
    } catch (error) {
      console.error('Error in syncAllTracking:', error);
      return { synced, errors };
    }
  }

  /**
   * Sync tracking for a specific job
   */
  async syncJobTracking(job: any): Promise<boolean> {
    if (!job.supplier_id || !job.supplier_order_id) {
      return false;
    }

    const adapter = await this.getSupplierAdapter(job. supplier_id, job.suppliers);
    if (!adapter || !adapter.supportsFeature('auto_tracking')) {
      return false;
    }

    try {
      const tracking = await adapter.getTracking(job.supplier_order_id);
      if (!tracking) return false;

      // Update job if tracking number changed
      if (tracking.trackingNumber && tracking.trackingNumber !== job.tracking_number) {
        await this.supabase
          .from('fulfillment_jobs')
          .update({
            tracking_number: tracking.trackingNumber,
            carrier: tracking.carrier,
          })
          .eq('id', job.id);
      }

      // Update shipment record
      if (tracking.trackingNumber) {
        await this.updateShipmentTracking(job.order_id, tracking);
      }

      // Check if delivered
      if (tracking.status === 'delivered') {
        await this.handleDelivery(job);
      }

      return true;
    } catch (error) {
      console.error(`Error syncing tracking for job ${job.id}:`, error);
      return false;
    }
  }

  /**
   * Update shipment record with tracking events
   */
  private async updateShipmentTracking(orderId: string, tracking: any): Promise<void> {
    const { data: shipment } = await this.supabase
      .from('order_shipments')
      .select('*')
      .eq('order_id', orderId)
      .eq('tracking_number', tracking.trackingNumber)
      .single();

    if (!shipment) {
      // Create shipment if doesn't exist
      await this.supabase.from('order_shipments').insert({
        order_id: orderId,
        tracking_number: tracking.trackingNumber,
        carrier: tracking.carrier,
        status: tracking.status,
        tracking_events: tracking.events,
      });
    } else {
      // Update existing shipment
      await this.supabase
        .from('order_shipments')
        .update({
          status: tracking.status,
          tracking_events: tracking.events,
          delivered_at:
            tracking.status === 'delivered' && !shipment.delivered_at
              ? new Date().toISOString()
              : shipment.delivered_at,
        })
        .eq('id', shipment.id);
    }
  }

  /**
   * Handle delivery (mark order as delivered)
   */
  private async handleDelivery(job: any): Promise<void> {
    // Update job status
    await this.supabase
      .from('fulfillment_jobs')
      .update({ status: 'delivered' })
      .eq('id', job.id);

    // Check if all jobs for order are delivered
    const { data: allJobs } = await this.supabase
      .from('fulfillment_jobs')
      .select('status')
      .eq('order_id', job.order_id);

    const allDelivered = allJobs?.every((j) => j.status === 'delivered');

    if (allDelivered) {
      // Mark order as delivered
      await this.supabase
        .from('orders')
        .update({
          status: 'delivered',
          delivered_at: new Date().toISOString(),
        })
        .eq('id', job.order_id);

      // TODO: Send delivery confirmation email to customer
    }
  }

  /**
   * Get supplier adapter instance
   */
  private async getSupplierAdapter(
    supplierId: string,
    supplierData?: any
  ): Promise<SupplierAdapter | null> {
    if (this.adapters.has(supplierId)) {
      return this.adapters.get(supplierId)!;
    }

    let supplier = supplierData;
    if (!supplier) {
      const { data } = await this.supabase
        .from('suppliers')
        .select('*')
        .eq('id', supplierId)
        .single();
      supplier = data;
    }

    if (!supplier) return null;

    let adapter: SupplierAdapter;

    switch (supplier.type) {
      case 'manual':
        adapter = new ManualSupplierAdapter(supplier.id, supplier.name);
        break;
      case 'warehouse':
        adapter = new WarehouseAdapter(supplier.id, supplier.name);
        break;
      case 'api':
        adapter = new MockApiSupplierAdapter({
          supplierId: supplier.id,
          supplierName: supplier.name,
          apiEndpoint: supplier.api_endpoint,
          apiKey: supplier.api_key_encrypted,
        });
        break;
      default:
        return null;
    }

    this.adapters.set(supplierId, adapter);
    return adapter;
  }
}

