/**
 * Auto-Margin Guard
 * Simulates worst-case margin scenarios before product publication
 * Ensures product doesn't become unprofitable under adverse conditions
 */

import { MarginSimulation, MarginScenario } from './types';
import { computeMargin } from '@/lib/finance/margin';
import { roundToNearest } from '@/lib/finance/money';
import { GEORGIA_VAT_BPS } from '@/lib/finance/vat';

interface WorstCaseFactors {
  maxRefundRatePct: number; // 0-100 (worst case)
  maxShippingDelayDays: number; // Additional days delay
  maxReturnShippingCostCents: number; // Return shipping cost
  maxPlatformFeeincreaseBps: number; // Additional fee increase
  competitorPriceCutPct: number; // Competitive price pressure
}

/**
 * Simulate worst-case margin: multiple negative factors hitting at once
 *
 * Worst-case scenarios:
 * 1. Best case: Normal operation (most likely)
 * 2. Average case: Some refunds, minor delays
 * 3. Worst case: High refunds, long delays, competitor price war, platform fee increase
 */
export function simulateWorstCaseMargin(
  retailPriceCents: number,
  supplierCostCents: number,
  shippingCostCents: number,
  platformFeeBps: number,
  affiliateFeeBps: number,
  refundReserveBps: number,
  worseCaseFactors: WorstCaseFactors
): MarginSimulation {
  // Best case: everything goes well
  const bestCaseResult = computeMargin({
    retail_price_cents: retailPriceCents,
    supplier_cost_cents: supplierCostCents,
    shipping_cost_cents: shippingCostCents,
    vat_enabled: true,
    vat_rate_bps: GEORGIA_VAT_BPS,
    platform_fee_bps: platformFeeBps,
    affiliate_bps: affiliateFeeBps,
    refund_reserve_bps: refundReserveBps,
  });

  // Worst case: multiple bad things happen
  // 1. Refund rate high: Lose revenue on some orders
  const refundLoss = Math.round(
    (retailPriceCents * worseCaseFactors.maxRefundRatePct) /100
  );

  // 2. Shipping delays increase refund requests
  const _delayMargin = Math.max(0, -worseCaseFactors.maxShippingDelayDays * 100); // 100 bps per day

  // 3. Return shipping cost eats into margin
  // 4. Platform fee increases
  const additionalFeesBps = worseCaseFactors.maxPlatformFeeincreaseBps;

  // 5. Competitor price pressure: We match lower price
  const competitorAdjustedPrice = Math.round(
    retailPriceCents * (1 - worseCaseFactors.competitorPriceCutPct / 100)
  );

  // Worst-case effective price after refunds and competitor pressure
  const worstCaseEffectivePrice = Math.max(competitorAdjustedPrice, retailPriceCents - refundLoss);

  // Recompute margin with worst-case inputs
  const additionalCosts =
    worseCaseFactors.maxReturnShippingCostCents + worseCaseFactors.maxRefundRatePct * 10; // Rough estimate

  const worstCaseResult = computeMargin({
    retail_price_cents: worstCaseEffectivePrice,
    supplier_cost_cents: supplierCostCents + additionalCosts,
    shipping_cost_cents: shippingCostCents + worseCaseFactors.maxReturnShippingCostCents / 2, // Some customers don't return
    vat_enabled: true,
    vat_rate_bps: GEORGIA_VAT_BPS,
    platform_fee_bps: platformFeeBps + additionalFeesBps,
    affiliate_bps: affiliateFeeBps + 500, // Affiliate fees increase in promotional periods
    refund_reserve_bps: refundReserveBps + 500, // Need more buffer
  });

  // Average case: mild adverse conditions
  const avgCaseResult = computeMargin({
    retail_price_cents: retailPriceCents - Math.round(retailPriceCents * (worseCaseFactors.competitorPriceCutPct / 100) * 0.3),
    supplier_cost_cents: supplierCostCents + worseCaseFactors.maxReturnShippingCostCents / 4,
    shipping_cost_cents: shippingCostCents,
    vat_enabled: true,
    vat_rate_bps: GEORGIA_VAT_BPS,
    platform_fee_bps: platformFeeBps + additionalFeesBps * 0.5,
    affiliate_bps: affiliateFeeBps,
    refund_reserve_bps: refundReserveBps,
  });

  // Build scenario breakdown
  const scenarios: MarginScenario[] = [
    {
      name: 'Best Case (Normal)',
      probability: 0.60, // 60% of time
      marginBps: Math.round(bestCaseResult.margin_percent * 100),
      assumptions: {
        refundRate: '2-3%',
        deliveryDelay: 'On-time',
        priceCompetition: 'None',
        platformFees: 'Standard',
      },
    },
    {
      name: 'Average Case (Minor Issues)',
      probability: 0.30, // 30% of time
      marginBps: Math.round(avgCaseResult.margin_percent * 100),
      assumptions: {
        refundRate: `${Math.round(worseCaseFactors.maxRefundRatePct / 2)}%`,
        deliveryDelay: `${Math.round(worseCaseFactors.maxShippingDelayDays * 0.5)} days`,
        priceCompetition: `${Math.round(worseCaseFactors.competitorPriceCutPct * 0.3)}%`,
        platformFees: '+50 bps',
      },
    },
    {
      name: 'Worst Case (All Negatives)',
      probability: 0.10, // 10% of time (rare but possible)
      marginBps: Math.round(worstCaseResult.margin_percent * 100),
      assumptions: {
        refundRate: `${worseCaseFactors.maxRefundRatePct}%`,
        deliveryDelay: `${worseCaseFactors.maxShippingDelayDays} days`,
        priceCompetition: `${worseCaseFactors.competitorPriceCutPct}%`,
        platformFees: `${Math.round(additionalFeesBps / 100)}%`,
        returnShipping: `₾${Math.round(worseCaseFactors.maxReturnShippingCostCents / 100)}`,
      },
    },
  ];

  // Determine approval: Worst case must be profitable
  const minAcceptableMarginBps = 500; // 5% absolute minimum
  const worstCaseMarginBps = Math.round(worstCaseResult.margin_percent * 100);
  const isApproved = worstCaseMarginBps >= minAcceptableMarginBps;

  const rejectionReason = !isApproved
    ? `Worst-case margin ${(worstCaseMarginBps / 100).toFixed(1)}% falls below 5% minimum. Recommended: increase price by ${Math.ceil(
        ((minAcceptableMarginBps - worstCaseMarginBps) / retailPriceCents) * 10000
      )} bps`
    : undefined;

  return {
    bestCaseMarginBps: Math.round(bestCaseResult.margin_percent * 100),
    worstCaseMarginBps: worstCaseMarginBps,
    avgCaseMarginBps: Math.round(avgCaseResult.margin_percent * 100),
    scenarios,
    isApproved,
    rejectionReason,
  };
}

/**
 * Conservative margin check: Product must survive worst case
 * Returns the minimum price needed to stay profitable in worst case
 */
export function minPriceForWorstCase(
  supplierCostCents: number,
  shippingCostCents: number,
  platformFeeBps: number,
  affiliateFeeBps: number,
  refundReserveBps: number,
  worseCaseFactors: WorstCaseFactors,
  minMarginBps = 500 // 5% minimum
): number {
  // Work backwards: What price is needed to maintain minMarginBps in worst case?
  // Margin = (Price - Costs - Fees) / Price
  // So: Price = Costs / (1 - margin/10000)

  const totalCostsInWorstCase = supplierCostCents +
    shippingCostCents +
    worseCaseFactors.maxReturnShippingCostCents +
    Math.round((supplierCostCents * (platformFeeBps + worseCaseFactors.maxPlatformFeeincreaseBps)) / 10000) +
    Math.round((supplierCostCents * affiliateFeeBps) / 10000);

  // Solve for price: price = costs / (1 - targetMargin%)
  const targetMarginFraction = Math.max(0.01, minMarginBps / 10000); // At least 0.1%
  const neededPrice = Math.round(totalCostsInWorstCase / (1 - targetMarginFraction));

  // Round to nearest 50 cents
  return roundToNearest(neededPrice, 50);
}

/**
 * Sensitivity analysis: How much does each factor affect margin?
 */
export function marginSensitivity(
  retailPriceCents: number,
  supplierCostCents: number,
  shippingCostCents: number,
  platformFeeBps: number
): Record<string, number> {
  const baseResult = computeMargin({
    retail_price_cents: retailPriceCents,
    supplier_cost_cents: supplierCostCents,
    shipping_cost_cents: shippingCostCents,
    vat_enabled: true,
    vat_rate_bps: GEORGIA_VAT_BPS,
    platform_fee_bps: platformFeeBps,
    affiliate_bps: 0,
    refund_reserve_bps: 0,
  });

  const baseMarginBps = Math.round(baseResult.margin_percent * 100);

  // Test each factor independently
  const refundResult = computeMargin({
    retail_price_cents: Math.round(retailPriceCents * 0.95), // 5% refund rate
    supplier_cost_cents: supplierCostCents,
    shipping_cost_cents: shippingCostCents,
    vat_enabled: true,
    vat_rate_bps: GEORGIA_VAT_BPS,
    platform_fee_bps: platformFeeBps,
    affiliate_bps: 0,
    refund_reserve_bps: 0,
  });

  const refundImpact = Math.round(refundResult.margin_percent * 100) - baseMarginBps;

  const shippingDelayImpact = -100; // 1% margin per day delay (fixed estimate)

  const competitorResult = computeMargin({
    retail_price_cents: Math.round(retailPriceCents * 0.9), // 10% price cut from competition
    supplier_cost_cents: supplierCostCents,
    shipping_cost_cents: shippingCostCents,
    vat_enabled: true,
    vat_rate_bps: GEORGIA_VAT_BPS,
    platform_fee_bps: platformFeeBps,
    affiliate_bps: 0,
    refund_reserve_bps: 0,
  });

  const competitorCapImpact = Math.round(competitorResult.margin_percent * 100) - baseMarginBps;

  const feesResult = computeMargin({
    retail_price_cents: retailPriceCents,
    supplier_cost_cents: supplierCostCents,
    shipping_cost_cents: shippingCostCents,
    vat_enabled: true,
    vat_rate_bps: GEORGIA_VAT_BPS,
    platform_fee_bps: platformFeeBps + 500, // +5% fee increase
    affiliate_bps: 0,
    refund_reserve_bps: 0,
  });

  const feesIncreaseImpact = Math.round(feesResult.margin_percent * 100) - baseMarginBps;

  return {
    refundRate5Pct: refundImpact,
    shippingDelayPerDay: shippingDelayImpact,
    competitorPrice10PctCut: competitorCapImpact,
    platformFeeIncrease5Pct: feesIncreaseImpact,
  };
}

/**
 * Dynamic Pricing Engine
 * Automatically adjusts prices based on:
 * - Margin targets
 * - Conversion rates
 * - Inventory levels
 * - Demand signals
 */

import { DynamicPriceResult, PricingSignals } from './types';
import { roundToNearest } from '@/lib/finance/money';

// Pricing constants (in basis points)
const _MIN_ADJUSTMENT_BPS = 100; // 1% minimum
const MAX_ADJUSTMENT_BPS = 1500; // 15% maximum
const TARGET_CONVERSION_RATE = 5; // 5% target

/**
 * Compute recommended dynamic price based on market signals
 *
 * Logic:
 * 1. If margin < target: increase price (+1-5%)
 * 2. If inventory high: decrease price to clear stock
 * 3. If conversion low: test lower price (-2-5%)
 * 4. If conversion high: increase price (+1-3%)
 * 5. Apply seasonality multiplier
 * 6. Respect minimum margin threshold (15% standard)
 *
 * @param currentPriceCents Current retail price (cents)
 * @param signals Market signals (margin, conversion, inventory, etc)
 * @param minMarginBps Minimum acceptable margin (default: 1500 = 15%)
 * @returns Dynamic price adjustment recommendation
 */
export function computeDynamicPrice(
  currentPriceCents: number,
  signals: PricingSignals,
  minMarginBps = 1500
): DynamicPriceResult {
  let adjustmentBps = 0;
  let reason = '';
  let recommendedAction: 'increase' | 'decrease' | 'maintain' = 'maintain';

  // Rule 1: Margin below target → Increase price
  if (signals.currentMarginBps < signals.targetMarginBps && signals.currentMarginBps >= minMarginBps) {
    const marginGap = signals.targetMarginBps - signals.currentMarginBps;
    adjustmentBps = Math.min(marginGap / 2, 500); // 0.5-5% increase
    reason = `Margin ${(signals.currentMarginBps / 100).toFixed(1)}% below target ${(signals.targetMarginBps / 100).toFixed(1)}%`;
    recommendedAction = 'increase';
  }

  // Rule 2: High inventory → Decrease to clear stock
  if (signals.inventoryLevel > signals.maxInventory * 0.8) {
    const overstock = signals.inventoryLevel / signals.maxInventory;
    const clearanceAdj = Math.min((overstock - 0.8) * 1000, 800); // 0.8-8% decrease
    adjustmentBps = Math.min(adjustmentBps - clearanceAdj, -200); // Max 2% price cut
    reason = `High inventory (${((signals.inventoryLevel / signals.maxInventory) * 100).toFixed(0)}% of capacity)`;
    recommendedAction = 'decrease';
  }

  // Rule 3: Low conversion → Test lower price
  if (signals.conversionRate < TARGET_CONVERSION_RATE / 2) {
    const conversionGap = TARGET_CONVERSION_RATE - signals.conversionRate;
    adjustmentBps = Math.min(adjustmentBps - 300, -conversionGap * 50); // 0.3-3% test reduction
    reason = `Low conversion rate ${signals.conversionRate.toFixed(2)}% (target: ${TARGET_CONVERSION_RATE}%)`;
    recommendedAction = 'decrease';
  }

  // Rule 4: High conversion → Increase price
  if (signals.conversionRate > TARGET_CONVERSION_RATE * 1.5) {
    const conversionBonus = signals.conversionRate - TARGET_CONVERSION_RATE;
    adjustmentBps = Math.min(adjustmentBps + conversionBonus * 30, 300); // 0.3-3% increase
    reason = `High conversion rate ${signals.conversionRate.toFixed(2)}% (premium pricing opportunity)`;
    recommendedAction = 'increase';
  }

  // Rule 5: Apply seasonality multiplier
  adjustmentBps = adjustmentBps * signals.seasonality;

  // Rule 6: Demand elasticity
  if (signals.demandTrend === 'high') {
    adjustmentBps = Math.min(adjustmentBps + 200, 800); // Additional 0.2-0.8%
  } else if (signals.demandTrend === 'low') {
    adjustmentBps = Math.max(adjustmentBps - 200, -600); // Additional 0.2-0.6% decrease
  }

  // Clamp adjustments to safe range
  adjustmentBps = Math.max(Math.min(adjustmentBps, MAX_ADJUSTMENT_BPS), -MAX_ADJUSTMENT_BPS);

  // Compute new price
  const multiplier = 1 + adjustmentBps / 10000;
  let newPriceCents = Math.round(currentPriceCents * multiplier);

  // Ensure new price respects minimum margin
  // Assuming: newMargin = (newPrice - costs - fees) / newPrice
  // For safety, we validate margin is still above minimum
  const expectedMarginBps = Math.max(
    Math.round(signals.currentMarginBps + adjustmentBps * 0.5), // Conservative estimate
    minMarginBps
  );

  // If calculated margin would drop below minimum, revert adjustment
  if (expectedMarginBps < minMarginBps) {
    adjustmentBps = 0;
    newPriceCents = currentPriceCents;
    reason = `Maintaining current price to respect ${(minMarginBps / 100).toFixed(1)}% margin floor`;
    recommendedAction = 'maintain';
  }

  // Round to nearest 50 cents (standard pricing format)
  newPriceCents = roundToNearest(newPriceCents, 50);

  return {
    newPriceCents,
    reason,
    expectedMarginBps,
    confidence: Math.min(100, Math.abs(adjustmentBps) * 2), // Higher adjustment = higher confidence
    recommendedAction,
  };
}

/**
 * Batch compute dynamic prices for multiple products
 */
export function batchComputeDynamicPrices(
  prices: Array<{ currentPriceCents: number; signals: PricingSignals }>,
  minMarginBps?: number
): DynamicPriceResult[] {
  return prices.map(({ currentPriceCents, signals }) =>
    computeDynamicPrice(currentPriceCents, signals, minMarginBps)
  );
}

/**
 * Elasticity-aware pricing: How price change affects demand
 * @param currentConversionRate Current conversion (0-100)
 * @param priceChangePct Price change percentage
 * @param elasticity Price elasticity coefficient (default: -1.5, typical for e-commerce)
 * @returns Expected new conversion rate
 */
export function estimateConversionAfterPriceChange(
  currentConversionRate: number,
  priceChangePct: number,
  elasticity = -1.5
): number {
  const demandChange = elasticity * priceChangePct;
  const expectedConversion = currentConversionRate * (1 + demandChange / 100);
  return Math.max(0, expectedConversion); // Never negative
}

/**
 * Competitive pricing: Suggest price in competitor range
 * @param currentPriceCents Current price
 * @param competitorPriceCents Competitor's price
 * @param minMarginBps Minimum acceptable margin
 * @returns Suggested price (either current or competitor's, whichever maintains margin)
 */
export function competitivePrice(
  currentPriceCents: number,
  competitorPriceCents: number,
  _minMarginBps: number
): number {
  // If competitor is cheaper and we can still hit margin target, match or go slightly lower
  if (competitorPriceCents < currentPriceCents) {
    // Go 5% lower than competitor to gain volume (if margin allows)
    const discountedPrice = Math.round(competitorPriceCents * 0.95);
    return Math.max(discountedPrice, currentPriceCents); // Never go lower than current
  }

  // If competitor is more expensive, set to high end (maintain margin)
  return currentPriceCents;
}

// Avatar G Refund Service
// Handles refund processing, Stripe integration, and financial reversals

import { SupabaseClient } from '@supabase/supabase-js';
import Stripe from 'stripe';

export interface CreateRefundInput {
  orderId: string;
  returnRequestId?: string;
  amountCents?: number; // If not provided, full refund
  reason?: string;
}

export interface RefundResponse {
  success: boolean;
  refundId?: string;
  stripeRefundId?: string;
  error?: string;
  details?: string;
}

export class RefundService {
  private supabase: SupabaseClient;
  private stripe: Stripe;

  constructor(supabase: SupabaseClient, stripe: Stripe) {
    this.supabase = supabase;
    this.stripe = stripe;
  }

  /**
   * Create a refund and apply financial reversals
   * Step 1: Validate order and check if already refunded
   * Step 2: Call Stripe API to create refund
   * Step 3: Record refund in database
   * Step 4: Apply financial reversals (commissions, payouts, invoices)
   */
  async createRefund(input: CreateRefundInput): Promise<RefundResponse> {
    try {
      // Get order with payment details
      const { data: order, error: orderError } = await this.supabase
        .from('orders')
        .select('*')
        .eq('id', input.orderId)
        .single();

      if (orderError || !order) {
        return { success: false, error: 'Order not found' };
      }

      // Check if order is refundable
      if (order.status === 'refunded') {
        return { success: false, error: 'Order already refunded' };
      }

      if (!order.stripe_payment_intent_id) {
        return {
          success: false,
          error: 'Order was not paid via Stripe',
        };
      }

      // Calculate refund amount
      const refundAmount = input.amountCents || order.total_amount * 100;

      // Check for existing refund (idempotency)
      const { data: existingRefund } = await this.supabase
        .from('refunds')
        .select('id, stripe_refund_id')
        .eq('order_id', input.orderId)
        .eq('amount_cents', refundAmount)
        .eq('status', 'succeeded')
        .single();

      if (existingRefund) {
        return {
          success: true,
          refundId: existingRefund.id,
          stripeRefundId: existingRefund.stripe_refund_id,
          details: 'Refund already exists',
        };
      }

      // Create Stripe refund
      let stripeRefund: Stripe.Refund;
      try {
        stripeRefund = await this.stripe.refunds.create({
          payment_intent: order.stripe_payment_intent_id,
          amount: refundAmount,
          reason: 'requested_by_customer',
          metadata: {
            orderId: input.orderId,
            returnRequestId: input.returnRequestId || 'manual',
            reason: input.reason || 'Customer request',
          },
        });
      } catch (stripeError: unknown) {
        return {
          success: false,
          error: `Stripe refund failed: ${stripeError.message}`,
        };
      }

      // Record refund in database
      const { data: refundRecord, error: refundError } = await this.supabase
        .from('refunds')
        .insert({
          order_id: input.orderId,
          return_request_id: input.returnRequestId,
          stripe_refund_id: stripeRefund.id,
          stripe_payment_intent_id: order.stripe_payment_intent_id,
          amount_cents: refundAmount,
          currency: order.currency || 'usd',
          status: stripeRefund.status === 'succeeded' ? 'succeeded' : 'pending',
          reason: input.reason || 'Customer return',
        })
        .select()
        .single();

      if (refundError) {
        return { success: false, error: 'Failed to record refund' };
      }

      // Apply financial reversals
      const reversalsSuccess = await this.applyFinancialReversals(
        input.orderId,
        refundRecord.id,
        refundAmount,
        order
      );

      if (!reversalsSuccess) {
        console.warn('Some financial reversals may have failed for refund:', refundRecord.id);
      }

      // Update order status
      const fullRefund = refundAmount === order.total_amount * 100;
      await this.supabase
        .from('orders')
        .update({
          status: fullRefund ? 'refunded' : 'partial_refund',
        })
        .eq('id', input.orderId);

      return {
        success: true,
        refundId: refundRecord.id,
        stripeRefundId: stripeRefund.id,
      };
    } catch (error) {
      console.error('Error creating refund:', error);
      return { success: false, error: 'Internal server error' };
    }
  }

  /**
   * Apply financial reversals to account for refund
   * 1. Reverse affiliate commissions
   * 2. Adjust seller payouts
   * 3. Mark invoice as voided/refunded
   */
  private async applyFinancialReversals(
    orderId: string,
    refundId: string,
    _refundAmountCents: number,
    _order: unknown
  ): Promise<boolean> {
    try {
      // 1. Reverse affiliate commissions
      const { data: conversions, error: conversionError } = await this.supabase
        .from('affiliate_conversions')
        .select('id, commission_amount')
        .eq('order_id', orderId);

      if (!conversionError && conversions && conversions.length > 0) {
        for (const _conversion of conversions) {
          // Create reversal entry (stub: could create separate reversal table)
          // For now, we'll mark the refund as having reversed commission
          await this.supabase
            .from('refunds')
            .update({
              affiliate_commission_reversed: true,
            })
            .eq('id', refundId);
        }
      }

      // 2. Mark invoice as voided (if exists)
      // Stub: assumes invoice system tracks order references
      const { data: invoices } = await this.supabase
        .from('invoices')
        .select('id')
        .eq('metadata_json->>orderId', orderId)
        .limit(1);

      if (invoices && invoices.length > 0) {
        // Update invoice status to voided (in a real system)
        // For now, just mark it in refund record
        await this.supabase
          .from('refunds')
          .update({
            invoice_voided: true,
          })
          .eq('id', refundId);
      }

      // 3. Seller payout adjustment (stub)
      // If order has seller via shipments, mark payout as adjusted
      const { data: shipments } = await this.supabase
        .from('shipments')
        .select('seller_user_id')
        .eq('order_id', orderId)
        .limit(1);

      if (shipments && shipments.length > 0) {
        // Stub: In a full implementation, create payout reversal or hold future payouts
        await this.supabase
          .from('refunds')
          .update({
            seller_payout_adjusted: true,
          })
          .eq('id', refundId);
      }

      return true;
    } catch (error) {
      console.error('Error applying financial reversals:', error);
      return false;
    }
  }

  /**
   * Handle Stripe refund webhook update
   * Called when stripe sends charge.refunded event
   * Idempotent: stripe_refund_id is unique
   */
  async handleStripeRefundWebhook(stripeRefundId: string, status: string): Promise<boolean> {
    try {
      // Check if refund already exists
      const { data: existingRefund, error: _fetchError } = await this.supabase
        .from('refunds')
        .select('id')
        .eq('stripe_refund_id', stripeRefundId)
        .single();

      if (existingRefund) {
        // Update status if different
        await this.supabase
          .from('refunds')
          .update({ status })
          .eq('stripe_refund_id', stripeRefundId);

        return true;
      }

      // This shouldn't happen (refund should exist in our system first)
      console.warn('Received webhook for unknown refund:', stripeRefundId);
      return false;
    } catch (error) {
      console.error('Error handling Stripe refund webhook:', error);
      return false;
    }
  }

  /**
   * Get refund details
   */
  async getRefund(refundId: string) {
    try {
      const { data, error } = await this.supabase
        .from('refunds')
        .select('*')
        .eq('id', refundId)
        .single();

      if (error) throw error;
      return data;
    } catch (error) {
      console.error('Error fetching refund:', error);
      return null;
    }
  }

  /**
   * List refunds for an order
   */
  async listOrderRefunds(orderId: string) {
    try {
      const { data, error } = await this.supabase
        .from('refunds')
        .select('*')
        .eq('order_id', orderId)
        .order('created_at', { ascending: false });

      if (error) throw error;
      return data || [];
    } catch (error) {
      console.error('Error listing refunds:', error);
      return [];
    }
  }
}

// Avatar G Shipping Service
// Core business logic for shipping management

import { SupabaseClient } from '@supabase/supabase-js';
import {
  ShippingProvider,
  _ShipmentCreateData,
  _ShippingProviderConfig,
} from './ShippingProvider';
import { ManualProvider } from './providers/ManualProvider';
import { GeorgianPostProvider } from './providers/GeorgianPostProvider';
import { DHLProvider } from './providers/DHLProvider';
import { UPSProvider } from './providers/UPSProvider';

export interface ShippingQuote {
  rateId: string;
  name: string;
  minDays: number;
  maxDays: number;
  priceCents: number;
  currency: string;
}

export class ShippingService {
  private supabase: SupabaseClient;

  constructor(supabase: SupabaseClient) {
    this.supabase = supabase;
  }

  /**
   * Get shipping quotes for a given address and items
   * Looks up seller shipping zones and rates
   */
  async getShippingQuotes(
    sellerUserId: string,
    countryCode: string,
    _city: string
  ): Promise<ShippingQuote[]> {
    try {
      // Find shipping zone that matches country
      const { data: zones, error: zoneError } = await this.supabase
        .from('shipping_zones')
        .select('id')
        .eq('seller_user_id', sellerUserId)
        .contains('countries', [countryCode]);

      if (zoneError) throw zoneError;
      if (!zones || zones.length === 0) {
        // Fallback: No specific zone, return empty (buyer should be notified)
        return [];
      }

      // Get rates for matching zones
      const { data: rates, error: rateError } = await this.supabase
        .from('shipping_rates')
        .select('id, name, min_days, max_days, price_cents, currency')
        .in('zone_id', zones.map((z) => z.id))
        .eq('is_active', true);

      if (rateError) throw rateError;

      return (
        rates?.map((rate) => ({
          rateId: rate.id,
          name: rate.name,
          minDays: rate.min_days,
          maxDays: rate.max_days,
          priceCents: rate.price_cents,
          currency: rate.currency,
        })) || []
      );
    } catch (error) {
      console.error('Error fetching shipping quotes:', error);
      throw error;
    }
  }

  /**
   * Create a shipment record in database
   * Called after order is confirmed
   */
  async createShipmentRecord(
    orderId: string,
    sellerUserId: string,
    carrier: string = 'manual',
    serviceLevel: string = 'standard'
  ): Promise<{ shipmentId: string; trackingToken: string }> {
    try {
      const { data, error } = await this.supabase
        .from('shipments')
        .insert({
          order_id: orderId,
          seller_user_id: sellerUserId,
          carrier,
          service_level: serviceLevel,
          status: 'created',
        })
        .select('id, tracking_public_token')
        .single();

      if (error) throw error;

      return {
        shipmentId: data.id,
        trackingToken: data.tracking_public_token,
      };
    } catch (error) {
      console.error('Error creating shipment record:', error);
      throw error;
    }
  }

  /**
   * Get provider instance by name
   */
  getProvider(providerName: string): ShippingProvider {
    switch (providerName) {
      case 'manual':
        return new ManualProvider();
      case 'georgian_post':
        return new GeorgianPostProvider();
      case 'dhl':
        return new DHLProvider();
      case 'ups':
        return new UPSProvider();
      default:
        return new ManualProvider(); // Fallback to manual
    }
  }

  /**
   * Update shipment with tracking information
   */
  async updateShipmentTracking(
    shipmentId: string,
    trackingNumber: string,
    trackingUrl?: string
  ): Promise<void> {
    try {
      const { error } = await this.supabase
        .from('shipments')
        .update({
          tracking_number: trackingNumber,
          tracking_url: trackingUrl,
          status: 'label_created',
          shipped_at: new Date().toISOString(),
        })
        .eq('id', shipmentId);

      if (error) throw error;

      // Record event
      await this.addShipmentEvent(shipmentId, 'label_created', null, 'Label created', 'seller');
    } catch (error) {
      console.error('Error updating shipment tracking:', error);
      throw error;
    }
  }

  /**
   * Add an event to shipment event log (audit trail)
   */
  async addShipmentEvent(
    shipmentId: string,
    status: string,
    location: string | null,
    message: string,
    source: 'system' | 'carrier' | 'seller' | 'admin' = 'system'
  ): Promise<void> {
    try {
      const { error } = await this.supabase
        .from('shipment_events')
        .insert({
          shipment_id: shipmentId,
          status,
          location,
          message,
          source,
          occurred_at: new Date().toISOString(),
        });

      if (error) throw error;
    } catch (error) {
      console.error('Error adding shipment event:', error);
      throw error;
    }
  }

  /**
   * Update shipment status and order fulfillment status
   */
  async updateShipmentStatus(
    shipmentId: string,
    newStatus: string,
    location?: string,
    message?: string
  ): Promise<void> {
    try {
      // Update shipment status
      const updateData: unknown = { status: newStatus };

      if (newStatus === 'delivered') {
        updateData.delivered_at = new Date().toISOString();
      } else if (newStatus === 'in_transit' && !updateData.shipped_at) {
        updateData.shipped_at = new Date().toISOString();
      }

      const { error: shipmentError } = await this.supabase
        .from('shipments')
        .update(updateData)
        .eq('id', shipmentId);

      if (shipmentError) throw shipmentError;

      // Add event
      await this.addShipmentEvent(
        shipmentId,
        newStatus,
        location || null,
        message || `Status updated to ${newStatus}`,
        'system'
      );

      // Update order fulfillment status if needed
      if (newStatus === 'delivered' || newStatus === 'canceled' || newStatus === 'returned') {
        const mappedStatus = newStatus === 'delivered' ? 'delivered' : newStatus;
        const { data: shipment } = await this.supabase
          .from('shipments')
          .select('order_id')
          .eq('id', shipmentId)
          .single();

        if (shipment) {
          await this.supabase
            .from('orders')
            .update({ fulfillment_status: mappedStatus })
            .eq('id', shipment.order_id);
        }
      }
    } catch (error) {
      console.error('Error updating shipment status:', error);
      throw error;
    }
  }

  /**
   * Get shipment for buyer (public via token)
   */
  async getShipmentForTracking(token: string) {
    try {
      // Validate token exists
      const { data: tokenData, error: tokenError } = await this.supabase
        .from('tracking_tokens')
        .select('shipment_id, expires_at')
        .eq('token', token)
        .single();

      if (tokenError || !tokenData) return null;

      // Check expiration
      if (tokenData.expires_at && new Date(tokenData.expires_at) < new Date()) {
        return null; // Token expired
      }

      // Get shipment with events (exclude sensitive data)
      const { data: shipment, error: shipmentError } = await this.supabase
        .from('shipments')
        .select(
          `
          id,
          status,
          carrier,
          tracking_number,
          tracking_url,
          shipped_at,
          delivered_at,
          created_at,
          shipment_events (
            id,
            status,
            location,
            message,
            occurred_at
          )
        `
        )
        .eq('id', tokenData.shipment_id)
        .single();

      if (shipmentError) throw shipmentError;

      return shipment;
    } catch (error) {
      console.error('Error fetching shipment for tracking:', error);
      return null;
    }
  }
}

/**
 * Shipping System Library - Server Side
 * All shipping calculations and status management
 * 
 * All money values are in cents (integers).
 */

'use server';

import { createServerClient } from '@supabase/ssr';
import { cookies } from 'next/headers';
import { getServerEnv } from '@/lib/env/server';
import { ShippingProfile, _ShippingEvent, ShippingTracking, ShippingStatus } from '@/lib/commerce/types';

/**
 * Initialize Supabase client for server operations
 */
function getSupabaseClient() {
  const cookieStore = cookies();
  const supabase = createServerClient(
    getServerEnv().NEXT_PUBLIC_SUPABASE_URL || '',
    getServerEnv().NEXT_PUBLIC_SUPABASE_ANON_KEY || '',
    {
      cookies: {
        getAll() {
          return cookieStore.getAll();
        },
        setAll(cookiesToSet: Array<{ name: string; value: string; options?: unknown }>) {
          try {
            cookiesToSet.forEach(({ name, value, options }) => {
              cookieStore.set(name, value, options);
            });
          } catch {}
        },
      },
    }
  );
  return supabase;
}

// ============================================
// SHIPPING COST CALCULATION
// ============================================

export interface ComputeShippingCostInput {
  profileId: string;
  weightGrams?: number;
  destinationCountryCode?: string;
}

export interface ComputeShippingCostResult {
  shippingCostCents: number;
  profileId: string;
  weightGrams: number;
  calculatedAt: string;
}

/**
 * Compute shipping cost based on profile and weight
 * Formula: base_cost + (weight_kg * per_kg_cost)
 * 
 * Server-side only to prevent cost manipulation.
 */
export async function computeShippingCost(
  input: ComputeShippingCostInput
): Promise<ComputeShippingCostResult> {
  const supabase = getSupabaseClient();
  
  const { data: profile, error } = await supabase
    .from('shipping_profiles')
    .select('*')
    .eq('id', input.profileId)
    .single();
    
  if (error || !profile) {
    throw new Error(`Shipping profile not found: ${input.profileId}`);
  }

  const weightGrams = input.weightGrams || 0;
  const weightKg = weightGrams / 1000;
  
  const baseCost = profile.base_cost || 0; // cents
  const perKgCost = profile.per_kg_cost || 0; // cents per kg
  const weightCost = Math.round(perKgCost * weightKg);
  
  const totalCostCents = baseCost + weightCost;
  
  return {
    shippingCostCents: Math.max(0, totalCostCents),
    profileId: input.profileId,
    weightGrams,
    calculatedAt: new Date().toISOString(),
  };
}

// ============================================
// SHIPPING EVENT MANAGEMENT
// ============================================

export interface AddShippingEventInput {
  orderId: string;
  status: ShippingStatus;
  location?: string;
  trackingCode?: string;
  metadata?: Record<string, unknown>;
}

/**
 * Add shipping event and update order status
 * Events are immutable (append-only audit trail)
 * 
 * Server-side verification:
 * - User must be order owner or store owner
 * - Order must exist
 */
export async function addShippingEvent(input: AddShippingEventInput) {
  const supabase = getSupabaseClient();
  
  // Get current user
  const { data: { user }, error: authError } = await supabase.auth.getUser();
  if (authError || !user) {
    throw new Error('Not authenticated');
  }

  // Verify user is order owner or store owner
  const { data: order, error: orderError } = await supabase
    .from('orders')
    .select('id, user_id, store_id')
    .eq('id', input.orderId)
    .single();

  if (orderError || !order) {
    throw new Error('Order not found');
  }

  const isOrderOwner = order.user_id === user.id;
  const isStoreOwner = order.store_id
    ? await checkStoreOwnership(order.store_id, user.id)
    : false;

  if (!isOrderOwner && !isStoreOwner) {
    throw new Error('Unauthorized: not order owner or store owner');
  }

  // Insert shipping event
  const { data: event, error: insertError } = await supabase
    .from('shipping_events')
    .insert([
      {
        order_id: input.orderId,
        status: input.status,
        location: input.location || null,
        tracking_code: input.trackingCode || null,
        metadata_json: input.metadata || null,
      },
    ])
    .select()
    .single();

  if (insertError) {
    throw new Error(`Failed to add shipping event: ${insertError.message}`);
  }

  // Update order shipping status and tracking code
  const updateData: unknown = {
    shipping_status: input.status,
  };

  if (input.trackingCode) {
    updateData.tracking_code = input.trackingCode;
  }

  if (input.status === 'shipped') {
    updateData.shipped_at = new Date().toISOString();
  } else if (input.status === 'delivered') {
    updateData.delivered_at = new Date().toISOString();
  }

  const { error: updateError } = await supabase
    .from('orders')
    .update(updateData)
    .eq('id', input.orderId);

  if (updateError) {
    console.error('Failed to update order shipping status:', updateError);
  }

  return event;
}

// ============================================
// GET TRACKING INFORMATION
// ============================================

/**
 * Get complete tracking info for order
 * Includes shipping profile, current status, and event history
 * 
 * RLS enforced by database:
 * - Order buyer can view
 * - Store owner can view
 */
export async function getTracking(orderId: string): Promise<ShippingTracking> {
  const supabase = getSupabaseClient();

  const { data: order, error: orderError } = await supabase
    .from('orders')
    .select(`
      id,
      shipping_status,
      tracking_code,
      shipping_profile_id,
      weight_grams,
      user_id,
      store_id
    `)
    .eq('id', orderId)
    .single();

  if (orderError || !order) {
    throw new Error('Order not found or access denied');
  }

  let profile: ShippingProfile | null = null;
  if (order.shipping_profile_id) {
    const { data: profileData } = await supabase
      .from('shipping_profiles')
      .select('*')
      .eq('id', order.shipping_profile_id)
      .single();
    profile = profileData;
  }

  // Get shipping events (ordered by most recent first)
  const { data: events, error: eventsError } = await supabase
    .from('shipping_events')
    .select('*')
    .eq('order_id', orderId)
    .order('created_at', { ascending: false });

  if (eventsError) {
    throw new Error('Failed to fetch shipping events');
  }

  const currentEvent = events?.[0];

  return {
    orderId: order.id,
    shippingStatus: order.shipping_status as ShippingStatus,
    trackingCode: order.tracking_code,
    estimatedDaysMin: profile?.estimated_days_min || 1,
    estimatedDaysMax: profile?.estimated_days_max || 7,
    events: events || [],
    currentLocation: currentEvent?.location || null,
    lastUpdated: currentEvent?.created_at || new Date().toISOString(),
  };
}

// ============================================
// SELECT SHIPPING PROFILE FOR ORDER
// ============================================

export interface SelectShippingProfileInput {
  orderId: string;
  shippingProfileId: string;
  weightGrams?: number;
}

/**
 * Select shipping profile for order
 * Recomputes order total with new shipping cost if needed
 * 
 * Only store owner allowed.
 */
export async function selectShippingProfile(input: SelectShippingProfileInput) {
  const supabase = getSupabaseClient();

  const { data: { user }, error: authError } = await supabase.auth.getUser();
  if (authError || !user) {
    throw new Error('Not authenticated');
  }

  // Verify user owns the order's store
  const { data: order, error: orderError } = await supabase
    .from('orders')
    .select('id, store_id, total_amount')
    .eq('id', input.orderId)
    .single();

  if (orderError || !order) {
    throw new Error('Order not found');
  }

  const isStoreOwner = order.store_id
    ? await checkStoreOwnership(order.store_id, user.id)
    : false;

  if (!isStoreOwner) {
    throw new Error('Unauthorized: not store owner');
  }

  // Verify profile exists and belongs to same store
  const { data: profile, error: profileError } = await supabase
    .from('shipping_profiles')
    .select('id, store_id, base_cost, per_kg_cost')
    .eq('id', input.shippingProfileId)
    .single();

  if (profileError || !profile) {
    throw new Error('Shipping profile not found');
  }

  if (profile.store_id !== order.store_id) {
    throw new Error('Shipping profile does not belong to this store');
  }

  // Compute new shipping cost
  const costResult = await computeShippingCost({
    profileId: input.shippingProfileId,
    weightGrams: input.weightGrams || 0,
  });

  // Get old shipping cost for adjustment
  const { data: oldData } = await supabase
    .from('orders')
    .select('shipping_cost')
    .eq('id', input.orderId)
    .single();

  const oldShippingCost = oldData?.shipping_cost || 0;
  const shippingCostDifference = costResult.shippingCostCents - oldShippingCost;

  // Update order with new shipping profile and cost
  const { error: updateError } = await supabase
    .from('orders')
    .update({
      shipping_profile_id: input.shippingProfileId,
      shipping_cost: costResult.shippingCostCents,
      weight_grams: input.weightGrams || null,
      total_amount: order.total_amount + shippingCostDifference,
    })
    .eq('id', input.orderId);

  if (updateError) {
    throw new Error(`Failed to update order: ${updateError.message}`);
  }

  return {
    orderId: input.orderId,
    shippingProfileId: input.shippingProfileId,
    shippingCostCents: costResult.shippingCostCents,
    newTotal: order.total_amount + shippingCostDifference,
  };
}

// ============================================
// CREATE SHIPPING PROFILE (Store Owner)
// ============================================

export interface CreateShippingProfileInput {
  storeId: string;
  name: string;
  baseCost: number;
  perKgCost: number;
  estimatedDaysMin: number;
  estimatedDaysMax: number;
  description?: string;
}

/**
 * Create new shipping profile for store
 * Only store owner allowed.
 */
export async function createShippingProfile(input: CreateShippingProfileInput) {
  const supabase = getSupabaseClient();

  const { data: { user }, error: authError } = await supabase.auth.getUser();
  if (authError || !user) {
    throw new Error('Not authenticated');
  }

  // Verify user owns the store
  const isOwner = await checkStoreOwnership(input.storeId, user.id);
  if (!isOwner) {
    throw new Error('Unauthorized: not store owner');
  }

  const { data, error } = await supabase
    .from('shipping_profiles')
    .insert([
      {
        store_id: input.storeId,
        name: input.name,
        base_cost: input.baseCost,
        per_kg_cost: input.perKgCost,
        estimated_days_min: input.estimatedDaysMin,
        estimated_days_max: input.estimatedDaysMax,
        description: input.description || null,
      },
    ])
    .select()
    .single();

  if (error) {
    throw new Error(`Failed to create shipping profile: ${error.message}`);
  }

  return data;
}

// ============================================
// HELPER: Check Store Ownership
// ============================================

async function checkStoreOwnership(storeId: string, userId: string): Promise<boolean> {
  const supabase = getSupabaseClient();
  
  const { data, error } = await supabase
    .from('shop_stores')
    .select('user_id')
    .eq('id', storeId)
    .single();

  if (error || !data) {
    return false;
  }

  return data.user_id === userId;
}

