export class DigitalDeliveryAdapter extends SupplierAdapter {
  constructor(supplierId: string, supplierName: string = 'Digital Delivery') {
    super({
      supplierId,
      supplierName,
    });
  }

  async searchProducts(_query: string): Promise<SupplierSearchResult> {
    return {
